            vec2 p = (-iResolution.xy + 2.0*(fragCoord.xy+vec2(i,j)))/iResolution.y;

            vec3 ro = vec3( 0., 0., 2.);
            vec3 ta = vec3( 0. , 0. , 0. );

            mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

            vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length

            vec2 res = calcIntersection( ro , rd  );
        }
    }
    fragColor = vec4(result,1.0);
}

float PI=3.14159265;
#define Power 10.0
#define Bailout 2.0

void powN1(inout vec3 z, float r, inout float dr) {
	// extract polar coordinates
	float theta = acos(z.z/r);
	float phi = atan(z.y,z.x);
	dr =  pow( r, Power-1.0)*Power*dr + 1.0;

	// scale and rotate the point
	float zr = pow( r,Power);
	theta = theta*Power;
	phi = phi*Power;

	// convert back to cartesian coordinates
	z = zr*vec3(sin(theta)*cos(phi), sin(phi)*sin(theta), cos(theta));
}


// Compute the distance from `pos` to the Mandelbox.
float DE(vec3 pos) {
	vec3 z=pos;
	float r;
	float dr=0.75;
	r=length(z);
	for(int i=0; (i < 4); i++) {
		powN1(z,r,dr);
		z+=pos;
		r=length(z);
		if (r>Bailout) break;
	}

	return (cos(iTime*0.60+cos(iTime*0.3+(pos.x*pos.x+pos.y*pos.y+pos.z*pos.z)*50.0)*0.4)*0.06+0.14)*log(r)*r/dr;

}

vec3 DEColor(vec3 pos) {
	vec3 z=pos;
	float r;
	float dr=1.0;
	r=length(z);
	float minR = 1000.0;
	for(int i=0; (i < 2); i++) {
		powN1(z,r,dr);
		z+=pos;
		r=length(z);
		minR = min(r,minR);
		if (r>Bailout) break;
	}
	float i = minR*minR*minR*minR*0.50;
	return vec3(clamp(i,0.0,1.0));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 vPos=-1.0+2.0*fragCoord.xy/iResolution.xy;

  //Camera animation
  vec3 vuv=vec3(0,1.0,0.5);//Change camere up vector here
  vec3 vrp=vec3(0,cos(iTime)*0.25,sin(iTime)*0.25); //Change camere view here
  float mx=iMouse.x*PI*1.0;
  float my=iMouse.y*PI/1.0;
  vec3 prp=vec3(cos(my)*cos(mx),sin(my),cos(my)*sin(mx))*2.0; //Trackball style camera pos


  //Camera setup
  vec3 vpn=normalize(vrp-prp);
  vec3 u=normalize(cross(vuv,vpn))*0.25;
  vec3 v=cross(vpn,u);
  vec3 vcv=(prp+vpn);
  vec3 scrCoord=vcv+vPos.x*u*iResolution.x/iResolution.y+vPos.y*v;
  vec3 scp=normalize(scrCoord-prp);

  //Raymarching
  const vec3 e=vec3(0.001,0,0);
  const float maxd=4.0; //Max depth
  float s=0.0;
  vec3 c,p,n;

  float f=0.10;
  for(int i=0;i<46;i++){
    f+=s;
    p=prp+scp*f;
    s=DE(p)*1.0;
    if (abs(s)<.0025||f>maxd) break;

  }

  if (f<maxd){
    n=normalize(
      vec3(s-DE(p-e.xyy),
           s-DE(p-e.yxy),
           s-DE(p-e.yyx)));
    c = DEColor(p);
    c.yz = max(mix(c.yz, n.yz, 0.2),0.3)+0.2;
    float b=dot(n,normalize(prp-p));  
    fragColor = mix(vec4((b*c+pow(b,256.0))*(1.0-f*.31),1.0), vec4(c,1.0),0.38);
  }
  else {
      fragColor=vec4(0.0,0,0,1); //background color
  }

}

