float GetDist(vec3 p  ) {	



    float d, dif1, dif2, dif3, dif4;

    d=999.9;

    float planeDist = p.y+3.0;

    vec2 res;
    res=vec2(999.9);

    vec3 pp;
    pp=p;

    vec3 pr;
    pr=p;
    pr.xz*= rotate(iTime * 0.5);


    //piso grande
    float sdbg1= sdBox(  pr- vec3(0,0.5,0), vec3(5,0.5,5));

    //columnas
    float sdbc1= sdBox(  pr- vec3(-4,0.5,4), vec3(0.5,7.0,0.5));
    float sdbc2= sdBox(  pr- vec3(4,0.5,4), vec3(0.5,7.0,0.5));
    float sdbc3= sdBox(  pr- vec3(-4,0.5,-4), vec3(0.5,7.0,0.5));
    float sdbc4= sdBox(  pr- vec3(4,0.5,-4), vec3(0.5,7.0,0.5));


    pp.xz = mod(p.xz, 2.0) - vec2(0.1,1.0);


    //piso inferior
    float sdb1= sdBox(  pp- vec3(0,0,0), vec3(1,0.5,1));
    float sdb2= sdBox(  pp- vec3(2,0,0), vec3(1,0.5,1));
    float sdb3= sdBox(  pp- vec3(0,0,1), vec3(1,0.5,1));
    float sdb4= sdBox(  pp- vec3(2,0,1), vec3(1,0.5,1));


     //cubo solo
    vec3 pbB=vec3(1.0,0.5,1.0);
    vec3 pbBPos=vec3(0,2.5,0);
    pbBPos.xz *= rotate(iTime *2.0 );
    float bx1 = sdBox(pr-pbBPos,pbB);





    res=vec2(planeDist,4);
    res=opU(res, vec2(sdb1,3));
    res=opU(res, vec2(sdb2,5));
    res=opU(res, vec2(sdb3,5));
    res=opU(res, vec2(sdb4,3));
    res=opU(res, vec2(sdbg1,6));


    res=opU(res, vec2(sdbc1,7));
    res=opU(res, vec2(sdbc2,7));
    res=opU(res, vec2(sdbc3,7));
    res=opU(res, vec2(sdbc4,7));


    res=opU(res, vec2(bx1,11));



    d = res.x;
    mObj.dist = res.x;
    mObj.tipo = res.y;

    return d;
}


float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.2;
    //Determines size of shadow
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}





vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    //Texture of white and black in image
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}



float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos.xz += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}


float GetLightv2(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    //lightPos.xz += vec2(sin(iTime), cos(iTime)*2.);

    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}






