uniform float fGlobalTime; // in seconds
uniform vec2 v2Resolution; // viewport resolution (in pixels)

#define NB_BRANCHES 3.0

#define C_FOND       0
#define C_PAYSAGE    1
#define C_TRONC      2
#define C_BRANCHES   3
#define C_NEIGE      4

vec3 couleur(int c) {
  if (c == C_FOND)     return vec3(1.00, 0.00, 0.00);     
  if (c == C_PAYSAGE)  return vec3(0.90, 0.90, 0.90);
  if (c == C_TRONC)    return vec3(0.40, 0.15, 0.00);
  if (c == C_BRANCHES) return vec3(0.00, 0.80, 0.00);
  if (c == C_NEIGE)    return vec3(1.00, 1.00, 1.00);
}

int paysage(in vec2 uv) {
  return int(min(1., 1.35 - uv.y + cos(uv.x * 3.) / 12.));
}

int tronc(in vec2 uv) {    

  uv = abs(uv);

  return int(
    1. - (step(0.2, uv.y) + step(0.1, uv.x) )
  );
}

int branches(in vec2 uv) {

  uv = abs(uv);
  float l =
    1.0
    - (uv.x + uv.y)
    + floor(uv.y * 5.) / 8.0;

  l = step(0.5, l);

  l *= step(0.2, uv.y);

  return int(l);
}

int neige(in vec2 uv) {
  float angle = atan(uv.y,uv.x);
  float radius = length(uv);

  angle *= NB_BRANCHES;

  uv = vec2(cos(angle + iTime),sin(angle + iTime)) * radius;

  vec2 uv2 = uv;

  uv = abs(uv);
  float t =
    step(uv.x, 0.045)
    * step(uv.y, 0.3);

  t +=
    step(uv.x, 0.15)
    * step(abs(uv.y - 0.2), 0.015);

  t +=
    step(uv.x, 0.25)
    * step(abs(uv.y - 0.1), 0.025);

  return int(min(1., t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv /= vec2(iResolution.y / iResolution.x, 1);
  uv.x -= 0.2;

  int plan = 0;

  float sx = 2.2 - mod(iTime / 4.0, 3.);

  plan = max(plan, tronc(uv - vec2(sx, 0.))         * C_TRONC);
  plan = max(plan, branches(uv - vec2(sx, 0.))      * C_BRANCHES);
  plan = max(plan, paysage(uv + vec2(iTime/13.,0.)) * C_PAYSAGE);
  plan = max(plan, neige(fract( (uv- vec2(0.3,-0.2) * float(iTime))*5.0) -0.5 )       * C_NEIGE);

  fragColor = vec4(couleur(plan), 1.0);
}

float checkerboard(vec2 uv) {
    vec2 adjusted = floor(uv);
 	bool inside = mod(adjusted.y, 2.0) == 1.0
		? mod(adjusted.x, 2.0) == 0.0
        : mod(adjusted.x, 2.0) == 1.0;
    return inside ? 1.0 : 0.0;
}

struct HitResult { bool hit; vec3 point; };
struct Ray { vec3 origin; vec3 direction; };
struct Plane { vec3 origin; vec3 normal; };

mat4 rotateY(float angle) {
 	float x = cos(angle);
    float y = sin(angle);
    return mat4(
		y, 0,-x, 0,
        0, 1, 0, 0,
        x, 0, y, 0,
        0, 0, 0, 1
     );
}

HitResult rayPlaneIntersect(Ray ray, Plane plane) {
 	float horizonDot = dot(ray.direction, plane.normal);
    if (horizonDot < 0.00001) {
		vec3 difference = plane.origin - ray.origin;
        float time = dot(difference, plane.normal) / horizonDot;

        bool hit = time > 0.0;
        vec3 point = hit
            ? ray.origin + ray.direction * time
            : vec3(0.0);

        return HitResult(hit, point);
    }

    return HitResult(false, vec3(0.0));
}

vec3 scene(vec2 uv) {
    const vec3 up = vec3(0.0, 1.0, 0.0);

    vec3 origin = vec3(0.0, 2.0 + cos(iTime), 0.0);
    mat4 rotation = rotateY(iTime * 0.2);
    vec3 rayDirection = normalize(vec3(uv.x, uv.y, 0.9));
    rayDirection = (rotation * vec4(rayDirection, 0.0)).xyz;

    Ray ray = Ray(origin, rayDirection);
    Plane plane = Plane(vec3(0.0), up);  


    HitResult result = rayPlaneIntersect(ray, plane);

    float value = checkerboard(uv * 10.0);
    vec3 color = result.hit
        ? vec3(checkerboard(result.point.xz))
        : vec3(rayDirection.x, rayDirection.y, rayDirection.z);

   	return color;
}

const float PI = 3.14159;

vec2 coordToUv(vec2 coord) {
 	return (coord - iResolution.xy * 0.5) / iResolution.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;

    vec3 singleSample = scene(uv);

    float a = (3.0 / 8.0);
    float b = (1.0 / 8.0);
    vec3 acc = vec3(0.0);
    acc += scene(coordToUv(fragCoord + vec2(-a, b)));
    acc += scene(coordToUv(fragCoord + vec2(-b, -a)));
    acc += scene(coordToUv(fragCoord + vec2(a, -b)));
    acc += scene(coordToUv(fragCoord + vec2(b, a)));
    acc /= 4.0;

    vec3 color = fragCoord.x / iResolution.x > 0.5
        ? acc
        : singleSample;

    color = pow(color, vec3(1.0 / 2.2));

    color *= step(0.001, abs(fragCoord.x / iResolution.x - 0.5));

    fragColor = vec4(color, 1.0);
}

