const float PI = 3.14159265;
mat2 r2d(float a){float sa = sin(a);float ca=cos(a);return mat2(ca,sa,-sa,ca);}

float sat(float a)
{
  return clamp(a,0.,1.);
}
float lenny(vec2 uv)
{
  return abs(uv.x)+abs(uv.y);
}
float sdf_cir(vec2 uv,float r)
{
  return length(uv)-r;
}

float _union(float a, float b)
{
  return max(a,b);
}

vec4 rdr(vec2 uv, float tim)
{
  float sharp = 5.;
  float sz = 0.1;
  float a = 1.-sat(sdf_cir(uv-vec2(sin(tim),sin(tim*2.)*0.5)*0.3,sz)*sharp);
  vec3 cola = 0.8*vec3(0.,1.,1.)*min(1.,a*100.);

  float b = 1.-sat(sdf_cir((uv-vec2(sin(-tim+1.),sin(tim*2.+3.)*0.5)*0.3),sz)*sharp);
  vec3 colb = 1.2*vec3(1.,0.75,.4)*min(1.,b*100.);

float c = 1.-sat(sdf_cir((r2d(PI/2.)*uv-vec2(sin(tim+0.8),sin(tim*2.)*0.5)*0.3),sz)*sharp);
  vec3 colc = vec3(1.,0.,.3)*min(1.,c*100.);


  return vec4(cola+colb+colc,a+b+c);
}

vec3 rdrblur(vec2 uv)
{
  const int blurSamples = 8;
  float iTimeSp = 0.1;
  vec3 col = vec3(.42,.4,.4)*.6;
col *= 1.-sat(lenny(uv*.5));

  vec3 acc;
  float opa = 0.;
  for (int i= 0; i<blurSamples;++i)
  {
    vec4 rdrCol = rdr(uv, iTime-float(i)*iTimeSp);
    acc += rdrCol.xyz;
    opa = max(opa, rdrCol.w)*float(blurSamples-i)/8.;
  }

  return mix(col,acc, sat(opa)*0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord.xy / iResolution.xx;
  uv -= vec2(.5)*(iResolution.xy/iResolution.xx);
  vec3 col ;
uv *= 2.5;

  //cyan vec3(0.,1.,1.)
  // mangenta vec3(1.,0.,.3)
  // yellow vec3(1.,0.65,.3)
  col = mix(col, vec3(1.,0.65,.3), sat((1.-length(uv)*8.)*30.));
  col = rdrblur(uv);
  col += rdrblur(mod(2.*abs(uv*vec2(1.*uv.y,abs(uv.x))),0.5)*2.)*0.5;

    fragColor = vec4(col, 1.0);
}

