//https://www.shadertoy.com/view/4dS3Wd
float hash(float p) { p = fract(p * 0.011); p *= p + 7.5; p *= p + p; return fract(p); }

//https://www.shadertoy.com/view/4dS3Wd
float noise(vec3 x) {
    const vec3 step = vec3(110, 241, 171);

    vec3 i = floor(x);
    vec3 f = fract(x);

    // For performance, compute the base input to a 1D hash from the integer part of the argument and the 
    // incremental change to the 1D based on the 3D -> 1D wrapping
    float n = dot(i, step);

    vec3 u = f * f * (3.0 - 2.0 * f);
    return mix(mix(mix( hash(n + dot(step, vec3(0, 0, 0))), hash(n + dot(step, vec3(1, 0, 0))), u.x),
                   mix( hash(n + dot(step, vec3(0, 1, 0))), hash(n + dot(step, vec3(1, 1, 0))), u.x), u.y),
               mix(mix( hash(n + dot(step, vec3(0, 0, 1))), hash(n + dot(step, vec3(1, 0, 1))), u.x),
                   mix( hash(n + dot(step, vec3(0, 1, 1))), hash(n + dot(step, vec3(1, 1, 1))), u.x), u.y), u.z);
}

//https://www.shadertoy.com/view/4dS3Wd
//slightly modified for octave spcification
float fbm5(vec3 x) {
	float v = 0.0;
	float a = 0.5;
	vec3 shift = vec3(100);
	for (int i = 0; i < 5; ++i) {
		v += a * noise(x);
		x = x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}

float fbm3(vec3 x) {
	float v = 0.0;
	float a = 0.5;
	vec3 shift = vec3(100);
	for (int i = 0; i < 3; ++i) {
		v += a * noise(x);
		x = x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}

//iq
float sdPlaneInf(vec3 pos, float y)
{
 	return pos.y - y;   
}

//iq
float sdRoundBox( vec3 p, vec3 b, float r )
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}


//iq, modified color and made fog amount grow exponentially
vec3 applyFog( in vec3  rgb,      // original color of the pixel
               in float distance, // camera to point distance
               in vec3  rayDir,   // camera to point vector
               in vec3  sunDir )  // sun light direction
{
    float fogAmount = 1.0 - exp( -distance*(distance*(0.001 / pow(clamp(VIEW_DIST / 70.0, 0.0, 1.0), 2.0))) );
    float sunAmount = max( dot( rayDir, sunDir ), 0.0 );
    vec3  fogColor  = mix( 	vec3(0.8, 0.6, 0.2),
        					vec3(0.3, 0.6, 1.0),
                           pow(sunAmount,8.0) );
    return mix( rgb, fogColor, fogAmount );
}

//https://www.iquilezles.org/www/articles/smin/smin.htm
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

Object map(vec3 pos)
{
    Object o;
    o.difVal = 1.0;
    o.specVal = 1.0;
    o.specKs = 0.0;
    o.dist = 1000.0;
    o.normEps = 0.001;
    o.color = vec3(0);

    vec3 planePos = pos;
    float yVal = 3.0*fbm3(pos/4.0);
    //float yVal = 6.0+5.0*sin(2.0*pos.x + pos.z);
    planePos.y -= yVal;
    float dPlane = sdPlaneInf(planePos, 0.0);
    if(dPlane < o.dist)
    {
        o.dist = dPlane;
        o.color = mix(vec3(194., 178., 128.)/255.0, vec3(133., 100., 50.)/255.0, fbm3(pos*100.0));
    }

    vec3 bbPos = pos;
    bbPos.x = mod(bbPos.x, -5.0);
    bbPos.z = abs(bbPos.z);

   	float bound = sdRoundBox(bbPos-vec3(-3.0,2.1,2.0), vec3(0.2, 3.0, 0.2), 0.1);
    if(bound < o.dist)
    {
        vec3 pillPos = pos;
        vec2 id = floor(pillPos.xz/5.0);
        float h = sin(id.x) * 1337.0 * cos(id.y) * 420.69;
        h = -0.5 + mod(h, 1.1);

        //pillPos.y += 0.15*fbm(pillPos*10.0) * smoothstep(0.7, 0.8, pillPos.y);

        pillPos.z -= sin(pillPos.y*2.0+2.0*iTime+id.x*id.y)*0.2*smoothstep(yVal, yVal+1.0, pillPos.y);
        float dist = 0.1*fbm5(pillPos*5.0);
        pillPos.z += dist;// * step(sin(pillPos.z) + cos(pillPos.x), 0.0);
        pillPos.x += dist;// * step(sin(pillPos.z) + cos(pillPos.x), 0.0);

        //red orbs to pillars?

        pillPos.x = mod(pillPos.x, -5.0);
        pillPos.z = abs(pillPos.z);
        pillPos -= vec3(-3.0,1.1+h,2.0);
        float dBox = sdRoundBox(pillPos, vec3(0.1, 3.0, 0.1), 0.1);
        if(dBox < o.dist)
        {
            o.dist = dBox;
            //o.dist = smin(o.dist, dBox, 0.2);      
            o.difVal = 1.0;
            o.color = mix(vec3(44,53,57)/255.0, vec3(194., 178., 128.)/255.0, fbm3(pos*200.0));

            o.specVal = 20.0;
            o.specKs = 0.4;
        }


    }

    return o;
}

vec3 calcNormal(vec3 pos, float ep)
{
    return normalize(vec3(map(pos + vec3(ep, 0, 0)).dist - map(pos - vec3(ep, 0, 0)).dist,
                		map(pos + vec3(0, ep, 0)).dist - map(pos - vec3(0, ep, 0)).dist,
                		map(pos + vec3(0, 0, ep)).dist - map(pos - vec3(0, 0, ep)).dist));                                
}

MarchRes marchRay(vec3 pos, vec3 dir, float speed)
{
 	MarchRes res;
    Object o;

    res.totalDist = 0.001;

    for(int x=0; x<100; x++)
    {
 		res.curRay = pos + (dir*res.totalDist);

        o = map(res.curRay);
        if(abs(o.dist) < 0.0001)
        {
            res.obj = o;
            break;
        }
        else if(res.totalDist >= VIEW_DIST) break;

        res.totalDist += o.dist*speed; // repalce 0.8 w/ this for trippy mode ;p => (0.3+0.2*(sin(iTime))); //couldn't handle the hair :' (
    }

    if(res.totalDist < VIEW_DIST)
    {
        o.normal = calcNormal(res.curRay, o.normEps);
        res.obj = o;
    }


    return res;
}

float marchSoftShadow(vec3 pos, Light l, float speed, float w)
{
 	MarchRes res;
    Object o;
    vec3 dir = normalize(l.pos);
    float len = length(l.pos - pos);
    float maxDist = len;

    res.totalDist = 0.001;

    float sha = 1.0;
    for(int x=0; x<200; x++)
    {
 		res.curRay = pos + (dir*res.totalDist);

        o = map(res.curRay);

        sha = min( sha, 0.5*o.dist/(w*res.totalDist) );
        if(sha < 0.00001)
        {
            res.obj = o;
            break;
        }
        else if(res.totalDist > maxDist) break;

        res.totalDist += o.dist*speed; // repalce 0.8 w/ this for trippy mode ;p => (0.3+0.2*(sin(iTime))); //couldn't handle the hair :' (
    }	

    sha = max(sha,0.0);
    return sha*sha*(3.0-2.0*sha);
}

float calcShadowSoft(vec3 pos, Light l)
{
    //MarchRes res;
    float val;
    val = marchSoftShadow(pos, l, 1.0, 0.05); //march slower to prevent shadow artifacts
	return val;
}

vec3 calcDiffuseLight(Object o, Light l, vec3 pos)
{
    vec3 dir = normalize(l.pos - pos);
    return (o.color) * l.intensity * l.color * clamp(dot(o.normal, dir), 0.0, 1.0) * o.difVal;   
}

vec3 calcSpecLight(Object o, Light l, vec3 pos, vec3 camPos)
{
 	vec3 dir = normalize(l.pos - pos);  
    vec3 viewDir = normalize(camPos - pos);
    vec3 specR = 2.0*clamp( dot(o.normal, dir), 0.0, 1.0) * o.normal - dir;
    float spec = clamp( dot(viewDir, specR), 0.0, 1.0);
    //lightInt*(lightCol*pow(lightSpec, res.obj.specVal))*res.obj.specKs * lightShadow;

    return o.specKs*l.intensity*(l.color*pow(spec, o.specVal));
}

