// IMPORTANT TO NOTE each "slice" is not of its own 3D space but rather to be treated as another "row" of pixels on a 3D image
// This is why the checkboard pattern acts the way it does when you move away from the center W slice.
// Click and drag to see different 2D slices of the 3D image created by this 4D world

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define R_STEP 8. // How many samples along the uv.z coordinate don't set this much higher than like 32 or else you'll die
// set above to .5 so we only get a single image
#define W_CROP 128. // How far along the W axis do we render our samples? (Bigger number = Narrower slice)
#define PI 3.14159265

float GetDist(vec4 p)
{
 vec4 glome[4];
    glome[0]  = vec4(.5,1.,6,0);
    glome[1]  = vec4(-1,-1.5,12,1);
    glome[2]  = vec4(2.,-1.5,8,.0);
    glome[3]  = vec4(-1,-1.5,4,0);
    float radius = 1.0;
    float sDIST = 100.;

    for(int i = 0;i < 4;i++)
    {sDIST = min(length(p-glome[i])-radius,sDIST);}
    sDIST = min(sDIST,p.y+2.5);
    return sDIST;
}


float RayMarch(vec4 ro, vec4 rd)
{
 float dO = 0.0;

    for(int i = 0;i < MAX_STEPS;i++)
    {
        vec4 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO > MAX_DIST || dS < SURF_DIST) break;

    }

    return dO;
}

vec4 GetNormal(vec4 p)
{
    float D = GetDist(p);
    vec2 e = vec2(.01,0);
    vec4 n = D - vec4(
        GetDist(p-e.xyyy),
        GetDist(p-e.yxyy),
        GetDist(p-e.yyxy),
        GetDist(p-e.yyyx)
        );
    return normalize(n);
}

float GetLight(vec4 p)   
{
    vec4 LightPos = vec4(4,8,4,4);

    vec4 l = normalize(LightPos-p);
    vec4 n = GetNormal(p);
    float s = RayMarch(p+n*SURF_DIST*2.,l);
    if(s<length(LightPos-p)) l *= .0;

    float GAY = dot(n,l);
	return GAY;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec4 col = vec4(0);

    // Time varying pixel color
    vec4 space = vec4(uv,iMouse.x/iResolution.x - 0.5,iMouse.y/iResolution.y - 0.5);
    //space = vec4(uv,0,0); // Locks W value to 0.
    float T = (iTime/20. - float(int(iTime/20.)))*2.-1.;
    T = clamp(iMouse.x/iResolution.x - 0.5,-1.,1.);

    //space = vec4(uv,0,round(T*64.)/64.);
    space = vec4(uv,0,T); // set T to 0.0 so we lock on the center slice of pixels

    vec4 rO = vec4(cos(iTime/2.)*9.,0,6.+sin(iTime/2.)*9.,0);
    vec4 rD = normalize(vec4(space.xy,1.0,space.w));
    float D = 0.;
    vec3 lightcol = vec3(1.0,1.0,1.0);

    for(float i = -R_STEP;i < R_STEP;i++)
    {
        float YAW = iTime/2.+PI/2.;

    space = vec4(uv,0,i/R_STEP/W_CROP + T);
    rD = normalize(vec4(
        cos(YAW)*space.x - sin(YAW),
        space.y,
        sin(YAW)*space.x + cos(YAW),
        space.w));
    D = RayMarch(rO,rD);

        vec4 p = rO + rD * D;
    //vec4 col = vec4(1.-1./D);
    float CHK = 0.0;
    if(int(p.x+64.) % 2 >= 1) // p.x+64. is so the checkerboard pattern repeats properly with negative numbers
        CHK = 1.;
    if(int(p.z+64.) % 2 >= 1)
        CHK = 1.-CHK;
    if(int(p.w+64.) % 2 >= 1)
        CHK = 1.-CHK;
    CHK = 1.-CHK/3.;
        if(p.y >= -2.49) // makes the checkerboard only affect the grid and shit agh
            CHK = 1.;

    //lightcol = vec3(clamp(1.-i*2. ,.0,1.), clamp(1.+i*2. ,.0,1.) , 0.);
        lightcol = vec3(CHK,1.0-CHK/2.,1.0);

    col += vec4(  (clamp(GetLight(p),0.,1.) + 0.1)  ) * vec4(lightcol,1.) / R_STEP/2.;
    }

    // Tonemapping
    //col = (col/(col+1.))*1.9-0.1;

    // Output to screen
    fragColor = vec4(col);
}

// exploring from JohnShadow's https://www.shadertoy.com/view/tlcGRr

float n = 20.;

#define pi 3.1415927
#define rot(a) mat2( cos(a), -sin(a), sin(a), cos(a) )


void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 200. * u / R.y,
         M = length(iMouse.xy) > 10. ? iMouse.xy/ R : vec2(.3, .015*pi);

    float df = M.x, da = pi*M.y,
          t = 10.*iTime,
          s = 0., m, v, 
        noise = 1., _n=0.;

    for( m = 0.; m < n; m++)
    {
        _n = noise;
        noise = 2.* fract(1e3*sin( 1e3 * m ) ) - 1.;
        s += sin( (1.+ df *_n)* ( U * rot( da * noise) ).x  -t -m );
    }
    s /= 2.*sqrt(m);
    O = .5 + .5* vec4( u.x < R.x/2. ? s : s/fwidth(s) ); // normalized phasor noise
}

