// taken from YX here : https://www.shadertoy.com/view/tdlXW4
// rough shadertoy approximation of the bonzomatic noise texture
vec4 texNoise(vec2 uv)
{
    float f = 0.;
    f += texture(iChannel0, uv*.125).r*.5;
    f += texture(iChannel0, uv*.25).r*.25;
    f += texture(iChannel0, uv*.5).r*.125;
    f += texture(iChannel0, uv*1.).r*.125;
    f=pow(f,1.2);
    return vec4(f*.45+.05);
}

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float hash21(vec2 x)
{return fract(sin(dot(x,vec2(13.4,18.5)))*1478.5);}

float stmin (float a, float b, float k, float n)
{
    float st = k/n;
    float u = b-k;
    return min(min(a,b), 0.5*(u+a+abs(mod(u-a+st,2.*st)-st)));
}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r, abs(p.z)-h);}

float scenery (vec3 p)
{
    p.y += cos((p.z+sin(p.x)*0.2)*5.)*0.05;
    p.y += texNoise(p.xz*.15).g*0.15;
    p.y += 15.8;

    float b1 = box(p,vec3(4.,15.,15.));
    p.z += 3.;
    float b2 = box(p,vec3(18.,15.,5.));
    return min(b2,b1);  
}

float lantern (vec3 p)
{
    vec3 pp = p;
    float c1 = cyl(p.xzy, 0.1+(abs(p.y-0.5)-0.1)*0.15, .6);
    p.y -= 0.85;
    float c2 = cyl(p.xzy+vec3(0.,0.,cos(p.x*3.)*0.1), 0.3, 0.02);
    p.y -= 0.1;
    float c3 = max(-box(p,vec3(0.1,0.1,10.))+0.05,cyl (p.xzy, 0.25,0.18));
    p.y -= 0.5;
    float c4 = cyl(p.xzy, 0.01-p.y, 0.3);
    float c5 = cyl(p.xzy, 0.05, 0.05);
    return stmin(c1,min(c2,min(c3,stmin(c4,c5,0.1,2.))),0.3,2.);
}

float lanterns (vec3 p)
{
    float per = 1.5;
    p.z += 3.;
    p.x = abs(p.x)-4.;
    p.z = abs(p.z)-1.;
    return lantern(p);
}

float fence (vec3 p)
{
    p.y += .25;
    p.x = abs(p.x)-1.5;
    float b1 = min(box(p, vec3(0.06,0.7,0.06)),length(p-vec3(0.,.8,0.))-.15);
    float b2 = box(p-vec3(-1.,.4,0.),vec3(1.,0.06,0.06));
    float b3 = box(p-vec3(-1.,0.,0.),vec3(1.,0.06,0.06));
    float b4 = box(p-vec3(-1.,-0.1,0.),vec3(0.06,0.6,0.06));
    return min(b1,min(b2,min(b3,b4)));
}

float fences (vec3 p)
{
    vec3 pp = p;
    p.z -= 8.;
    p.x = abs(p.x)-2.;
    float f1 = fence(p);

    p = pp;
    p.z -= 4.;
    p.xz *= rot(PI/2.);
    p.xz = abs(p.xz)-vec2(1., 3.6);
    float f2 = fence(p);

    p = pp;
    p.z -= 1.;
    p.x = abs(p.x)-6.8;
    p.x = abs(p.x)-1.5;
    float f3 = fence(p);

    return min(f3,min(f2,f1));
}

float water (vec3 p)
{
    p.y += (texNoise(p.xz*0.1+time*0.05).r + texNoise(p.xz*0.05-time*0.04).r)*0.4;
    return p.y;
}

float prim1 (vec3 p)
{
    p.xz *= rot(sin(p.y*2.-time));
    p.x = abs(p.x)-.5;

    p.x += sin(p.y*5.+time)*0.3;
    p.y += cos(p.x+time)*0.5;
    float s = cyl(p.xzy, 0.1+p.y*0.5, 10.);
    return s;
}

float pool (vec3 p)
{
    p.z -= 4.;
    float b1 = max(-box(p,vec3(2.,10.,1.5)),box(p,vec3(2.5,.4,2.)))-0.05;
    float w = max(water(p-vec3(0.,0.5,0.)),
                  box(p,vec3(2.5,.6,2.)));
    return min(smin(w,prim1(p),1.),b1);
}

float SDF (vec3 p)
{
    return min(water(p+vec3(0.,1.,0.)),min(fences(p),min(lanterns(p),stmin(scenery(p),pool(p),.7,3.))));
}

vec3 getcam (vec3 ro, vec3 tar, vec2 uv)
{
    vec3 f = normalize(tar-ro);
    vec3 l = normalize(cross(vec3(0.,1.,0.),f));
    vec3 u = normalize(cross(f,l));
    return normalize(f*0.85 + l*uv.x + u*uv.y);
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.05,0.);
    return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
}

float lighting (vec3 n, vec3 l)
{return max(dot(n,l),0.);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    float dither = hash21(uv);

    vec3 ro = vec3(0.001,3.,-8.),
        p = ro,
        tar = vec3(0.,0.5,0.),
        rd = getcam(ro,tar, uv),
        l = normalize(vec3(0.,0.5, -3.)),
        col = vec3(0.);

    float shad = 0.;

    for (float i=0.; i<64.; i++)
    {
        float d = SDF(p);
        if (d<0.01)
        {
            shad =  i/64.;
            break;
        }
        d *= 0.5+dither*0.1;
        p += d*rd;
    }

    float t = length(ro-p);
    vec3 n = getnorm(p);
    float fre = clamp(pow(1.-dot(n,-rd),2.5),0.,1.);
    float light = pow(floor(lighting(n,l)*4.)/4., 0.5);

    col = mix(vec3(0.,0.3,0.7), vec3(0.8,0.4,0.7), light)*(1.-fre);
    col += fre*vec3(1.,0.8,0.2);

    col = mix(col, vec3(0.7,0.8,0.6), 1.-exp(-0.003*t*t));

    fragColor = vec4(sqrt(col),1.);
}

