#define AA 2

// { 2d cell id, distance to border, distnace to center )
vec4 hexagon( vec2 p ) 
{
	vec2 q = vec2( p.x*2.0*0.5773503, p.y + p.x*0.5773503 );

	vec2 pi = floor(q);
	vec2 pf = fract(q);

	float v = mod(pi.x + pi.y, 3.0);

	float ca = step(1.0,v);
	float cb = step(2.0,v);
	vec2  ma = step(pf.xy,pf.yx);

    // distance to borders
	float e = dot( ma, 1.0-pf.yx + ca*(pf.x+pf.y-1.0) + cb*(pf.yx-2.0*pf.xy) );

	// distance to center	
	p = vec2( q.x + floor(0.5+p.y/1.5), 4.0*p.y/3.0 )*0.5 + 0.5;
	float f = length( (fract(p) - 0.5)*vec2(1.0,0.85) );		

	return vec4( pi + ca - cb*ma, e, f );
}

vec2 rot(in vec2 p, float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c) * p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int mm=0; mm<AA; mm++ )
    for( int nn=0; nn<AA; nn++ )
    {
        vec2 off = vec2(mm,nn)/float(AA);
        vec2 uv = (fragCoord+off)/iResolution.xy;
        vec2 pos = (-iResolution.xy + 2.0*(fragCoord+off))/iResolution.y;
    #else    
    {
        vec2 uv = fragCoord/iResolution.xy;
        vec2 pos = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
    #endif

        float d0 = hexagon(20.0*pos + .5*iTime).z;
        float d1 = hexagon(rot(20.0*pos, radians(10.) * sin(radians(15.) * iTime))  + .5*iTime).z;
        float d = min(d0, d1);

        vec3 col = vec3(1, 1, 1);
        col *= smoothstep( 0.02, 0.04, d );

        col *= pow( 16.0*uv.x*(1.0-uv.x)*uv.y*(1.0-uv.y), 0.1 );

        tot += col;
	}	
 	#if AA>1
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

#define PI 3.14159265359
#define VIEW_DIST 70.0
//change VIEW_DIST for farther or shorter render distance

struct Object
{
 	float dist;
    float difVal;
    float specVal;
    float specKs;
    float normEps; //artifacting was occuring for some objects when this value was too high
    			   //thus, you can specify a value for each object.
    vec3 color;
    vec3 normal;
};

struct MarchRes
{
 	float totalDist;
    vec3 curRay;
    Object obj;
};

struct Light
{
	float intensity;
    vec3 color;
