#define HASHSCALE1 .1031
#define PI 3.14159265

vec3 _Color = vec3(1.0,0.3,0.0);
float _GridSize = 30.;
float _RotSpd = 0.5;
float _YSpd = 0.7;

float Hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 acc = vec3(0.0,0.0,0.0);

    float rotDeg = 3.*_RotSpd * iTime;
    float yOffset = 4.*_YSpd* iTime;

    vec2 coord = uv*_GridSize - vec2(0.,yOffset);//æ´ä½æ²¿yè½´ä¸å
    if (abs(mod(coord.y,2.0))<1.0){
        coord.x += 0.5;
    }
    vec2 gridIndex = vec2(floor(coord));
    float rnd = Hash12(gridIndex);//æ ¹æ®ID è·åhashå¼
    // å¼¥è¡¥yè½´ä¸åçéå·® è·ååæ¥çyå¼ 
    // åæ¶å ä¸ºgridIndex = floor(coord) çåå   ä¼è®©tempYå¼å¨éå®åºå®çgridçåæ¶è¶æ¥è¶å¤§;
    float tempY = gridIndex.y + yOffset ;
    float life = min(10.0*(1.0-min((tempY)/(24.0-20.0*rnd),1.0)),1.0);
    if (life>0.0 ) {
        float size = 0.08*rnd;//è®©å¤§å°éæºå
        float deg = 999.0*rnd*2.0*PI + rotDeg*(0.5+0.5*rnd);//æ·»å æè½¬éæºå
        vec2 rotate = vec2(sin(deg),cos(deg));//åä½åæè½¬åç§»
        float radius =  0.5-size*0.2;
        vec2 cirOffset = radius*rotate;//
        vec2 part = fract(coord-cirOffset) - 0.5 ;//è®©æ ¼å­èªå·±æè½¬èµ·æ¥ ä½ç½®å æ¹åä¸å
        float len = length(part);
        float sparksGray = max(0.0,1.0 -len/size);//ç»å
        float sinval = sin(PI*1.*(0.3+0.7*rnd)*iTime+rnd*10.);//å ç¹äº®åº¦çååå®ç°éªç 
        float period = clamp(pow(pow(sinval,5.),5.),0.,1.);
        float blink =(0.8+0.8*abs(period));
        acc = life*sparksGray*_Color*blink;
    }

    // Output to screen
    fragColor = vec4(acc,1.0);
}

// A gnarly apollian tree
// Based upon: https://www.shadertoy.com/view/4ds3zn
#define PI  3.141592654

const int   max_iter      = 130;
const vec3  bone          = vec3(0.89, 0.855, 0.788);

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(c*p.x + s*p.y, -s*p.x + c*p.y);
}

float box(vec3 p, vec3 b) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float mod1(inout float p, float size) {
    float halfsize = size*0.5;
    float c = floor((p + halfsize)/size);
    p = mod(p + halfsize, size) - halfsize;
    return c;
}

vec2 modMirror2(inout vec2 p, vec2 size) {
    vec2 halfsize = size*0.5;
    vec2 c = floor((p + halfsize)/size);
    p = mod(p + halfsize, size) - halfsize;
    p *= mod(c,vec2(2))*2.0 - vec2(1.0);
    return c;
}

float apollian(vec3 p) {
  vec3 op = p;
  float s = 1.3 + smoothstep(0.15, 1.5, p.y)*0.95;
//  float s = 1.3 + min(pow(max(p.y - 0.25, 0.0), 1.0)*0.75, 1.5);
  float scale = 1.0;

  float r = 0.2;
  vec3 o = vec3(0.22, 0.0, 0.0);

  float d = 10000.0;

  const int rep = 7;

  for( int i=0; i<rep ;i++ ) {
    mod1(p.y, 2.0);
    modMirror2(p.xz, vec2(2.0));
    rot(p.xz, PI/5.5);

    float r2 = dot(p,p) + 0.0;
    float k = s/r2;
    float r = 0.5;
    p *= k;
    scale *= k;
  }

  d = box(p - 0.1, 1.0*vec3(1.0, 2.0, 1.0)) - 0.5;
  d = abs(d) - 0.01;
  return 0.25*d/scale;
}

float df(vec3 p) { 
  float d1 = apollian(p);
  float db = box(p - vec3(0.0, 0.5, 0.0), vec3(0.75,1.0, 0.75)) - 0.5;
  float dp = p.y;
  return min(dp, max(d1, db)); 
} 


float intersect(vec3 ro, vec3 rd, out int iter) {
  float res;
  float t = 0.2;
  iter = max_iter;

  for(int i = 0; i < max_iter; ++i) {
    vec3 p = ro + rd * t;
    res = df(p);
    if(res < 0.0003 * t || res > 20.) {
      iter = i;
      break;
    }
    t += res;
  }

  if(res > 20.) t = -1.;
  return t;
}

float ambientOcclusion(vec3 p, vec3 n) {
  float stepSize = 0.012;
  float t = stepSize;

  float oc = 0.0;

  for(int i = 0; i < 12; i++) {
    float d = df(p + n * t);
    oc += t - d;
    t += stepSize;
  }

  return clamp(oc, 0.0, 1.0);
}

vec3 normal(in vec3 pos) {
  vec3  eps = vec3(.001,0.0,0.0);
  vec3 nor;
  nor.x = df(pos+eps.xyy) - df(pos-eps.xyy);
  nor.y = df(pos+eps.yxy) - df(pos-eps.yxy);
  nor.z = df(pos+eps.yyx) - df(pos-eps.yyx);
  return normalize(nor);
}

vec3 lighting(vec3 p, vec3 rd, int iter) {
  vec3 n = normal(p);
  float fake = float(iter)/float(max_iter);
  float fakeAmb = exp(-fake*fake*9.0);
  float amb = ambientOcclusion(p, n);

  vec3 col = vec3(mix(1.0, 0.125, pow(amb, 3.0)))*vec3(fakeAmb)*bone;
  return col;
}

vec3 post(vec3 col, vec2 q) {
  col=pow(clamp(col,0.0,1.0),vec3(0.65)); 
  col=col*0.6+0.4*col*col*(3.0-2.0*col);  // contrast
  col=mix(col, vec3(dot(col, vec3(0.33))), -0.5);  // satuation
  col*=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);  // vigneting
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )  { 
  vec2 q=fragCoord.xy/iResolution.xy;
  vec2 uv = -1.0 + 2.0*q; 
  uv.y += 0.225;
  uv.x*=iResolution.x/iResolution.y; 

  vec3 la = vec3(0.0, 0.5, 0.0); 
  vec3 ro = vec3(-4.0, 1., -0.0);
  rot(ro.xz, 2.0*PI*iTime/120.0);
  vec3 cf = normalize(la-ro); 
  vec3 cs = normalize(cross(cf,vec3(0.0,1.0,0.0))); 
  vec3 cu = normalize(cross(cs,cf)); 
  vec3 rd = normalize(uv.x*cs + uv.y*cu + 3.0*cf);  // transform from view to world

  vec3 bg = mix(bone*0.5, bone, smoothstep(-1.0, 1.0, uv.y));
  vec3 col = bg;

  vec3 p=ro; 

  int iter = 0;

  float t = intersect(ro, rd, iter);

  if(t > -0.5) {
    p = ro + t * rd;
    col = lighting(p, rd, iter); 
    col = mix(col, bg, 1.0-exp(-0.001*t*t)); 
  } 


  col=post(col, q);
  fragColor=vec4(col.x,col.y,col.z,1.0); 
}

#define PI 3.14159265359
#define VIEW_DIST 70.0
//change VIEW_DIST for farther or shorter render distance

struct Object
{
 	float dist;
    float difVal;
    float specVal;
    float specKs;
    float normEps; //artifacting was occuring for some objects when this value was too high
    			   //thus, you can specify a value for each object.
    vec3 color;
    vec3 normal;
};

struct MarchRes
{
 	float totalDist;
    vec3 curRay;
    float minDist;
    float glowAmt;
    Object obj;
};

struct Light
{
	float intensity;
    vec3 color;
    vec3 pos;  
};

//iq
float sdSphere(vec3 pos, float rad)
{
 	return length(pos) - rad;
}

//iq
float sdPlaneInf(vec3 pos, float y)
{
 	return pos.y - y;   
}

//iq
float sdPlane( vec3 p, vec4 n )
{
  	return dot(p,n.xyz) + n.w;
}

//iq
float sdVerticalCapsule( vec3 p, float h, float r )
{
      p.y -= clamp( p.y, 0.0, h );
      return length( p ) - r;
}

//iq
float sdRoundBox( vec3 p, vec3 b, float r )
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

//iq
float sdEllipsoid( vec3 p, vec3 r )
{
    float k0 = length(p/r);
    float k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;
}

//slightly modified, but IQ
vec3 applyFog( in vec3  rgb,      // original color of the pixel
               in float distance, // camera to point distance
               in vec3  rayDir,   // camera to point vector
               in vec3  sunDir )  // sun light direction
{
    float fogAmount = 1.0 - exp( -distance*(distance*(0.001 / pow(clamp(VIEW_DIST / 70.0, 0.0, 1.0), 2.0))) );
    float sunAmount = max( dot( rayDir, sunDir ), 0.0 );
    vec3  fogColor  = mix( vec3(0.5,0.6,0.7), // bluish
                           vec3(0.5,0.6,0.7), // yellowish
                           pow(sunAmount,8.0) );
    return mix( rgb, fogColor, fogAmount );
}

//https://www.iquilezles.org/www/articles/smin/smin.htm
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

Object map(vec3 pos)
{
    Object o;
    o.difVal = 1.0;
    o.dist = 1000.0;
    o.normEps = 0.00001;
    o.color = vec3(0);

    float yOff = 0.05*sin(5.0*iTime);
    vec3 offset = vec3(0, yOff, 0);

    //ground
    /*vec3 boardPos = pos;
    boardPos.z = mod(boardPos.z, 10.0);
    boardPos.x = mod(boardPos.x, 12.0);
	float dBoard = sdRoundBox(boardPos - vec3(0,-3,0), vec3(12, 0.5, 10.0), 0.1);
    if(dBoard < o.dist)
    {
        o.dist = dBoard;
        o.difVal = 0.9;

        //checker board
        vec3 col;
        float modi = 2.0*(round(step(sin(pos.z*1.*PI), 0.0)) - 0.5);

        col = vec3(1.0);
        col *= (round(step((modi)*sin(pos.x*1.0*PI), 0.0)));

        o.color = col;
        o.specVal = 200.0;
        o.specKs = 0.5;
    }*/

    vec3 objPos = pos - vec3(0, 1.5, 0);
    float ang = PI/4.0;
    vec2 objPosXZ = mat2(cos(ang), -sin(ang), sin(ang), cos(ang)) * objPos.xz;
    objPos = vec3(objPosXZ.x, objPos.y, objPosXZ.y);

    float dCube = sdRoundBox(objPos, vec3(0.5), 0.1);
    float dSphere = sdSphere(objPos, 1.0);
    float dElip = sdEllipsoid(objPos, vec3(0.5, 1.0, 0.5));
    //float dCube1 = sdRound

    float mixVal = (1.0+sin((PI*(iTime + 0.1)) / 3.0));

    float dMix = mix(dSphere, dCube, mixVal) * step(mixVal, 1.0001)
        		 + mix(dCube, dElip, mixVal - 1.0) * step(mixVal, 2.0) * step(1.0, mixVal);
    if(dMix < o.dist){
        o.dist = dMix;
        o.color = vec3(113.0 / 255.0, 55.0/255.0, 225.0/255.0);
        o.specVal = 55.0;
        o.specKs = 1.0;
    }


    return o;
}

vec3 calcNormal(vec3 pos, float ep)
{
    return normalize(vec3(map(pos + vec3(ep, 0, 0)).dist - map(pos - vec3(ep, 0, 0)).dist,
                		map(pos + vec3(0, ep, 0)).dist - map(pos - vec3(0, ep, 0)).dist,
                		map(pos + vec3(0, 0, ep)).dist - map(pos - vec3(0, 0, ep)).dist));                                
}

MarchRes marchRay(vec3 pos, vec3 dir, float speed)
{
 	MarchRes res;
    res.minDist = 100000.0;
    res.glowAmt = 1.0;
    Object o;

    res.totalDist = 0.001;

    for(int x=0; x<200; x++)
    {
 		res.curRay = pos + (dir*res.totalDist);

        o = map(res.curRay);
        res.glowAmt = min(res.glowAmt, 0.5*o.dist/(0.02+abs(0.02*sin(iTime))*res.totalDist));
        if(abs(o.dist) < 0.00001)
        {
            res.minDist = o.dist;
            res.obj = o;
            break;
        }
        else if(res.totalDist >= VIEW_DIST) break;

        if(o.dist < res.minDist)
            res.minDist = o.dist;
        res.totalDist += o.dist*speed; // repalce 0.8 w/ this for trippy mode ;p => (0.3+0.2*(sin(iTime))); //couldn't handle the hair :' (
    }

    if(res.totalDist < VIEW_DIST)
    {
        o.normal = calcNormal(res.curRay, o.normEps);
        res.obj = o;
    }

    res.glowAmt = max(res.glowAmt, 0.0);
    res.glowAmt = smoothstep(0.0, 1.0, res.glowAmt);// res.glowAmt*res.glowAmt*(3.0-2.0*res.glowAmt);
    return res;
}

vec3 calcDiffuseLight(Object o, Light l, vec3 pos)
{
    vec3 dir = normalize(l.pos - pos);
    return (o.color) * l.intensity * l.color * clamp(dot(o.normal, dir), 0.0, 1.0) * o.difVal;   
}

vec3 calcSpecLight(Object o, Light l, vec3 pos, vec3 camPos)
{
 	vec3 dir = normalize(l.pos - pos);  
    vec3 viewDir = normalize(camPos - pos);
    vec3 specR = 2.0*clamp( dot(o.normal, dir), 0.0, 1.0) * o.normal - dir;
    float spec = clamp( dot(viewDir, specR), 0.0, 1.0);
    //lightInt*(lightCol*pow(lightSpec, res.obj.specVal))*res.obj.specKs * lightShadow;

    return o.specKs*l.intensity*(l.color*pow(spec, o.specVal));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord - iResolution.xy)/iResolution.y;
	vec3 camEye = vec3(0, 2.4,3.0);
    vec3 dir = normalize(vec3(uv, -1));


    float camAng = -0.2;
    float camAngX = iMouse.y/20.0;

    mat2 rotCam = mat2( vec2(cos(camAng), -sin(camAng)), vec2(sin(camAng), cos(camAng)) );
    mat2 rotCamX = mat2( vec2(cos(camAng), sin(camAng)), vec2(-sin(camAng), cos(camAng)) );

    vec2 camXZ = rotCam * camEye.xz;
    //camEye = vec3(camXZ.x, camEye.y, camXZ.y);
    vec2 dirZY = rotCamX * dir.yz;
    dir = vec3(dir.x, dirZY.xy);

    Light light;
    light.intensity = 1.00;
    light.pos = vec3(0, 10, 10);
    light.color = vec3(1);

    Light lightSky;
    lightSky.intensity = 3.1;
    lightSky.pos = vec3(0, 3, 0);
    lightSky.color = vec3(0.1, 0.1, 0.1);

    //add bounce light

	MarchRes res = marchRay(camEye, dir, 1.0);
    vec3 pos = res.curRay;  

    /*float timeMod = 1.0 - sqrt(pow(uv.x, 2.0) + pow(uv.y, 2.0));
    float time = iTime * timeMod;
    vec2 newUv = mat2(cos(time), sin(time), -sin(time), cos(time)) * uv;
    //newUv.y -= timeMod;
    float y = newUv.y + 1.0;
    y = y / 1.62;
    y = clamp(y, 0.7, 1.0);*/
    vec2 newUv = uv;
	//newUv *= 100.0;
    vec3 uv3D = vec3(newUv, iTime/3.0);
    uv3D.z = mod(uv3D.z, 1.0);
    uv3D.xy = -0.25 + mod(uv3D.xy, 0.5);
    uv3D.xy = uv3D.xy/uv3D.z;


    float y = uv.y + 2.3;
    y = y / 1.62;
    vec3 col = (vec3(0.3, 0.0, 0.75)*(y) + vec3(0.8)*(1.7 - y));
	col += step(sqrt(pow(uv3D.x, 2.0) + pow(uv3D.y, 2.0)), 0.5) 
        * step(0.45, sqrt(pow(uv3D.x, 2.0) + pow(uv3D.y, 2.0))) * vec3(1,0,0);

    if(res.totalDist < VIEW_DIST)
    {
        col = calcDiffuseLight(res.obj, light, pos) + calcDiffuseLight(res.obj, lightSky, vec3(0,0,0));
        col += calcSpecLight(res.obj, light,pos, camEye);
        col = applyFog(col, sqrt(pow(pos.x,2.0) + pow(pos.y,2.0) + pow(pos.z,2.0)), normalize(pos), normalize(light.pos - pos)); 	
    }else{
     	if(res.minDist < 0.3) //check if we barely missed the surface, if so, draw an outline :)
            col = mix(vec3(0.2, 0.5, 0.75), col, res.glowAmt);
    }

    fragColor = vec4(col,1.0);
}

