float random (in vec2 st) { 
    return fract(sin(dot(st.xy, vec2(12.9898,78.233)))* 43758.5453123);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) + 
            (c - a)* u.y * (1.0 - u.x) + 
            (d - b) * u.x * u.y;
}

#define OCTAVES 6
float fbm (in vec2 st, in float speed) {
    // Initial values
    float value = 0.0;
    float amplitud = .5;
    float frequency = 0.;
    //
    // Loop of octaves
    for (int i = 0; i < OCTAVES; i++) {
        value += amplitud * noise(st + speed * iTime);
        st *= 2.;
        amplitud *= .5;
    }
    return value;
}












void combineColors( out vec4 adjustedColor, in vec4 bg, in vec4 color ) {
    float a = color.a;

    float r = (1.0 - a) * bg.r + a * color.r;
    float g = (1.0 - a) * bg.g + a * color.g;
    float b = (1.0 - a) * bg.b + a * color.b;

    adjustedColor = vec4(r, g, b, 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;

    const float c0 = 128.0;

    float speed = 0.35;
    float spread = 2.0;

    float its = mix(0.0, 1.0 / c0, 0.985 + (0.015 * sin(speed * iTime)));
    float ofs = texture(iChannel0, fragCoord.xy / iChannelResolution[0].xy / spread).r;

    vec3 ditherColor;
    ditherColor = vec3(its + (ofs / 255.0));
    ditherColor.rgb = floor( ditherColor.rgb * 255.0 ) / 255.0;
    ditherColor.rgb *= c0;

    vec4 outColor = vec4(vec3(0.0), 1.0 - min(ditherColor.r, min(ditherColor.g, ditherColor.b)));

    vec2 st = uv;
    st.x *= iResolution.x / iResolution.y;
    vec3 noise = vec3(0.0);
    noise += fbm(st * 3.344, speed * 5.0);


    outColor = vec4(outColor.rgb, (outColor.a) * smoothstep(0.0, 1.0, (noise.r + noise.g + noise.b) / 3.0) - 0.085);

    combineColors(fragColor, vec4(1.0), outColor);

}

#define PI 3.14159265358

