// Fix a floating point number to two decimal places
float toFixedTwo(float f) {
    return float(int(f * 100.0)) / 100.0;
}

// Returns the slope of a line given the degrees of the angle on which that line is rotated;
float slopeForDegrees(float deg) {
	// Ensure degrees stay withing 0.0 - 360.0
    deg = mod(deg, 360.0);

    float radians = deg * (PI / 180.0);

    return tan(radians);
}

// Returns slope adjusted for screen ratio.
float normalizedSlope(float slope) {
    vec2 p = vec2(1.0) / iResolution.xy;
	return ((slope * 100.0) / p.x) / (100.0 / p.x);
}

// Returns offsets (+/-) for any coordinate at distance given slope.
//   Note: This function does not normalize distance.
//   Note: This function does not adjust slope for screen ratio.
vec2 offsetsForCoordAtDistanceOnSlope(float d, float slope) {
	return vec2(
        (d * cos(atan(slope))),
        (d * sin(atan(slope)))
    );
}

// Returns a boolean designating whether or not an infinite line intersects with an infinite line, and sets an `out` variable for the intersection point if it is found.
//   Note: This function does not adjust slope for screen ratio.
bool lineLineIntersection (out vec2 intersect, in vec2 p1, in float m1, in vec2 p2, in float m2) {
	// See: http://gamedev.stackexchange.com/questions/44720/line-intersection-from-parametric-equation
	//      http://stackoverflow.com/questions/41687083/formula-to-determine-if-an-infinite-line-and-a-line-segment-intersect/41687904#41687904

	bool isIntersecting = false;

	float dx = 1.0;
	float dy = m1;

	float dxx = 1.0;
	float dyy = m2;

	float denominator = ((dxx * dy) - (dyy * dx));
	if (denominator == 0.0) {
		// Lines are parallel
		return isIntersecting;
	}

   	if (isinf(dy)) {
		isIntersecting = true;
		intersect = vec2(p1.x, p2.y);
		return isIntersecting;
	}

	if (isinf(dyy)) {
		isIntersecting = true;
		intersect = vec2(p2.x, p1.y);
		return isIntersecting;
	}

	float u = ((dx * (p2.y - p1.y)) + (dy * (p1.x - p2.x))) / denominator;

	isIntersecting = true;
	intersect = p2 + (u * vec2(dxx, dyy));

	return isIntersecting;
}

// Returns a boolean designating whether or not an infinite line intersects with a line segment, and sets an `out` variable for the intersection point if it is found.
//   Note: This function does not adjust slope for screen ratio.
bool lineLineSegmentIntersection (out vec2 intersect, in vec2 point, in float m, in vec2 pA, in vec2 pB) {
	// See: http://gamedev.stackexchange.com/questions/44720/line-intersection-from-parametric-equation
	//      http://stackoverflow.com/questions/41687083/formula-to-determine-if-an-infinite-line-and-a-line-segment-intersect/41687904#41687904

	bool isIntersecting = false;

	float dx = 1.0;
	float dy = m;

	float dxx = pB.x - pA.x;
	float dyy = pB.y - pA.y;

	float denominator = ((dxx * dy) - (dyy * dx));
	if (denominator == 0.0) {
		// Lines are parallel
		return isIntersecting;
	}

	if (isinf(dy)) {
		isIntersecting = true;
		intersect = vec2(point.x, pB.y);
		return isIntersecting;
	}

	float u = ((dx * (pA.y - point.y)) + (dy * (point.x - pA.x))) / denominator;

	if (u >= 0.0 && u <= 1.0) {
		// Intersection occured on line segment
		isIntersecting = true;
		intersect = pA + (u * vec2(dxx, dyy));
	}

	return isIntersecting;
}

// Dev Note: Terrible code. Needs refactor. Just trying to find 
//   which two edges of the rect the intersections occur at.
void intersectsOnRectForLine(out vec2 iA, out vec2 iB, in vec2 rMinXY, in vec2 rMaxXY, in vec2 point, in float slope) {
	bool firstIntersectFound = false;

	vec2 intersectA = vec2(0.0);
	vec2 intersectB = vec2(0.0);
	vec2 intersectC = vec2(0.0);
	vec2 intersectD = vec2(0.0);

	bool intersectsLeft = lineLineSegmentIntersection(intersectA, point, slope, rMinXY, vec2(rMinXY.x, rMaxXY.y));
	bool intersectsTop = lineLineSegmentIntersection(intersectB, point, slope, vec2(rMinXY.x, rMaxXY.y), rMaxXY);
	bool intersectsRight = lineLineSegmentIntersection(intersectC, point, slope, rMaxXY, vec2(rMaxXY.x, rMinXY.y));
	bool intersectsBottom = lineLineSegmentIntersection(intersectD, point, slope, rMinXY, vec2(rMaxXY.x, rMinXY.y));

	if (intersectsLeft) {
		iA = intersectA;
		firstIntersectFound = true;
	}

	if (intersectsTop) {
		if (firstIntersectFound) {
			iB = intersectB;
		}
		else {
			iA = intersectB;
			firstIntersectFound = true;
		}
	}

	if (intersectsRight) {
		if (firstIntersectFound) {
			iB = intersectC;
		}
		else {
			iA = intersectC;
			firstIntersectFound = true;
		}
	}

	if (intersectsBottom) {
		if (firstIntersectFound) {
			iB = intersectD;
		}
		else {
			iA = intersectD;
		}
	}
}

float isWave(vec2 uv, float deg, float wavelength, float edgeDistance, float centerDistance) {

    // Setup
    // -------------------------------

    // Dev Note: I've seen some strange artifacting when the input degrees have
    //   a high number of decimal places. Fixing degrees to a max of two decimal places
    //   fixes this bug. Not sure that it's the full solution but the problem is mitigated.
	deg = toFixedTwo(deg);

    vec2 centerUv = vec2(0.5);
	float slope = normalizedSlope(slopeForDegrees(deg));
    float perpendicularDeg = mod(deg + 90.0, 360.0); // Offset angle by 90.0, but keep it from exceeding 360.0
    float perpendicularSlope = normalizedSlope(slopeForDegrees(perpendicularDeg));


    // Get wave details
    // -------------------------------

    // Find point on elipses (imagine oval where radius moves between half height and half width) for perpendicularDeg, adjusting for 
    //   distance from, center space, and amplitude of our wave. Resolution x/y divided by 4 for two waves on screen with amplitude 
    //   equal to 1/4 directional resolution.
    float ampX = (((iResolution.x / 4.0) - (edgeDistance / 2.0) - (centerDistance / 2.0)) * cos(perpendicularDeg * (PI / 180.0)));
    float ampY = (((iResolution.y / 4.0) - (edgeDistance / 2.0) - (centerDistance / 2.0)) * sin(perpendicularDeg * (PI / 180.0)));
    float amplitude = distance(vec2(0.0), vec2(ampX, ampY));

    // Find point on elipses (imagine oval where radius moves between half height and half width) for perpendicularDeg, adjusting for 
    //   distance from, center space, and amplitude of our wave. Resolution x/y divided by 4 for two waves on screen with amplitude 
    //   equal to 1/4 directional resolution. We add centerDistance here (note amplitude subtracts it) to push radius towards edge,
    //   adjusting for shortened amplitude.
  	float radiusX = (((iResolution.x / 4.0) - (edgeDistance / 2.0) + (centerDistance / 2.0)) * cos(perpendicularDeg * (PI / 180.0)));
  	float radiusY = (((iResolution.y / 4.0) - (edgeDistance / 2.0) + (centerDistance / 2.0)) * sin(perpendicularDeg * (PI / 180.0)));
    float radius = abs(distance(vec2(0.0), vec2(radiusX, radiusY)));


    // Get offset UVs for width of line, adjacent to center point
    // -------------------------------

    float angleA = perpendicularDeg;
    float angleB = mod(perpendicularDeg + 180.0, 360.0); // Offset angle by 180.0, but keep it from exceeding 360.0

    float slopeA = normalizedSlope(slopeForDegrees(angleA));
    float slopeB = normalizedSlope(slopeForDegrees(angleB));

    vec2 kA = offsetsForCoordAtDistanceOnSlope(radius, slopeA);
    vec2 kB = offsetsForCoordAtDistanceOnSlope(radius, slopeB);

    vec2 offsetCoordA = centerUv * iResolution.xy;
    vec2 offsetCoordB = centerUv * iResolution.xy;

    if (angleA <= 90.0 || angleA >= 270.0) {
        offsetCoordA += kA;
    }
    else {
        offsetCoordA -= kA;
    }

    if (angleB <= 90.0 || angleB >= 270.0) {
        offsetCoordB += kB;
    }
    else {
        offsetCoordB -= kB;
    }

    // Denormalize uvs and uv offsets and find closest point on line for our uv, then renormalize.
    vec2 uvA = vec2(0.0);
	lineLineIntersection(uvA, offsetCoordA, slope, uv * iResolution.xy, perpendicularSlope);
	uvA /= iResolution.xy;
    vec2 uvB = vec2(0.0);
	lineLineIntersection(uvB, offsetCoordB, slope, uv * iResolution.xy, perpendicularSlope);
	uvB /= iResolution.xy;


	// Find intersects for line with edges of viewport
    // -------------------------------

    vec2 uvAIntersectA = vec2(0.0);
    vec2 uvAIntersectB = vec2(0.0);
    intersectsOnRectForLine(uvAIntersectA, uvAIntersectB, vec2(0.0), iResolution.xy, offsetCoordA, slope);

    vec2 uvBIntersectA = vec2(0.0);
    vec2 uvBIntersectB = vec2(0.0);
    intersectsOnRectForLine(uvBIntersectA, uvBIntersectB, vec2(0.0), iResolution.xy, offsetCoordB, slope);

    vec2 perpendicularIntersectA = vec2(0.0);
    vec2 perpendicularIntersectB = vec2(0.0);
    // Note: We use `uvA` here. `uvB` would have same effect and what we want is to
    //   find the intersect perpedicular to the uv's closest point on the line.
    intersectsOnRectForLine(perpendicularIntersectA, perpendicularIntersectB, vec2(0.0), iResolution.xy, uvA * iResolution.xy, perpendicularSlope); 


    // Prepare wave
    // -------------------------------

    // Find length of each edge of line in relation to the viewport, and to the
    //   perpendicular line cutting through each uv's closest point on the line.
    float aLength = distance(uvAIntersectA, uvAIntersectB);
    float bLength = distance(uvBIntersectA, uvBIntersectB);
    float perpendicularIntersectionLength = distance(perpendicularIntersectA, perpendicularIntersectB);

    // Find percent of edge length that the uv's closest point on the line constitutes
    //   for each edge of the line.
    float aDistance = (distance(offsetCoordA, uvA * iResolution.xy) / aLength) - ((wavelength / 4.0) / aLength);
    float bDistance = (distance(offsetCoordB, uvB * iResolution.xy) / bLength) - ((wavelength / 4.0) / bLength);

    // Create wave, adjusting input wavelength and amplitude (in pixels) in accordance with
    //   resolution of line edges along the line's slope. This is kind of convuluted so 
    //   consider a value between 0 and 1 on a diagonal line. That's the value on which each
	//   'pixel' of the wave's edge must be based. 
    float varianceA = sin(aDistance * PI * (aLength / (wavelength / 2.0))) * (amplitude / perpendicularIntersectionLength);
    float varianceB = sin(bDistance * PI * (bLength / (wavelength / 2.0))) * (amplitude / perpendicularIntersectionLength);
    varianceA *= perpendicularIntersectionLength;
    varianceB *= perpendicularIntersectionLength;

    // Use wave variance as offsets of each point along our line and adjust each uv's closest
    //   point on the line accordingly.
    vec2 kVA = offsetsForCoordAtDistanceOnSlope(varianceA, slopeA);
    vec2 kVB = offsetsForCoordAtDistanceOnSlope(varianceB, slopeB);

    if (angleA <= 90.0 || angleA >= 270.0) {
        offsetCoordA += kVA;
    }
    else {
        offsetCoordA -= kVA;
    }

    if (angleB <= 90.0 || angleB >= 270.0) {
		offsetCoordB += kVB;
    }
    else {
    	offsetCoordB -= kVB;
    }

	lineLineIntersection(uvA, offsetCoordA, slope, uv * iResolution.xy, perpendicularSlope);
	uvA /= iResolution.xy;
	lineLineIntersection(uvB, offsetCoordB, slope, uv * iResolution.xy, perpendicularSlope);
	uvB /= iResolution.xy;


    // Define wave edges
    // -------------------------------

   	float distX = abs(step(uvB, uv).x - step(uvA, uv).x);
   	float distY = abs(step(uvB, uv).y - step(uvA, uv).y);
   	float wave = max(distX, distY);


    return wave;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // Setup
    // -------------------------------

	vec2 uv = fragCoord.xy / iResolution.xy;

    lowp float deg = 90.0;

    float wavelength = 100.0;

    if (iMouse.z > 0.0) {
    	wavelength = iMouse.x;
    }

    float distanceFromEdge = 20.0;
    float distanceFromCenter = 50.0;
	float speed = 0.025;


    // Prepare edge details 
    // -------------------------------

	deg = fract(iTime * speed) * 360.0;
    float wave = isWave(uv, deg, wavelength, distanceFromEdge, distanceFromCenter);

    fragColor = 1.0 - vec4(wave);
}

void mainImage(out vec4 c, in vec2 uv) 
{
  uv = (uv * 2.0 - iResolution.xy) / iResolution.xy / 10.0;
  uv *= mat2x2(cos(iTime),sin(iTime),-sin(iTime),cos(iTime)) / 5.0;
  uv /= vec2(dot(uv,uv) * 30.0, dot(uv,uv) * 20.0);
  c.rgb = vec3(mod(floor(uv.x)+floor(uv.y),2.0));
