// Signed distance to a 2D rounded box

// List of some other 2D distances:
//
// Triangle:             https://www.shadertoy.com/view/XsXSz4
// Isosceles Triangle:   https://www.shadertoy.com/view/MldcD7
// Regular Triangle:     https://www.shadertoy.com/view/Xl2yDW
// Regular Pentagon:     https://www.shadertoy.com/view/llVyWW
// Regular Octogon:      https://www.shadertoy.com/view/llGfDG
// Rounded Rectangle:    https://www.shadertoy.com/view/4llXD7
// Rhombus:              https://www.shadertoy.com/view/XdXcRB
// Trapezoid:            https://www.shadertoy.com/view/MlycD3
// Polygon:              https://www.shadertoy.com/view/wdBXRW
// Hexagram:             https://www.shadertoy.com/view/tt23RR
// Regular Star:         https://www.shadertoy.com/view/3tSGDy
// Ellipse 1:            https://www.shadertoy.com/view/4sS3zz
// Ellipse 2:            https://www.shadertoy.com/view/4lsXDN
// Quadratic Bezier:     https://www.shadertoy.com/view/MlKcDD
// Uneven Capsule:       https://www.shadertoy.com/view/4lcBWn
// Vesica:               https://www.shadertoy.com/view/XtVfRW
// Cross:                https://www.shadertoy.com/view/XtGfzw
// Pie:                  https://www.shadertoy.com/view/3l23RK
// Arc:                  https://www.shadertoy.com/view/wl23RK
// Horseshoe:            https://www.shadertoy.com/view/WlSGW1
// Parabola:             https://www.shadertoy.com/view/ws3GD7
//
// and many more here:   http://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm


// (0, 0) is the center of the box.
// The four corners of the box are (+/- b.x, +/- b.y).
// d is the vector between p and the closest box corner,
// mapped into the positive quadrant: negative values
// for dimensions inside the box and positive values for
// dimensions outside the box.
// max(d, vec2(0)) is 0 for dimensions inside the box
// and negative for dimensions outside the box.
// length(max(d,vec2(0))) is 0 if p is inside the box and
// the distance from the closest point inside the box if
// p is outside the box.
// If p is outside the box, max(d.x,d.y) is positive and
// min(max(d.x,d.y),0.0) is zero.
// If p is inside the box, max(d.x,d.y) is the negative
// distance from p to the closest point on an edge of the box,
// and min(max(d.x,d.y),0.0) is the same.

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

	vec2 ra = 0.4 + 0.3*cos( iTime + vec2(0.0,1.57) + 0.0 );

	float d = sdBox( p, ra);

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(150.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );

	fragColor = vec4(col,1.0);
}

float N21Old(vec2 p) {
   return fract( sin(p.x * 100. + p.y*6574.)*5647.);
}

float N21(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx*1500.) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float SmoothNoise(vec2 uv) {
   vec2 cv = fract(uv);
   vec2 id = floor(uv);
   cv = cv*cv*(3.-2.*cv);


   float bl = N21(id);
   float br = N21(id+vec2(1, 0));
   float b = mix(bl, br, cv.x);

   float tl = N21(id+vec2(0, 1));
   float tr = N21(id+vec2(1, 1));
   float t = mix(tl, tr, cv.x);
   return mix(b, t, cv.y);
}

float SmoothNoise2(vec2 uv, float nBase) {
   float c = SmoothNoise(uv*nBase);
   c += SmoothNoise(uv*(2.*nBase)) * .5;
   c += SmoothNoise(uv*(4.*nBase)) * .25;
   c += SmoothNoise(uv*(8.*nBase)) * .125;
   c += SmoothNoise(uv*(16.*nBase)) * .0625;

   return c / 1.9;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord/iResolution.xy;
   uv += iTime * .4;
   float c = SmoothNoise2(uv, 4.);

   vec3 col = vec3(c);

   fragColor = vec4(col,1.0);
}

#define PIP 

// View distance
const float view=6500.0;

// Iterations
const int N=400;

// Lipschitz constant
const float K=8.0;

// Hashing function
float Hash(vec3 p)  
{
    p  = fract( p*0.3183099+0.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

// Procedural value noise
float Noise( in vec3 x ) // x : Point
{
    vec3 i = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    return mix(mix(mix( Hash(i+vec3(0,0,0)), 
                        Hash(i+vec3(1,0,0)),f.x),
                   mix( Hash(i+vec3(0,1,0)), 
                        Hash(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( Hash(i+vec3(0,0,1)), 
                        Hash(i+vec3(1,0,1)),f.x),
                   mix( Hash(i+vec3(0,1,1)), 
                        Hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}

float ridge_noise(in vec2 p, in float k)
{
    float n  = Noise(vec3(p.xy,0.0));
    for( float i=0.0; i<k; i++ )
    {
        n = min(n, Noise(vec3(p.xy + i*5.0, 0.0)));
    }
    return n;
}

// Terrain
float Terrain( in vec2 x ) // x : Position in the plane
{
    const mat2 m2 = mat2(0.8,-0.6,0.6,0.8); // Rotation matrix

    const float l0 = 600.0; // longueur d'onde ini
    const float a0 = 400.0; // y (altitude) scale

	vec2  p = x/l0;
    float a = 0.0;      // altitude
    float alpha = 1.0;  // coeff de reduction
    float b = 1.0;      // frÃ©quence rÃ©duite par 2

    for( int i=0; i<8; i++ ) 
    {
        float n = ridge_noise(p, 2.0);
        a += b*n *alpha; 
        alpha *= smoothstep(-0.30, 0.99 , a); 
		b *= 0.6; 
        p = m2*p*2.0;
    }
	return 0.0 +a0*a;
}

bool Intersect( in vec3 ro, in vec3 rd, out float t, out int i)
{
    t = 0.0;
	for( i=0; i<N; i++ )
	{
        vec3 pos = ro + t*rd;
		float h = pos.y - Terrain( pos.xz );
		if( abs(h)<(0.01*t)  ) return true;
		if( t>view ) return false;
		// Empirical Lipschitz constant with level of detail (the further, the larger steps)
        t += h*sqrt(1.0+8.0*t/view)/K;
	}

	return false;
}

// Normal of the Terrain
vec3 Normal( in vec2 p) // p : Position in the plane
{
    vec2 eps = vec2( 0.25, 0.0 );
    return normalize( vec3( Terrain(p-eps.xy) - Terrain(p+eps.xy),
                            2.0*eps.x,
                            Terrain(p-eps.yx) - Terrain(p+eps.yx) ) );
}

vec4 Render( in vec3 ro, in vec3 rd, bool pip )
{
    // ro : Camera transform
    // rd : Camera ray

    // Light direction
    vec3 light1 = normalize( vec3(-0.8,0.4,-0.3) );

	vec3 color;
    float t;
    int it;
    bool b=Intersect( ro, rd, t , it);
    if( b==false)
    {
        // sky		
        color = vec3(0.35,0.65,0.95) - rd.y*rd.y*0.5;
    }
	else
	{
        // mountains		
		vec3 p = ro + t*rd;
        vec3 n = Normal( p.xz );

        vec3 ref = reflect( rd, n );
        float fre = clamp( 1.0+dot(rd,n), 0.0, 1.0 );
        vec3 hal = normalize(light1-rd);


        color=vec3(0.471,0.361,0.110);

         // Pseudo diffuse lighting
		float dif = 0.5*(1.0+dot( light1, n ));
        dif*=dif*2.0; // *alpha: intensitÃ© 

        color += dif*vec3(0.35,0.35,0.35);

		// fog
        float fo = 1.0-exp(-pow(0.0005*t,1.5) );
        fo *= 0.85;
        //float fo = 0.f; // fog dÃ©sactivÃ©
        vec3 fco = 0.65*vec3(0.4,0.65,1.0);
        color = mix( color, fco, fo );

	}

    // Shading for iterations
    if (pip==true)
    {
    float s=float(it)/float(N);
	return vec4(0.112+0.824*s,0.418+0.556*s,0.611-0.515*s,t);
    }
    else
    {
    // Gamma with square root
    return vec4( sqrt(color), t );
    }
}

// Camera path
vec3 Path( float time )
{
    // desynchronize cos and sin for epic lissajou path, circle otherwise
	return vec3( 1100.0*cos(0.25*time), (613.0 ), 1100.0*sin(0.85*time) );
}

mat3 moveCamera(float time, out vec3 ro)
{
	// Origin
    ro = Path( time );

    // Target
    vec3 ta = Path( time + 3.0 );
	ta.y = ro.y - 362.0;

	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(0.0, 1.0,0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw +0.2);
}

vec2 RayDirection(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/3.0;
    // Picture in picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p=(-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip=true;
    }
       else
       {
           pip=false;
       }
   }
   return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   // Time as function of the position of the mouse
    float time = iTime*0.2 + 4.0*iMouse.x/iResolution.x;

    // Camera transform
    vec3 ro; // set par moveCamera
    mat3 cam = moveCamera( time, ro);   


    // Picture in picture
    bool pip=true;

    // Pixel coordinates
    vec2 p = RayDirection(fragCoord, pip);

    // Camera ray    
    vec3 rd = cam * normalize(vec3(p,3.0)); 

    // Render
    vec4 res = Render( ro, rd, pip );

    fragColor = vec4( res.xyz, 0.0 );

    //fragColor = vec4( 0.0,0.0,0.0, 0.0 );
}

#define time iTime

float cube(vec3 p, vec3 s)
{
   	float v = 0.78; 
   	p.xy *= mat2(cos(v),-sin(v),sin(v),cos(v)) ;
   	vec3 q = fract(p)*2.0 -1.0;
	return length(max(abs(q)-s,0.0));
}

float trace(vec3 o, vec3 r)
{
	float t =0.0;
    for(int i = 0; i < 100;i++)
    {
    	vec3 p = o+r*t;
        float d = cube(p-vec3(-2,0.5,0),vec3(0.01,0.05,3));
        t += d * 0.25;         
    }
    return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = vec2(fragCoord.x/iResolution.x,fragCoord.y/iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y/iResolution.x,1.0);
	uv.y *= (sin(5.0*uv.y+ time)*0.5+0.5)/5.0;
    vec3 r = normalize(vec3(uv,1.0)); 
    r.xy*= mat2(cos(0.79),-sin(0.79),sin(0.79),cos(0.79)) ;
    vec3 o = vec3(-0.75,0,0);
    float t = trace(o,r);

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float fog = 1.0/(1.0+t*t*0.01);


    fragColor = vec4(vec3(fog*col),1.0);
}

