float GetDist(vec3 p  ) {	



    float d;
    float dif1;
    float dif2;
    float dif3;
    float dif4;


    d=999.9;
    float planeDist = p.y;
    vec2 res;

    res=vec2(planeDist,4);


    float bxInf = sdBox (p-vec3(0.0,1,4.5),vec3(2.65,1.5,0.8));


    float sdc0=sdCylinder( p.xzy-vec3(-1.5,4.5,1.5), vec2(0.5,4.0) ) ;
    float sdc1=sdCylinder( p.xzy-vec3(0.0,4.5,1.5), vec2(0.5,2.0) ) ;
    float sdc2=sdCylinder( p.xzy-vec3(1.5,4.5,1.5), vec2(0.5,4.0) ) ;;



    res = opU(res, vec2(bxInf, CInf));

    res = opU(res, vec2(sdc0, CAtras));
    res = opU(res, vec2(sdc1, CAtras));
    res = opU(res, vec2(sdc2, CAtras));


    d = res.x;
    mObj.dist = res.x;
    mObj.tipo = res.y;

    return d;
}


float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.2;
    //Determines size of shadow
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}





vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    //Texture of white and black in image
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}



float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos.xz += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}






void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    TObj Obj;

    //Color of each object

    vec3 ro = vec3(0, 2.5, -3.5);


    //vec3 rLuz=vec3(-0.5, 1.5, -5.5);
    vec3 rLuz=vec3(-0.5, -4.5, -5.5);


    vec3 rd = normalize(vec3(uv.x, uv.y, 1.0));

    float d = RayMarch(ro, rd);
    Obj=mObj;



    vec3 p = (ro + rd * d ); 
    vec3 pluz=(rLuz + rd * d );
    float dif = GetLight(p);

    float dif2 = abs(1.0- GetLight( pluz));
    vec3 colobj;

    colobj=getSphereColor(int( Obj.tipo));
    dif=(dif+dif2)/1.5;

    col = vec3(dif)*colobj;



    fragColor = vec4(col,1.0);
    //Background color is white
}

