vec4 hxHit;
vec3 ltDir;
float tCur, dstFar, aMin, dLoop, aLoop, hLen, rCyl;
const float pi = 3.14159, sqrt3 = 1.73205;
const float nSeg = 5.;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 b, c;
  float dMin, d, lb, dr, rSph;
  dMin = dstFar;
  rSph = 0.9 *rCyl;
  p.x -= - 0.5 * hLen;
  p.xy = Rot2D (p.xy, 0.5 * pi - aLoop);
  for (float k = 0.; k < nSeg; k ++) {
    q = p;
    q.xy = Rot2D (q.xy, 0.5 * pi);
    q.xy = Rot2D (vec2 (q.x, q.y - dLoop), aLoop - 0.5 * pi);
    b = vec2 (length (q.xy) - dLoop, q.z);
    lb = length (b);
    c = atan (vec2 (q.y, b.x), vec2 (- q.x, b.y)) * vec2 (4. * dLoop / pi, 1.) / pi;
    dr = SmoothBump (0.15, 0.85, 0.1, mod (2. * c.x +
       2. * c.y * (mod (float (k), 2.) - 0.5), 1.)) - 0.5;
    d = max (min (abs (lb - 0.95 * rCyl) + 0.05 * dr * rCyl, lb - 0.7 * rCyl),
       dot (vec2 (q.x, abs (q.y)), sin (aLoop + vec2 (0., 0.5 * pi))));
    if (k == 0.) d = min (d, PrSphDf (p, rSph));
    p.xy = Rot2D (q.xy, aLoop) + vec2 (dLoop, 0.);
    p.x *= -1.;
    if (k == nSeg - 1.) d = min (d, PrSphDf (p, rSph));
    if (d < dMin) {
      dMin = d;
      hxHit = vec4 (c, lb, k);
    }
  }
  return 0.8 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float HexEdgeDist (vec2 p)
{
  p = abs (p);
  return (sqrt3/2.) - p.x + 0.5 * min (p.x - sqrt3 * p.y, 0.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 vn, col;
  vec2 p, ip;
  float dstObj, tCyc, sLoop, vDotL, sh, c;
  tCyc = 10.;
  aMin = 0.6;
  sLoop = aMin + 20. * pow (1. - SmoothBump (0.25, 0.75, 0.24, mod (tCur / tCyc, 1.)), 4.);
  aLoop = 0.25 * pi / sLoop;
  dLoop = 6. * 0.25 * pi * sLoop;
  hLen = 2. * nSeg * dLoop * sin (aLoop);
  rCyl = 2.;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (hxHit.z < 0.2 * rCyl) {
      col4 = (hxHit.x < 0.) ? vec4 (1., 0., 0., -1.) : vec4 (0., 1., 0., -1.);
    } else if (hxHit.z < 0.91 * rCyl) {
      hxHit.x -= 1.5 * hxHit.y * (mod (hxHit.w, 2.) - 0.5);
      p = hxHit.xy * vec2 (2. * sqrt3, 3.);
      ip = PixToHex (p);
      c = mod (dot (mod (2. * ip + ip.yx, 3.), vec2 (1., 2.)), 3.);
      col4 = (c == 0.) ? vec4 (0.7, 0.6, 0., 0.2) : ((c == 1.) ? vec4 (0.8, 0.8, 0.4, 0.2) :
         vec4 (0.4, 0.2, 0., 0.2));
      col4 *= 0.3 + 0.7 * smoothstep (0.05, 0.07, HexEdgeDist (p - HexToPix (ip)));
    } else {
      col4 = vec4 (0.8, 0.8, 0.9, 0.2);
    }
    if (col4.a >= 0.) {
      sh = ObjSShadow (ro, ltDir);
      vDotL = max (dot (vn, ltDir), 0.);
      col = col4.rgb * (0.2 + 0.8 * sh * vDotL * vDotL) +
         0.2 * step (0.95, sh) * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
    } else col = col4.rgb * (0.5 + 0.5 * max (- dot (vn, rd), 0.));
  } else {
    col = vec3 (0.2);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  el = 0.;
  az = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 2. * pi * mPtr.y;
  } else {
    az += 0.01 * 2. * pi * tCur;
  }
  zmFac = 3.5;
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -50.);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  dstFar = 100.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (pow (col, vec3 (0.8)), 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

