const int iter =100;
const float eps = 0.001, far = 30.;
vec3 lDir0 = normalize(vec3(1,2,1)), lDir1 = normalize(vec3(-1,1.0,-2));
vec3 lCol0 = vec3(1,.8,.5), lCol1 = vec3(.6,0.8,1); 


float maxcomp(in vec3 p ) { return max(p.x,max(p.y,p.z));}

float sdBox( vec3 p, vec3 b )
{
  vec3  di = abs(p) - b;
  float mc = maxcomp(di);
  return min(mc,length(max(di,0.0)));
}


float cylUnion(vec3 p){
    float xy = dot(p.xy,p.xy);
    float xz = dot(p.xz,p.xz);
    float yz = dot(p.yz,p.yz);
    return sqrt(min(xy,min(xz,yz))) - 1.;
}

float cylIntersection(vec3 p){
    float xy = dot(p.xy,p.xy);
    float xz = dot(p.xz,p.xz);
    float yz = dot(p.yz,p.yz);
    return sqrt(max(xy,max(xz,yz))) - 1.;
}


//-------------------------------------------

float dsSphere(vec3 center, float r, vec3 point)// basic sphere SDF
{
    // point is point pos in space, center is sphere's center, r is its radius
    return length(point - center) - r;
}

float dsCapsule(vec3 point_a, vec3 point_b, float r, vec3 point_p)//cylinder SDF
{
 	vec3 ap = point_p - point_a;
    vec3 ab = point_b - point_a;
    float ratio = dot(ap, ab) / dot(ab , ab);
    ratio = clamp(ratio, 0.0, 1.0);
    vec3 point_c = point_a + ratio * ab;
    return length(point_c - point_p) - r;
}

float dsTorus(vec3 center, float r1, float r2, vec3 point)
{
 	float x = length(point.xz - center.xz) - r1;
    float y = point.y - center.y;
    float dist = length(vec2(x,y)) - r2;
    return dist;
}
//--------------------------------------------


float DE(vec3 p){

    //float d = cylIntersection(p);
	float d;
    float distToCapsule = dsCapsule(vec3(-0.0,0.0,0.0), vec3(2.0,1.0,0.1), 1.0, p);    

    d=distToCapsule;

    float s = 1.;


    for(int i = 0;i<5;i++){
        p *= 3.;
    	s*=3.;
    	float d2 = cylUnion(p) / s;


        float d3=sdBox(p, vec3(2.0,1.0,2.5));
        //d2=d2*d3/2.0;

        //float m = texelFetch(iChannel0, ivec2(32, 0), 0).x * 2. - 1.;
        float m = -1.0;

    	d = max(d,m*d2);
   	 	p = mod(p+1. , 2.) - 1.; 	
    }
    return d;
}

vec2 march(vec3 ro, vec3 rd){
	float t=0. , d = far, it = 0.;
    for (int i=0;i<iter;i++){
     	t += (d = DE(ro+t*rd));
        if(d<eps || t> far) break;
        it += 1.;
    }
    return vec2(t,it/float(iter));
}

float getShadow(vec3 p, vec3 n, vec3 ld){
    p +=  2. * eps * n;
    float t=0.,d=far;
    for(int i=0;i<50;i++){
        t += (d=DE(p + t*ld));
        if (d<eps || t>3.) break;
    }
    return t<=3. ? 0.1 : 1. ;
}

vec3 getRay(vec3 ro, vec3 look, vec2 uv){
    vec3 f = normalize(look - ro);
    vec3 r = normalize(vec3(f.z,0,-f.x));
    vec3 u = cross (f,r);
    return normalize(f + uv.x * r + uv.y * u);
}

vec3 getNorm(vec3 p){
    vec2 e = vec2(eps, 0);
    return normalize(vec3(DE(p+e.xyy)-DE(p-e.xyy),DE(p+e.yxy)-DE(p-e.yxy),DE(p+e.yyx)-DE(p-e.yyx)));
}

vec3 light(vec3 p, vec3 n){
    vec3 col = vec3(0);
    for(int i=0;i<2;i++){
        vec3 ld = (i==0) ? lDir0 : lDir1;
        float diff = max(dot(n, (ld)),0.);
        diff *= getShadow(p, n, ld);
        col += diff * (i==0 ? lCol0 : lCol1);
    }
    return col * .7;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //float time = iTime * .04;
    float time = iTime * .6;


	vec2 uv = (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;
    float s1 = sin(time), c1 = cos(time);
    float arg = 1.5*sin(time*.7894)*.5 + .5;
    float s2 = sin(arg), c2 = cos(arg);

    vec3 ro = vec3(0, .2, 1)*2.5;


    ro.yz = mat2(c2,-s2,s2,c2)*ro.yz;
    ro.xz = mat2(c1,s1,-s1,c1)*ro.xz;

    vec3 rd = getRay(ro, vec3(0.), uv);
    vec2 hit = march(ro, rd);
    vec3 p = ro + hit.x*rd;
    vec3 col = hit.x<far ? light(p, getNorm(p)) : vec3(.1*(1.-length(uv)));
    col += pow(hit.y,3.);
	fragColor = vec4(sqrt(col),1.0);
}

#define COLORF            vec3(1.0, 0.5, 0.0)
#define COLORG            vec3(0.1, 0.9, 0.1)
#define COLORH            vec3(0.2, 0.2, 1.0)

#define COLOR_INNER       vec3(1.0, 1.0, 1.0)

#define OUTER 0.07
#define INNER 0.01


float pulse(float factor, float x)
{
    float t = factor*x;
    return t*exp(1.0-t);
}

vec3 mindist_f(float a, float b, vec2 uv, float step)
{
    vec3 mindist = vec3(10., 10., 10.);
    for(float d=-0.05; d<=0.05; d+=0.005) {
        float x = uv.x + d;
        float y1 = 0.5 + (0.30 + a + b) * sin(0.5 + x * 12. + step);
        float y2 = 0.5 + (0.25 + 1.2 * a + 0.8 * b) * sin(4. + x * 9. + step * 1.1);
        float y3 = 0.5 + (0.20 + 1.4 * a + 0.7 * b) * sin(3. + x * 7. + step * 1.2);
        float dist1 = distance(uv, vec2(x, y1));
        float dist2 = distance(uv, vec2(x, y2));
        float dist3 = distance(uv, vec2(x, y3));
        mindist = min(mindist, vec3(dist1, dist2, dist3));
    }
    return mindist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1 for y, from 0 to n for x)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    // Step based on time
    float step = iTime * 3.;

    // Slow and small amplitude.
    float a = 0.02 * cos(iTime * 4.);
    // Longer and larger amplitude
    float b = 0.1 * pulse(10., mod(iTime * 1.5, 4.));

    vec3 dist = mindist_f(a, b, uv, step);
    float fx = dist[0];
    float gx = dist[1];
    float hx = dist[2];
    // Blue and white parts.
    float fcolor = smoothstep(OUTER, 0., fx);
    float fwhite = smoothstep(INNER, 0., fx);
    float gcolor = smoothstep(OUTER, 0., gx);
    float gwhite = smoothstep(INNER, 0., gx);
    float hcolor = smoothstep(OUTER, 0., hx);
    float hwhite = smoothstep(INNER, 0., hx);
    vec3 color = fcolor * COLORF +
                 gcolor * COLORG +
                 hcolor * COLORH +
                 fwhite * COLOR_INNER +
                 gwhite * COLOR_INNER +
                 hwhite * COLOR_INNER;

    fragColor = vec4(color, 1.0);

}

#define PI 3.1415

void rotate2d(inout vec2 v, float a) {
    mat2 m = mat2(cos(a), -sin(a), sin(a), cos(a));
    v = m * v;
}

float arrow(vec2 av) {

    float line1 = length(av - vec2(0., clamp(av.y, -0.2, 0.2)));
    line1 = smoothstep(0.06, 0.05, line1);

    vec2 rav = av;
    rav.y += 0.23;
    rotate2d(rav, PI/4.);

    float line2 = length(rav - vec2(0., clamp(rav.y, 0., 0.2)));
    line2 = smoothstep(0.06, 0.05, line2);

    rotate2d(rav, -PI/2.);
    float line3 = length(rav - vec2(0., clamp(rav.y, 0., 0.2)));
    line3 = smoothstep(0.06, 0.05, line3);

    return clamp(line1 + line2 + line3, 0., 1.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0);

    float time = fract(iTime / 1000.) * 600.;

    float at = fract(time); // 0..1
    at -= 0.5;
	at *= at * at;

    vec2 av = uv;
    av.y += at * 6.;
	float a = arrow(av);

    float cir = length(uv);
    cir = smoothstep(0.36, 0.35, cir);

    float ca = fract(time); // 0..1
    ca = (ca * 6. - 1.);
    ca *= -ca;
    ca += 1.;
    ca /= 2.;
    ca = clamp(ca, 0., 1.);
    float cir2 = length(uv - vec2(0., .65 + ca/12.));
    cir2 = smoothstep(0.26 + ca/1., 0.15 + ca/4., cir2);


    float ca2 = fract(time); // 0..1
    ca2 = (ca2 * 6. - 5.);
    ca2 *= -ca2;
    ca2 += 1.;
    ca2 /= 2.;
    ca2 = clamp(ca2, 0., 1.);
    float cir3 = length(uv - vec2(0., -.65 + ca2/12.));
    cir3 = smoothstep(0.26 + ca2/1., 0.15 + ca2/4., cir3);

    col = vec3(cir - a - cir2 - cir3);

    fragColor = vec4(col,1.0);
}

const int N = 6;

float sdPoly( in vec2[N] v, in vec2 p )
{
    const int num = v.length();
    float d = dot(p-v[0],p-v[0]);
    float s = 1.0;
    for( int i=0, j=num-1; i<num; j=i, i++ )
    {
        // distance
        vec2 e = v[j] - v[i];
        vec2 w =    p - v[i];
        vec2 b = w - e*clamp( dot(w,e)/dot(e,e), 0.0, 1.0 );
        d = min( d, dot(b,b) );

        // winding number from http://geomalgorithms.com/a03-_inclusion.html
        bvec3 cond = bvec3( p.y>=v[i].y, p.y<v[j].y, e.x*w.y>e.y*w.x );
        if( all(cond) || all(not(cond)) ) s*=-1.0;  
    }

    return s*sqrt(d);
}

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    float a = 3.1415/3.;

    //float[] ivs = float[](14.,0.,31.,31.,31.,31.);

    float center=2.;
    float lim=31.;

    vec2[6] poly;
    vec2[6] bg;
    float[6] ivs;
    for (int i=0;i<6;i++){
        float fi = float(i);
        ivs[i] = noise(vec2((iTime), fi*a))*lim;
    	poly[i] = vec2(sin(fi*a), cos(fi*a))/(lim+center)*(ivs[i]+center);
        bg[i] = vec2(sin(fi*a), cos(fi*a))/(lim+center)*(lim+center);
    }

	float d = sdPoly(poly, p );
    float db = sdPoly(bg, p );

    vec3 gaugec = vec3(.5,.8,.8)*smoothstep(0.,-0.01,d);
    vec3 bgc = vec3(.4,.4,.5)*smoothstep(0.,-0.01,db);

	fragColor = vec4(max(gaugec, bgc),1.0);
}

#define pi 3.141592654

vec2 hash22(vec2 p)//Dave Hoskins https://www.shadertoy.com/view/4djSRW
{
	return fract(cos(p*mat2(-64.2,71.3,81.4,-29.8))*8321.3);
}


float Worley(vec2 q)
{
    float n = 9e9;

    for(int i = -1; i < 2; i++){
        for(int j = -1; j < 2; j++){
            vec2 p = floor(q) + vec2(i, j);
            vec2 h = hash22(p);
            vec2 g = p + 0.5+ 0.5 * sin(h*12.6);
            float xx = abs(q.x-g.x);
            float yy = abs(q.y-g.y);
            float d = max(xx, yy);
            if(d < n){n = d;}
            //n = n*1;
        }
    }
    return n;
}

void mainImage( out vec4 Color, in vec2 Coord)
{
	Color = vec4(vec3(Worley(Coord.xy/32.0+iTime)),1.0+iTime);
}

