// Animated texture inspired by Zone Of The Enders VR training scene

// Original version can be seen in the video https://youtu.be/L0oue-ccvNM

// A bit of a hacked together procedural texture computing the closest distance to a hexagon grid using planes and voronoi regions
float hexTex(vec2 Tex)
{
    float degAlpha = 57.0;

    float alpha = (degAlpha) / 360.0 * 2.0 * 3.14159, cal = cos(alpha), sal = sin(alpha);
	float beta = ((90.0 - degAlpha) / 360.0 * 2.0 * 3.14159), cbe = cos(beta), sbe = sin(beta);
	float r = 0.5 / cbe;
	vec2 P1 = vec2(r*sbe, r*cbe);
	vec2 P2 = vec2(2.0 * r*cal, 0.0);
	vec2 N = normalize(P2 - P1);
	float D = dot(vec2(-N.y, N.x), P1);

	float v = 1.0 - 2.0 * abs(0.5 - mod(abs(Tex.y), 1.0));
	float v2 = 1.0 - 2.0 * abs(0.5 - mod(abs(Tex.x), 1.0));
	float d = (dot(vec2(-N.y, N.x), vec2(v2, v)) - D);
	float d2 = (dot(vec2(-N.y, -N.x), vec2(v2, v)));

	if (v2 <= r * cal)
	{
		if (d2 > 0.0)
			d = abs(d) / D;
		else if (v < 0.5)
			d = abs(0.5 - v) * 2.0;
		else if (d > 0.0)
			d = abs(d2) / D;
		else
			d = abs(0.5 - v) * 2.0;
	}
	else if (d <= 0.0)
	{
		d = abs(d) / D;
	}
	else
	{
		if (v2 >= 2.0 * r*cal)
		{
			if (v < 0.5)
			{
				vec2 P3 = vec2(0.25, 0.5);
				vec2 N2 = normalize(P3 - P2);
				float D2 = dot(vec2(-N2.y, N2.x), P2);
				float d3 = (dot(vec2(-N2.y, -N2.x), vec2(v2, v)) - D2);
				if (d3 < 0.0)
					d = 1.0 - abs(0.5 - v) * 2.0;
				else
					d = abs(d) / D;
			}
			else
			{
				float d4 = (dot(vec2(-N.y, N.x), vec2(v2, v)) - D * 2.0);
				if (d4 > 0.0)
					d = 1.0 - abs(0.5 - v) * 2.0;
				else
					d = abs(d2) / D;
			}
		}
		else if (v > 0.5)
			d = abs(dot(vec2(-N.y, -N.x), vec2(v2, v))) / D;
		else
			d = abs(d) / D;
	}

    return min(0.999, d);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float c = pow(1.0-hexTex(uv.xy*vec2(6.0,3.0)),3.1+2.0*sin(iTime)); // eval texture and bend with pow
    float ang = atan(uv.x-0.5,uv.y-0.5);
    float len = length(uv.xy-vec2(0.5,0.5))*5.0;
    float clrRing = 2.0 * max(0.0,-len * 0.2 + pow(1.0 - abs(sin(-iTime + len + 0.2*cos(ang * 6.0))), 3.0) * (0.5 + pow(c, 2.0)));
    c = c*abs(sin(iTime*2.0))*(1.0-len*0.3)+clrRing; // combine and animate more

    // Output to screen
    fragColor = vec4(0,c,c,1.0);
}

// by @etiennejcb
// Using code from : https://www.shadertoy.com/view/Xt2XDt
// and 3D Simplex noise from https://www.shadertoy.com/view/XsX3zB
// Got help from @tdhooper

const float MAX_TRACE_DISTANCE = 100.0;           // max trace distance
const float INTERSECTION_PRECISION = 0.0001;        // precision of the intersection
const int NUM_OF_TRACE_STEPS = 190;


vec3 random3(vec3 c) {
	float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
	vec3 r;
	r.z = fract(512.0*j);
	j *= .125;
	r.x = fract(512.0*j);
	j *= .125;
	r.y = fract(512.0*j);
	return r-0.5;
}

/* skew constants for 3d simplex functions */
const float F3 =  0.3333333;
const float G3 =  0.1666667;

/* 3d simplex noise */
float simplex3d(vec3 p) {
	 /* 1. find current tetrahedron T and it's four vertices */
	 /* s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices */
	 /* x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices*/

	 /* calculate s and x */
	 vec3 s = floor(p + dot(p, vec3(F3)));
	 vec3 x = p - s + dot(s, vec3(G3));

	 /* calculate i1 and i2 */
	 vec3 e = step(vec3(0.0), x - x.yzx);
	 vec3 i1 = e*(1.0 - e.zxy);
	 vec3 i2 = 1.0 - e.zxy*(1.0 - e);

	 /* x1, x2, x3 */
	 vec3 x1 = x - i1 + G3;
	 vec3 x2 = x - i2 + 2.0*G3;
	 vec3 x3 = x - 1.0 + 3.0*G3;

	 /* 2. find four surflets and store them in d */
	 vec4 w, d;

	 /* calculate surflet weights */
	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);

	 /* w fades from 0.6 at the center of the surflet to 0.0 at the margin */
	 w = max(0.6 - w, 0.0);

	 /* calculate surflet components */
	 d.x = dot(random3(s), x);
	 d.y = dot(random3(s + i1), x1);
	 d.z = dot(random3(s + i2), x2);
	 d.w = dot(random3(s + 1.0), x3);

	 /* multiply d by w^4 */
	 w *= w;
	 w *= w;
	 d *= w;

	 /* 3. return the sum of the four surflets */
	 return dot(d, vec4(52.0));
}

