float lineSDF(vec2 p,vec2 p1,vec2 p2){
	vec2 v1=p-p1;
    vec2 v2=p2-p1;
    vec2 projv1ATv2=dot(v1,v2)*v2/dot(v2,v2);
    vec2 k=projv1ATv2/v2;

    if(k.x<0.||k.y<0.)return length(v1);
    if(k.x>1.||k.y>1.)return length(p-p2);
	float d=length(v1-projv1ATv2);
