vec3 woodTexture(in vec3 p) {
	float rings = repramp(length(p.xz + vec2(NoiseGen(p*vec3(8.0, 1.5, 8.0)), 
                                             NoiseGen(-p*vec3(8.0, 1.5, 8.0)+4.5678))*0.05)*64.0) / 1.8;
	rings -= NoiseGen(p *1.0)*0.75;
	vec3 te = mix(vec3(0.3, 0.19, 0.075)*0.95, vec3(1.0, 0.73, 0.326)*0.4, rings)*1.5;
	te = max(vec3(0.0), te);
	float rough = (NoiseGen(p*64.0*vec3(1.0, 0.2, 1.0))*0.1+0.9);
	te *= rough;
    te = clamp(te, 0., 1.);
    return te;
}
