void rotate(inout vec2 point, float rad)
{
    float c = cos(rad);
    float s = sin(rad);
    float px = point.x * c - point.y * s;
    float py = point.x * s + point.y * c;
    point.x = px;
    point.y = py;
}

float dot2(in vec3 v )
{
    return dot(v,v);
}

