float sdBox(in vec3 p, in vec3 b)
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sdRoundCone(in vec3 p, in vec3 a, in vec3 b, in float r1, in float r2)
{
    // sampling independent computations (only depend on shape)
    vec3  ba = b - a;
    float l2 = dot(ba,ba);
    float rr = r1 - r2;
    float a2 = l2 - rr*rr;
    float il2 = 1.0/l2;

    // sampling dependant computations
    vec3 pa = p - a;
    float y = dot(pa,ba);
    float z = y - l2;
    float x2 = dot2(pa*l2 - ba*y);
    float y2 = y*y*l2;
    float z2 = z*z*l2;

    // single square root!
    float k = sign(rr)*rr*rr*x2;
    if( sign(z)*a2*z2 > k ) return  sqrt(x2 + z2)        *il2 - r2;
    if( sign(y)*a2*y2 < k ) return  sqrt(x2 + y2)        *il2 - r1;
                            return (sqrt(x2*a2*il2)+y*rr)*il2 - r1;
}

float sdCylinder(in vec3 p, in vec2 h)
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdCylinderH(in vec3 p, in vec2 h)
{
  vec2 d = abs(vec2(length(p.xy),p.z)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
//-------------------- End: distance functions (primitives)

// Intersection
float opI(in float d1, in float d2)
{
    return max(d2,d1);
}

// Subtraction
float opS(in float d1, in float d2)
{
    return max(-d2,d1);
}

// Union
vec2 opU(in vec2 d1, in vec2 d2)
{	
    return (d1.x<d2.x) ? d1 : d2;
}

// Union (float)
float opU(in float d1, in float d2)
{	
    return min(d1,d2);
}

// Smooth Union
vec2 opSU(in vec2 d1, in vec2 d2, in float k)
{
	float h = clamp( 0.5 + 0.5*(d2.x-d1.x)/k, 0.0, 1.0 );
	return vec2(mix( d2.x, d1.x, h ) - k*h*(1.0-h), d1.y);
}

// Smooth Union (float)
float opSU(in float d1, in float d2, in float k)
{
	float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
	return mix( d2, d1, h ) - k*h*(1.0-h);
}

// Repeat with limit
vec2 opRepLim(in vec2 p, in float s, in vec2 limmin, in vec2 limmax)
{
    return p-s*clamp(round(p/s),-limmin,limmax);
}

//-------------------- Start: Chess Pieces
vec2 pawn(in int id, in vec3 p)
{
    vec3 pos = p;
    vec2 bottom = vec2(opS(sdSphere(pos-vec3( 0.0, 0.55, 0.0), 0.55), 
                           sdBox(pos-vec3(0., .55 + .5, 0.), vec3(1.,.5,1.))), 10.0);

    vec2 top = vec2(opS(sdSphere(pos-vec3( 0.0, 0.55, 0.0), 0.55), 
                        sdBox(pos-vec3(0., .55 - .5, 0.), vec3(1.,.5,1.))), float(id)+100.0);    
    top = opSU(top, vec2(sdSphere(pos-vec3( 0.0, 1.55, 0.0), 0.33 ), 4. ), 0.38);

    return opU(bottom, top);
}

vec2 bishop(in int id, in vec3 p) 
{
    vec3 pos = p;
    vec2 bottom = vec2(opS(sdSphere(pos-vec3( 0.0, 0.7, 0.0), 0.7), 
                           sdBox(pos-vec3(0., .7 + .5, 0.), vec3(1.,.5,1.))), 10.0);

    vec2 top = vec2(opS(sdSphere(pos-vec3( 0.0, 0.7, 0.0), 0.7), 
                        sdBox(pos-vec3(0., .7-.5, 0.), vec3(1.,.5,1.))), float(id)+100.0);

    top = opSU(top, vec2(sdCylinder(vec3(pos-vec3(0.0, 1.5, 0.0)), vec2(.15, .7)), float(id)+100.0), 0.6);
    top = opSU(top, vec2(sdSphere(pos-vec3( 0.0, 2.5, 0.0), 0.4 ), float(id)+100.0), 0.3);
    top.x = opS(top.x, sdBox(pos-vec3(0., 2.8, 0.), vec3(.05,0.2,1.05)));

    return opU(bottom, top);
}

vec2 king(in int id, in vec3 p)
{
    vec3 pos = p;
    vec2 bottom = vec2(opS(sdSphere(pos-vec3( 0.0, 0.7, 0.0), 0.7), 
                           sdBox(pos-vec3(0., .7 + .5, 0.), vec3(1.,.5,1.))), 10.0);

    vec2 top = vec2(opS(sdSphere(pos-vec3( 0.0, 0.7, 0.0), 0.7), 
                        sdBox(pos-vec3(0., .7-.5, 0.), vec3(1.,.5,1.))), float(id)+100.0);

    top = opSU(top, vec2(sdCylinder(vec3(pos-vec3(0.0, 1.7, 0.0)), vec2(.15, 1.4)), float(id)+100.0), 0.6);
    top = opSU(top, vec2(sdSphere(pos-vec3( 0.0, 2.9, 0.0), 0.4 ), float(id)+100.0), 0.3);
    top = opSU(top, vec2(sdCylinder(vec3(pos-vec3(0.0, 2.0, 0.0)), vec2(.3, .04)), float(id)+100.0), 0.12);

    vec2 hat = vec2(sdBox(pos-vec3(0.,3.4,0.), vec3(.05,.2,.05)), 10.0);
    hat = opU(hat, vec2(sdBox(pos-vec3(0.,3.45,0.), vec3(.18,.05,.05)), 10.0));

    return opU(opU(bottom, top), hat);
}

vec2 queen(in int id, in vec3 p) 
{   
    vec3 pos = p;
    vec2 bottom = vec2(opS(sdSphere(pos-vec3( 0.0, 0.7, 0.0), 0.7), 
                           sdBox(pos-vec3(0., .7 + .5, 0.), vec3(1.,.5,1.))), 10.0);

    vec2 top = vec2(opS(sdSphere(pos-vec3( 0.0, 0.7, 0.0), 0.7), 
                        sdBox(pos-vec3(0., .7-.5, 0.), vec3(1.,.5,1.))), float(id)+100.0);

    top = opSU(top, vec2(sdCylinder(vec3(pos-vec3(0.0, 1.7, 0.0)), vec2(.15, 1.4)), float(id)+100.0), 0.6);
    top = opSU(top, vec2(sdSphere(pos-vec3( 0.0, 2.9, 0.0), 0.4 ), float(id)+100.0), 0.3);
    top = opSU(top, vec2(sdCylinder(vec3(pos-vec3(0.0, 2.0, 0.0)), vec2(.3, .04)), float(id)+100.0), 0.12);

    vec2 hat = vec2(sdSphere(pos-vec3(0.,3.35,0.), .1), 10.0);

    return opU(opU(bottom, top), hat);
}

vec2 rook(in int id, in vec3 p)
{
    vec3 pos = p;
    vec2 bottom = vec2(opS(sdSphere(pos-vec3( 0.0, 0.7, 0.0), 0.7), 
                           sdBox(pos-vec3(0., .7 + .5, 0.), vec3(1.,.5,1.))), 10.0);

    vec2 top = vec2(opS(sdSphere(pos-vec3( 0.0, 0.7, 0.0), 0.7), 
                        sdBox(pos-vec3(0., .7-.5, 0.), vec3(1.,.5,1.))), float(id)+100.0);

    top = opSU(top, vec2(sdCylinder(vec3(pos-vec3(0.0, 1.2, 0.0)), vec2(.285, .7)), float(id)+100.0), 0.3);
    top = opSU(top, vec2(sdCylinder(pos-vec3( 0.0, 2.1, 0.0), vec2(0.45,0.15)), float(id)+100.0), 0.3);
    top.x = opS(top.x, sdBox(pos-vec3(0., 2.3, 0.), vec3(.05,0.2,1.05)));
    top.x = opS(top.x, sdBox(pos-vec3(0., 2.3, 0.), vec3(1.05,0.2,.05)));

    return opU(bottom, top);
}

vec2 knight(in int id, in vec3 p) 
{
    vec3 pos = p;
    vec2 bottom = vec2(opS(sdSphere(pos-vec3( 0.0, 0.7, 0.0), 0.7), 
                           sdBox(pos-vec3(0., .7 + .5, 0.), vec3(1.,.5,1.))), 10.0);

    vec2 top = vec2(opS(sdSphere(pos-vec3( 0.0, 0.7, 0.0), 0.7), 
                        sdBox(pos-vec3(0., .7-.5, 0.), vec3(1.,.5,1.))), float(id)+100.0);

    top = opU(top, vec2(opS(opI(sdBox(pos-vec3(0., 1.7, 0.), vec3(.7,1.,.15)),
                                sdCylinderH(pos-vec3(3.005-.68, .7, 0.), vec2(3.01,.15))),
                                sdCylinderH(pos-vec3(3.0-.20, .7, 0.), vec2(3.0,.25))), float(id)+100.0));

    vec3 boxPos = pos;
    rotate(boxPos.xy, 3.15/5.9);
    top.x = opU(top.x, sdBox(boxPos - vec3(-0.9,1.7,0.0), vec3(.4,.25,.15)));
    top.x = opS(top.x, sdBox(boxPos - vec3(-1.5,2.82,0.0), vec3(1.1,1.0,.35)));

    return opU(bottom, top);
}
//-------------------- End: Chess Pieces

vec2 map(in vec3 pos)
{
    // Board
    vec2 res = vec2(sdBox(pos - vec3(-0.,-.5,-0.), vec3(16., .45, 16.)), 11.0);
    vec2 board = vec2(sdBox(pos - vec3(-0.05,.3,-0.05), vec3(7.9, .3, 7.9)), 1.0);     
    res = opU(res, board);

    // Cut repeated spheres
    vec3 q = pos - vec3(1.,0.,1.);
    q.xz = opRepLim( q.xz, 2., vec2(4.0,4.0), vec2(3.0,3.0));
    res.x = opS(res.x, sdSphere(q - vec3(0., 5.4, 0.), 5.));   

    // Add chess pieces
    // We can place one of each piece, and the rest is symmetry.
    float bottom = 0.325;

    // Unique id for each chess piece
    int id = int(clamp(pos.z + 8.,0.,15.) / 2.0)*8 + int(clamp(pos.x + 8.,0.,15.) / 2.0);

    vec3 mirrorZPos = pos;
    mirrorZPos.z = abs(mirrorZPos.z);

    vec3 mirrorZXPos = mirrorZPos;
    mirrorZXPos.x = abs(mirrorZXPos.x);

    vec3 pawnPos = mirrorZPos - vec3(1.,0.,5.);
    pawnPos.xz = opRepLim( pawnPos.xz, 2., vec2(4.0,0.0), vec2(3.0,0.0));
	res = opU(res, pawn(id, pawnPos - vec3(0., bottom, 0.)));

    vec3 knightPos = mirrorZPos - vec3(5.,0.,7.);
    knightPos.xz = opRepLim( knightPos.xz, 10., vec2(1.0,0.0), vec2(0.0,0.0));
	res = opU(res, knight(id, knightPos - vec3(0., bottom, 0.)));    

	res = opU(res, rook(id, mirrorZXPos - vec3(7., bottom, 7.)));    
	res = opU(res, bishop(id, mirrorZXPos - vec3(3., bottom, 7.)));    
    res = opU(res, queen(id, mirrorZPos - vec3(1., bottom, 7.)));
    res = opU(res, king(id, mirrorZPos - vec3(-1., bottom, 7.)));

    return res;
}

vec2 castRay(in vec3 ro, in vec3 rd)
{
    float tmin = .05;
    float tmax = 100.0;

    float t = tmin;
    float m = -1.0;
    for( int i=0; i<70; i++ )
    {
	    float precis = 0.0001*t;
	    vec2 res = map( ro+rd*t );
        if( res.x<precis || t>tmax ) break;
        t += res.x;
	    m = res.y;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}

float calcShadow(in vec3 ro, in vec3 rd, float k)
{
    float res = 1.0;

    float t = .2;
    for( int i=0; i<70; i++ )
    {
        vec3 pos = ro + t*rd;
        float h = map( pos ).x;
        res = min( res, k*max(h,0.0)/t );
        if( res<0.001 ) break;
        t += clamp(h,0.01,0.5);
    }

    return res;
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal(in vec3 pos)
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
}

float calcAO(in vec3 pos, in vec3 nor)
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) * (0.5+0.5*nor.y);
}

vec4 colorForMat(in float m, in vec3 pos, in vec3 nor) 
{
	vec3 col = vec3(0.);
    float mirror = 0.;

    if( m<1.5 )
    {    
        if (nor.y > 0.5) {
            col = (mod(pos.x, 4.0)>2.)!=(mod(pos.z, 4.0)>2.) ? .01+texture(iChannel0,pos.zx*1.5).xyz*.15 : .3+texture(iChannel0,pos.zx*0.5).xyz*.7*vec3(.7,1.,1.);
        } else if (abs(nor.x) > 0.5) {
            col = .01+texture(iChannel0,pos.zy*0.5).xyz*.15;
        } else {
			col = .01+texture(iChannel0,pos.xy*0.5).xyz*.15;
        }
  	} 
    else if ( m==10.0 ) 
    {
        col = vec3(0.01);
        mirror = 0.9;
    }
    else if ( m==11.0 )
    {
        col = vec3(0.4);
        mirror = 0.5;
        col = (0.5 + texture(iChannel1,pos.xz*.05).xyz * 0.5) * .6;
    }
    else if ( m >= 100.0 ) 
    {
        int id = int(m - 100.0);
        vec3 te = woodTexture(pos.xyz*0.75);

        if (id >= 32) {
            col = 0.01 + te*0.15*vec3(1.3,1.,1.);
        } else {
        	col = 0.2 + te*0.8;
        }
    }

    return vec4(clamp(col,0.0,1.0), mirror);
}

vec3 lighting(in vec3 pos, in vec3 nor, in vec3 rd, in vec3 ref, out float spe) 
{
	float occ = calcAO( pos, nor );
    vec3  lig = normalize( vec3(-0.6, 0.7, -0.1) );
    vec3  hal = normalize( lig-rd );
    float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
    float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
    float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
    float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );

    dif *= calcShadow(pos, lig, 50.);

    spe = pow(clamp(dot(nor, hal), 0.0, 1.0),16.0)*
        dif *
        (0.04 + 0.96*pow(clamp(1.0+dot(hal,rd),0.0,1.0), 5.0));

    vec3 lin = vec3(0.0);

	lin += 3.80*dif*vec3(1.30,1.00,0.70);
	lin += 0.55*amb*vec3(0.40,0.60,1.15)*occ;
	lin += 0.55*bac*vec3(0.25,0.25,0.25)*occ;
	lin += 0.25*fre*vec3(1.00,1.00,1.00)*occ;    
    lin *= 0.30;

    return lin;
}

vec3 render(vec3 ro, vec3 rd)
{     
	vec3 col = vec3(0.0);
	float atten = 1.0;

	for( int k=0; k<2; k++ )
	{
    	vec2 res = castRay(ro,rd);
    	float t = res.x;
		float m = res.y;

        if( m>-0.5 )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal( pos );   
            vec3 ref = reflect( rd, nor );

            vec4 material = colorForMat(m, pos, nor);

            float spe = 0.;
            vec3 lin = lighting(pos, nor, rd, ref, spe);

            vec3 c = material.rgb*lin.xyz;       
            c += 9.0*spe*vec3(1.00,0.90,0.70);

            col += c * atten;

            atten *= material.w;
            ro = pos + 0.001*nor;
            rd = ref;
        } else if (t > 1.) {
            col += skyColor * atten;
            break;
        }

		if (atten == 0.0) break;
    }
	return clamp(col,0.0,1.0);
}

mat3 setCamera(in vec3 ro, in vec3 ta, in float cr)
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

vec3 rayOrigin(in vec2 mo, in float time) {
    vec3 ro = vec3(18.*cos(0.2*time), 8.+mo.y*6.0, 18.*sin(0.2*time));
    return ro;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 mo = iMouse.xy/iResolution.xy;
	float time = 2.*iTime + 23.41;

    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif
        vec3 ro = rayOrigin(mo, time);
        vec3 ta = vec3( -0.5, -0.4, 0.5 );
        mat3 ca = setCamera( ro, ta, 0.0 );
        vec3 rd = ca * normalize( vec3(p.xy,2.0) );
        vec3 col = render(ro, rd);
        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif

    tot = pow(tot, vec3(0.45));
    fragColor = vec4(tot, 1.0);
}

float Circle(vec2 uv, vec2 p, float r, float blur) {
    float d = length(uv - p);
    float c = smoothstep(r, r - blur, d);

    return c;
}

// line from A to B
float segment(vec2 P, vec2 A, vec2 B, float thickness, float blur) {
    vec2 g = B - A;
    vec2 h = P - A;
    float d = length(h - g * clamp(dot(g, h) / dot(g,g), 0.0, 1.0));
	return smoothstep(thickness, blur*thickness, d);
}

#define PI 3.14159265359
#define TWO_PI 6.28318530718
float polygon(vec2 uv, vec2 coord, int n, float dim, float blur) {
    uv += coord;
    float a = atan(uv.x,uv.y)+PI;
    float r = TWO_PI/float(n);

    // Shaping function that modulate the distance
    float d = cos(floor(.5+a/r)*r-a)*length(uv);

    return 1.0 - smoothstep(dim, dim+blur, d);
}

float band(float t, float start, float end, float blur) {
	float step1 = smoothstep(start-blur, start + blur, t);
    float step2 = smoothstep(end + blur, end - blur, t);
    return step1*step2;
}

float rect(vec2 uv, float left, float right, float bottom, float top, float blur){
	float band1 = band(uv.x, left, right, blur);
    float band2 = band(uv.y, bottom, top, blur);

    return band1*band2;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= .5; // -0.5 <--> 0.5
    uv.x *= iResolution.x/iResolution.y;

    float t = iTime;

    float music = texture(iChannel0, vec2(.0, .0)).x;


    // head
    float mask = Circle(uv, vec2(0., -.15), .15, .005);

    // eyes
    mask -= Circle(uv, vec2(-.04, -.1), .03*music, .003);
    mask -= Circle(uv, vec2(.04, -.1), .03*music, .003);

    //ears
    mask += polygon(uv, vec2(.05, .015), 3, .025, .004);
    mask += polygon(uv, vec2(-.05, .015), 3, .025, .004);

    //nose
    mask -= segment(uv, vec2(-.02, -.15), vec2(.02, -.18), .004, .5);
    mask -= segment(uv, vec2(.02, -.15), vec2(-.02, -.18), .004, .5);

    // tale
    vec2 tale_uv = uv;
    tale_uv.x += sin(t+40.*tale_uv.y)*(.015);
    mask += segment(tale_uv, vec2(0., -.01), vec2(0., .27), .015, .7); 
    //mask += rect(tale_uv, -0.015, 0.015, -0.01, 0.27, 0.003);

    // end of tale
    vec2 eot_uv = uv;
    eot_uv.x += sin(t+150.)*.015;
    mask += Circle(eot_uv, vec2(0., .27), .03, .004);

    //body
    mask += segment(uv, vec2(-0.04, -0.3), vec2(0.04, -0.3), 0.04, 0.9);


    //legs
    mask += segment(uv, vec2(-0.069, -0.2), vec2(-0.065, -0.36), 0.013, 0.9);
    mask += segment(uv, vec2(0.069, -0.2), vec2(0.065, -0.36), 0.013, 0.9);


    // Output to screen
    fragColor = vec4(vec3(1. - mask),1.0);
}

