#define TAU			PI*2.

void mainImage(out vec4 c_out, in vec2 f)
{
    t  = iTime*.125;
    vec3	col = vec3(0., 0., 0.);
	vec2 R = iResolution.xy,
          uv  = vec2(f-R/2.) / R.y;
	vec3	dir = camera(uv);
    vec3	pos = vec3(.0, .0, 0.0);

    h*=0.;
    vec2	inter = (march(pos, dir));
    if (inter.y >= FAR)
        ret_col = vec3(.90, .82, .70);
    col.xyz = ret_col*(1.-inter.x*.005);
    col += h*.005;
    c_out =  vec4(col,1.0);
}

