float	scene(vec3 p)
{  
    p.z += 0.;
    float	var;
    float	mind = 1e5;
    float	cage = 1e5;
    rotate(p.xz, 1.57-.35*iTime );
    rotate(p.yz, 1.57);
    vec3 op = p;
    var  = atan(op.x,op.y);
    var /= 6.28;
    var += .5;

    p = op;
    vec3 pp = p;
    vec2 q = vec2(length(pp.xy)-20., pp.z);
    q = vec2(length(pp.xy)-40., pp.z);
    rotate(q, iTime*1. + var*2.*6.28+2.*sin(iTime*.25+var*6.28*1.));
    q.xy = modA(q.xy, 16.);
    q.x -= 10.;
    q.x = abs(q.x)-8.;q.x = abs(q.x)-1.;q.x = abs(q.x)-1.;q.x = abs(q.x)-1.;q.x = abs(q.x)-1.;

    mind = length(q)-0.-var*.25*20.0+1.1
        -step(var, .11)*3.*(1.65-var*15.)

        ;
    pp.y -= 20.;
    rotate(pp.yz, iTime*-1. +1.05 + -1.0*var*2.*6.28 );
    pp.yz = modA(pp.yz, 3.);
    pp.y -=5.;
    ret_col = vec3(.90, .82, .70);
    h -= vec3(-1.50,.21250,0.25)*vec3(1.)*.0125/(.0051+.1*mind*mind);
    h -= vec3(2.7505,-.2505,1.)*vec3(1.)*.0125/(.0125+mind*mind);
    return (mind);
}

vec2	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0, 0.0);
    vec3	p = vec3(0.0, 0.0, 0.0);
    vec2	s = vec2(0.0, 0.0);

	    for (float i = -1.; i < I_MAX; ++i)
	    {
	    	p = pos + dir * dist.y;
	        dist.x = scene(p);
	        dist.y += dist.x*.5;
	        if (dist.x < E || dist.y > FAR)
            {
                break;
            }
	        s.x++;
    }
    s.y = dist.y;
    return (s);
}

// Utilities

float	mylength(vec2 p)
{
	float	ret;

    ret = max( abs(p.x)+.5*abs(p.y), abs(p.y)+.5*abs(p.x) );

    return ret;
}

float	mylength(vec3 p)
{
	float	ret;

    ret = max( abs(p.x)+.5*abs(p.y), abs(p.y)+.5*abs(p.x) );
    ret = max(abs(p.z)+.5*abs(p.x), ret);
    return ret;
}

void rotate(inout vec2 v, float angle)
{
	v = vec2(cos(angle)*v.x+sin(angle)*v.y,-sin(angle)*v.x+cos(angle)*v.y);
}

vec2	rot(vec2 p, vec2 ang)
{
	float	c = cos(ang.x);
    float	s = sin(ang.y);
    mat2	m = mat2(c, -s, s, c);

    return (p * m);
}

vec3	camera(vec2 uv)
{
    float		fov = 1.;
	vec3		forw  = vec3(0.0, 0.0, -1.0);
	vec3    	right = vec3(1.0, 0.0, 0.0);
	vec3    	up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x) * right + (uv.y) * up + fov * forw));
}

vec3 calcNormal( in vec3 pos, float e, vec3 dir)
{
    vec3 eps = vec3(e,0.0,0.0);

    return normalize(vec3(
           march(pos+eps.xyy, dir).y - march(pos-eps.xyy, dir).y,
           march(pos+eps.yxy, dir).y - march(pos-eps.yxy, dir).y,
           march(pos+eps.yyx, dir).y - march(pos-eps.yyx, dir).y ));
}

