float Fbm1 (float p);
float Fbm2 (vec2 p);

mat3 flMat;
vec3 trkAx, trkAy, trkFx, trkFy, flPos, ltDir, cId, obRnd, obDisp;
vec2 obRotCs;
float bGrid, tCur, dstFar, spd, obOcc, szFac;
int idObj;
const float pi = 3.14159;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

vec3 TrackPath (float t)
{
  return vec3 (dot (trkAx, cos (trkFx * t)), dot (trkAy, cos (trkFy * t)), t);
}

vec3 TrackVel (float t)
{
  return vec3 (- dot (trkFx * trkAx, sin (trkFx * t)),
     - dot (trkFy * trkAy, sin (trkFy * t)), 1.);
}

vec3 TrackAcc (float t)
{
  return vec3 (- dot (trkFx * trkFx * trkAx, cos (trkFx * t)),
     - dot (trkFy * trkFy * trkAy, cos (trkFy * t)), 0.);
}

void ObjState ()
{
  obRnd = Hashv3v3 (cId);
  obDisp = vec3 (0.6, 0.6, 0.2) * bGrid * (obRnd - 0.5);
  obRotCs = cos (0.2 * pi * (obRnd.z - 0.5) * sin (1.5 * pi * (obRnd.y - 0.5) * tCur) +
     vec2 (0., 0.5 * pi));
}

float ObjDf (vec3 p)
{
  vec3 q, b;
  float dMin, d, h;
  dMin = dstFar / szFac;
  p = (p - obDisp) / szFac;
  p = vec3 (Rot2Cs (p.xy, obRotCs.xy), - p.z);
  b = vec3 (1.3, 0.9, 3.);
  q = p;
  b.x -= 0.2 * abs (q.y + 0.02 * q.z + 0.1);
  h = ((q.z > 0.5) ? 0.21 : 0.13) * abs (q.z - 0.5);
  q.y -= - h;
  b.yz -= vec2 (h, 0.1 * abs (q.y - 0.3));
  d = max (PrBoxDf (q, b), - PrBoxDf (p - vec3 (0., 0.1, -2.), vec3 (1.15, 0.5, 0.9)));
  q = p;
  q.xz = abs (q.xz) - vec2 (1.1, 2.);
  h = 0.15 * abs (q.z);
  q.y += 0.8 + h;
  d = max (d, - PrBoxDf (q, vec3 (0.15, 0.4, 0.6) - vec3 (0., h, 0.6 * q.y)));
  DMIN (1);
  q = vec3 (p.x, p.y + 0.95, abs (p.z) - 2.);
  d = PrCylDf (q.yzx, 0.08, 1.4);
  DMIN (2);
  q = p;
  q.y -= -0.88;
  d = PrBoxDf (q, vec3 (1.3, 0.03, 1.4));
  DMIN (3);
  q = vec3 (abs (p.xz) - vec2 (1.18, 2.), p.y + 0.95).xzy;
  d = PrRoundCylDf (q.yzx, 0.48, 0.07, 0.2);
  DMIN (4);
  return 0.7 * szFac * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 cIdP, p, s, rdi;
  float dHit, d, eps;
  eps = 0.0005;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  rdi = 1. / rd;
  cIdP = vec3 (-999.);
  dHit = eps;
  for (int j = 0; j < 120; j ++) {
    p = ro + dHit * rd;
    cId = floor (p / bGrid);
    if (cId != cIdP) {
      ObjState ();
      cIdP = cId;
    }
    s = (bGrid * (cId + step (0., rd)) - p) * rdi;
    d = abs (Minv3 (s)) + eps;
    if (obRnd.x < obOcc && length (cId.xy - floor (TrackPath (bGrid * cId.z).xy / bGrid)) >= 1.)
       d = min (d, ObjDf (p - (bGrid * (cId + 0.5))));
    dHit += d;
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec4 ObjCol (vec3 p)
{
  vec4 col4;
  float z, r, s;
  p = (p - obDisp) / szFac;
  p = vec3 (Rot2Cs (p.xy, obRotCs.xy), - p.z);
  col4 = vec4 (0.7, 0.1, 0.1, 0.4);
  if (idObj == 1) {
    z = abs (p.z - 0.5);
    if (p.y > 0.15 && abs (z - 0.78) < 0.72 && (abs (p.x) < 1.2 - 0.2 * p.y ||
       p.y < 0.85 - 2. * ((p.z > 0.5) ? 0.21 : 0.13) * z)) col4 = vec4 (0., 0., 0., -2.);
    else if (p.z < - 2.98 && abs (p.y + 0.1) < 0.05 && abs (p.x) < 1.25)
       col4 = vec4 (1., 0., 0., -1.);
    else if (p.z > 2.98 && abs (p.y + 0.2) < 0.05 && abs (p.x) < 1.25)
       col4 = vec4 (1., 1., 0.8, -1.);
    else if (p.y < - 0.75 || abs (p.y) < 0.85 && z < 0.008 || 
       abs (p.y + 0.09) < 0.31 && abs (p.z - 2.01) < 0.008 ||
       abs (p.y + 0.15) < 0.61 && abs (p.z + 1.01) < 0.008) col4.rgb *= 0.6;
    else if (p.z > 2.8 && abs (p.y + 0.5) < 0.15 && abs (p.x) < 0.8)
       col4.rgb *= 1. - 0.3 * step (0.2, cos (8. * pi * p.x));
    else if (p.y > -0.5 && abs (p.x) < 1.1 && abs (p.z + 2.) < 0.85)
       col4.rgb *= 0.9 + 0.1 * cos (16. * pi * p.x);
  } else if (idObj == 2) {
    col4 = vec4 (0.3, 0.3, 0.4, 0.1);
  } else if (idObj == 3) {
    col4.rgb *= 0.4;
  } else if (idObj == 4) {
    s = sign (p.z);
    p = vec3 (abs (p.xz) - vec2 (1.18, 2.), p.y + 0.95).xzy;
    r = length (p.yz);
    if (r < 0.2) {
      p.yz = Rot2D (p.yz, - pi * tCur * s);
      if (abs (abs (p.y) - abs (p.z)) < 0.1) col4 = vec4 (0.9, 0.9, 1., 0.3);
    } else if (r > 0.3) col4 = vec4 (0.15, 0.15, 0.15, 0.) * (0.7 + 0.3 * step (0., cos (32. * pi * p.x)));
  }
  return col4;
}

vec3 StarPat (vec3 rd, float scl)
{
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 131.13 * scl * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 171.13 * scl * q.yx));
  return 4. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 ErCol (vec3 rd)
{
  vec3 erDir, col, vn;
  float erRad, bs, ts;
  erDir = normalize (vec3 (0.02, -0.04, 1.));
  erRad = 0.025;
  col = vec3 (0.);
  bs = dot (rd, erDir);
  ts = bs * bs - 1. + erRad * erRad;
  if (ts > 0.) {
    ts = bs - sqrt (ts);
    if (ts > 0.) {
      vn = normalize ((ts * rd - erDir) / erRad);
      col = mix (vec3 (0.3, 0.4, 0.8), vec3 (1., 1., 0.95),
         smoothstep (0.2, 0.8, Fbm2 (6. * vn.xy + 7.1))) * (0.5 + 0.5 * max (- dot (vn, rd), 0.));
    }
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  float dstObj, nDotL;
  bool isBg, isRefl;
  isRefl = false;
  isBg = false;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    cId = floor (ro / bGrid);
    ObjState ();
    ro -= bGrid * (cId + 0.5);
    vn = ObjNf (ro);
    vn.xy = Rot2Cs (vn.xy, vec2 (obRotCs.x, - obRotCs.y));
    col4 = ObjCol (ro);
    col4.rgb *= 1. - 0.7 * smoothstep (0.6, 1., dstObj / dstFar);
    if (col4.a >= 0.) {
      nDotL = max (dot (vn, ltDir), 0.);
      col = col4.rgb * (0.2 + 0.8 * nDotL * nDotL) +
         col4.a * pow (max (dot (reflect (rd, vn), ltDir), 0.), 16.);
      if (idObj == 1) col += 0.2 * StarPat (reflect (rd, vn), 16.);
    } else if (col4.a == -1.) {
      col = col4.rgb * (0.2 - 0.8 * dot (rd, vn));
    } else if (col4.a == -2.) {
      rd = reflect (rd, vn);
      isRefl = true;
      isBg = true;
    }
  } else isBg = true;
  if (isBg) {
    col = ErCol (rd);
    if (length (col) < 0.03) col += StarPat (rd, 16.);
    if (isRefl) col *= 0.8;
  }
  return clamp (col, 0., 1.);
}

void FlyerPM (float s, float rl, float vu)
{
  vec3 vel, ort, ca, sa;
  float el, az;
  flPos = TrackPath (s);
  vel = normalize (TrackVel (s));
  el = - asin (vel.y);
  az = atan (vel.z, vel.x) - 0.5 * pi;
  rl = clamp (rl - 3. * TrackAcc (s).x, -0.4 * pi, 0.4 * pi);
  ort = (vu >= 0.) ? vec3 (el, az, rl) : vec3 (- el, az + pi, - rl);
  ca = cos (ort);
  sa = sin (ort);
  flMat = mat3 (ca.z, - sa.z, 0., sa.z, ca.z, 0., 0., 0., 1.) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x) *
          mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  spd = 1.;
  obOcc = 0.5;
  szFac = 0.12;
  tCur = mod (tCur, 1800.) + 11.1;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.2 * pi * mPtr.y;
  } else {
    az += 0.3 * pi * SmoothBump (0.2, 0.7, 0.15, mod (0.03* tCur, 1.)) * (2. * mod (floor (0.03 * tCur), 2.) - 1.);
  }
  vuMat = StdVuMat (el, az);
  bGrid = 1.;
  trkAx = 0.5 * vec3 (0.7, 0.8, 1.2);
  trkAy = 0.5 * vec3 (1., 1.1, 0.9);
  trkFx = vec3 (0.43, 0.33, 0.23);
  trkFy = vec3 (0.41, 0.31, 0.12);
  FlyerPM (spd * tCur, 0.1 * pi * (Fbm1 (0.1 * tCur) - 0.5), 1.);
  ro = flPos;
  zmFac = 5.;
  dstFar = 50.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    rd = vuMat * (rd * flMat);
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (pow (clamp (col, 0., 1.), vec3 (0.6)), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  float dxy, dz;
  dxy = length (p.xy) - r;
  dz = abs (p.z) - h;
  return min (min (max (dxy + rt, dz), max (dxy, dz + rt)), length (vec2 (dxy, dz) + rt) - rt);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

vec3 Hashv3v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  vec2 e = vec2 (1., 0.);
  return fract (sin (vec3 (dot (p + e.yyy, cHashVA3), dot (p + e.xyy, cHashVA3),
     dot (p + e.yxy, cHashVA3))) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float capsule(vec3 p, vec3 a, vec3 b, float r) {
    vec3 ap = p - a;
    vec3 ab = b - a;

    float t = dot(ap, ab)/dot(ab, ab);
    vec3 c = a + t*ab;

    return length(c-p) - r;
}

float plane(vec3 p, vec4 n) {
    n = normalize(n);
    return dot(p, n.xyz) + n.w;
}

float pmod1(float a, float m) {
    return mod(a-m/2., m) - m/2.;
}

float dF(vec3 p) {
    float xaxis = capsule(p, vec3(0), vec3(1., 0, 0), 1.);
    float yaxis = capsule(p, vec3(0), vec3(0, 1., 0), 1.);
    float zaxis = capsule(p, vec3(0), vec3(0, 0, 1.), 1.);    
    float axis = min(xaxis, yaxis);
    axis = min(axis, zaxis);

    float xzp = abs(plane(p, vec4(0, 1, 0, 100)));
    float xyp = abs(plane(p, vec4(0, 0, 1, 100)));
    float yzp = abs(plane(p, vec4(1, 0, 0, 100)));
    float planes = min(xzp, xyp);
    planes = min(planes, yzp);

    float lines = 200.;
    if(abs(p.z) < 1.) {
        p.x = pmod1(p.x, 2.);
        p.y = pmod1(p.y, 2.);
        lines = min(lines, capsule(p, vec3(0), vec3(0, 1, 0), .2));
        lines = min(lines, capsule(p, vec3(0), vec3(1, 0, 0), .2));
    } else if(abs(p.x) < 1.) {
        p.y = pmod1(p.y, 2.);
        p.z = pmod1(p.z, 2.);
        lines = min(lines, capsule(p, vec3(0), vec3(0, 1, 0), .2));
        lines = min(lines, capsule(p, vec3(0), vec3(0, 0, 1), .2));
    } else if(abs(p.y) < 1.) {
        p.x = pmod1(p.x, 2.);
        p.z = pmod1(p.z, 2.);
        lines = min(lines, capsule(p, vec3(0), vec3(1, 0, 0), .2));
        lines = min(lines, capsule(p, vec3(0), vec3(0, 0, 1), .2));
    }


    float scene = min(planes, axis); 
    return min(scene, lines);
}

float rayMarch(vec3 ro, vec3 rd) {
    float d = 0.0;
    for(int i=0;i<100;i++) {
        float dd = dF(ro + d*rd);
        if(dd < 0.001 || d > 101.) break;
        d += dd;
    }
    return d;
}

mat2 rot(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, s, -s, c);
}


vec3 getNormal(vec3 p) {
    vec2 e = vec2(0.01, 0.0);
	return normalize(vec3(
    	dF(p+e.xyy) - dF(p-e.xyy),
        dF(p+e.yxy) - dF(p-e.yxy),
        dF(p+e.yyx) - dF(p-e.yyx)
    ));
}

float getDiffuse(vec3 p, vec3 lightPos) {
    vec3 lightDir = normalize(p-lightPos);
    vec3 n = getNormal(p);
    return max(0.8, dot(lightDir, n));
}

vec4 image(vec2 uv, vec2 resolution)
{
    vec3 col = vec3(0);
    float ar = resolution.x/resolution.y;
    uv.x *= ar;

    vec3 ro = vec3(30, 10, 30);
    vec3 la = vec3(0);

    float t = iTime*0.5;

    ro.xz *= rot(t*.5);
	t *= 2.;
    ro.y *= sin(t+sin(t+sin(t + 0.5)));
    vec3 f = normalize(la-ro);
    vec3 u = normalize(vec3(0, 1, 0));
    vec3 r = normalize(cross(f, u));

    float focal_length = ar/tan(55.0*3.14/180.);
    vec3 rd = normalize(focal_length*f + uv.x*r + uv.y*u);

    float d = rayMarch(ro, rd);

    if(d < 100.) {
        vec3 p = ro + d*rd;
        float diffuse = getDiffuse(p, vec3(10, 10, 10));
        col = mix(vec3(1)*diffuse, vec3(0), d/100.);

        if(p.x > 1.1 && abs(p.y) < 1.1 && abs(p.z) < 1.1) col *= vec3(1, 0, 0);
        else if(p.x < -1.1 && abs(p.y) < 1.1 && abs(p.z) < 1.1)col *= 0.5*vec3(1, 0, 0);


        if(p.y > 1.1 && abs(p.x) < 1.1 && abs(p.z) < 1.1) col *= vec3(0, 1, 0);
        else if(p.y < -1.1 && abs(p.x) < 1.1 && abs(p.z) < 1.1) col *= 0.5*vec3(0, 1, 0);

        if(p.z > 1.1 && abs(p.y) < 1.1 && abs(p.x) < 1.1) col *= vec3(0, 0, 1);
        else if(p.z < -1.1 && abs(p.y) < 1.1 && abs(p.x) < 1.1) col *= 0.5*vec3(0, 0, 1);
    }

    return vec4(col, 1.0);
}


