float Band(float t, float start, float end, float blur) {
    float step1 = smoothstep(start+blur, start-blur, t);
	float step2 = smoothstep(end-blur, end+blur, t);

    return step1*step2;
}

float Rect(vec2 uv, float horizontal, float vertical, float blur) {
	float band1 = Band(uv.x, -horizontal, horizontal, -blur);
    float band2 = Band(uv.y, -vertical, vertical, -blur);

	return band1*band2;
}

