#define pi 3.14159265


//func is a mode of vibration of a 2D plane with fixed boundary conditions
//Look up membrane modes of vibrations
float func(vec2 mn, vec2 uv){
  float mag = length(mn);
  return sin(iTime*mag)/mag*sin(mn.x*uv.x)*sin(mn.y*uv.y);
}
//I this case f is the addition of 16 modes of vibration on a 2D membrane.
float f(float x, float y){ //this defines the function that is graphed.
  float total = 0.;
  for (float m = 1.; m < 4.1; m += 1.){
    for (float n = 1.; n < 4.1; n += 1.){
      total += func(vec2(m,n),vec2(x,y));
    }
  }

  return total/3.;
}
