vec2 times(vec2 a, vec2 b){
  return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.x;
  vec3 v = normalize(vec3(uv,1));
  /*v is the ray for our current pixel. We must find a value "t"
    which when multiplied by v is the intersection of v and the
    func.*/
  float angle = pi/6.;
  v.zy = times(v.zy,vec2(cos(angle),-sin(angle)));
  vec3 planePos = 3.*vec3(0,-1,1);//This is the position of the origin relative to camera
  float a = 0., b = 0.;
  vec3 pa = a*v-planePos; //Our starting pos relative to func origin


  if (f(pa.x,pa.z) > pa.y){ //if our cam below func leave
    fragColor = vec4(0);
    return;
  }
  //step forward along v in coarse steps until we pass through the func
  for (float i = 3.;i < 20.;i += 0.5){
    vec3 pb = i*v-planePos;
    if (f(pb.x,pb.z) > pb.y){
      a = i-0.5; //Once we pass through, set a to slightly above func, and b below 
      b = i;
      break;
    }
  }
  if (b == 0.){ //if ray doesn't pass through the func
    fragColor = vec4(0);
    return;
  }
  float mid, diff; //midpoint of a and b and diff
  int count = 0;
  do { //Use bisections to find a better approximation for 
    mid = (a+b)/2.;
    vec3 pm = mid*v-planePos;
    diff = f(pm.x,pm.z) - pm.y;
    if (diff < 0.)
      a = mid;
    else
      b = mid;
    ++count;
  }while (abs(diff) > 0.001 && count < 10);
//mid is now adequately close to the func
  vec3 p = mid*v-planePos;

  const float dx = 0.05;
  //gradX is a small vector which starts at point p (a point on the func)
  //and ends at a point on the func when making a small step in the x dir
  vec3 gradX = vec3(dx,f(p.x+dx,p.z)-p.y,0.);
  vec3 gradZ = vec3(0.,f(p.x,p.z+dx)-p.y,dx);
  //A vector perp to gradX and gradZ is a normal to our funcs surface
  vec3 normal = normalize(cross(gradX,gradZ));
  //Calculate lighting given our pos, lightPos, normal and texture
  normal.y *= -1.;
  vec3 reflected = reflect(v,normal);
  vec3 toLight = normalize(vec3(0,1,0));
  vec3 tex = texture(iChannel0,p.xz/pi).rgb;
  float diffuse = dot(normal,toLight);
  float specular = pow(abs(dot(toLight,reflected)),8.);
  vec3 color = 0.6*vec3(specular)+diffuse*tex;
  fragColor = vec4(color, 1.0);
}

