// A bit of a hacked together procedural texture computing the closest distance to a hexagon grid using planes and voronoi regions
float hexTex(vec2 Tex)
{
    float degAlpha = 57.0;

    float alpha = (degAlpha) / 360.0 * 2.0 * 3.14159, cal = cos(alpha), sal = sin(alpha);
	float beta = ((90.0 - degAlpha) / 360.0 * 2.0 * 3.14159), cbe = cos(beta), sbe = sin(beta);
	float r = 0.5 / cbe;
	vec2 P1 = vec2(r*sbe, r*cbe);
	vec2 P2 = vec2(2.0 * r*cal, 0.0);
	vec2 N = normalize(P2 - P1);
	float D = dot(vec2(-N.y, N.x), P1);

	float v = 1.0 - 2.0 * abs(0.5 - mod(abs(Tex.y), 1.0));
	float v2 = 1.0 - 2.0 * abs(0.5 - mod(abs(Tex.x), 1.0));
	float d = (dot(vec2(-N.y, N.x), vec2(v2, v)) - D);
	float d2 = (dot(vec2(-N.y, -N.x), vec2(v2, v)));

	if (v2 <= r * cal)
	{
		if (d2 > 0.0)
			d = abs(d) / D;
		else if (v < 0.5)
			d = abs(0.5 - v) * 2.0;
		else if (d > 0.0)
			d = abs(d2) / D;
		else
			d = abs(0.5 - v) * 2.0;
	}
	else if (d <= 0.0)
	{
		d = abs(d) / D;
	}
	else
	{
		if (v2 >= 2.0 * r*cal)
		{
			if (v < 0.5)
			{
				vec2 P3 = vec2(0.25, 0.5);
				vec2 N2 = normalize(P3 - P2);
				float D2 = dot(vec2(-N2.y, N2.x), P2);
				float d3 = (dot(vec2(-N2.y, -N2.x), vec2(v2, v)) - D2);
				if (d3 < 0.0)
					d = 1.0 - abs(0.5 - v) * 2.0;
				else
					d = abs(d) / D;
			}
			else
			{
				float d4 = (dot(vec2(-N.y, N.x), vec2(v2, v)) - D * 2.0);
				if (d4 > 0.0)
					d = 1.0 - abs(0.5 - v) * 2.0;
				else
					d = abs(d2) / D;
			}
		}
		else if (v > 0.5)
			d = abs(dot(vec2(-N.y, -N.x), vec2(v2, v))) / D;
		else
			d = abs(d) / D;
	}

    return min(0.999, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // normalized coordinates (-1 to 1 vertically)
    vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

    // rotate
    float rotateSpeed = 0.4;
	p=vec2(p.x*cos(iTime*rotateSpeed)+p.y*sin(iTime*rotateSpeed),p.y*cos(iTime*rotateSpeed)-p.x*sin(iTime*rotateSpeed));

    // angle of each pixel to the center of the screen
    float a = atan(p.y,p.x);

    // squareish tunnel
    float r = pow( pow(p.x*p.x,4.0) + pow(p.y*p.y,4.0), 1.0/16.0 );

    // index texture by (animated inverse) radious and angle
    uv = vec2( 0.3/r, a/3.1415927 );

    // Texture computation
    float c = pow(1.0-hexTex(vec2(iTime,0)+uv.xy*vec2(6.0,3.0)),4.1+2.0*sin(iTime)); // eval texture and bend with pow

    // fade by time
    c = 0.1+0.9*c*abs(sin(iTime*2.0));

    // spiral
    c *= 0.2+0.8*pow(abs(sin(uv.x*10.0-iTime*10.0+uv.y*3.14159)),4.0);

    // fade by distance
    c *= min(max(1.0-uv.x,0.0),1.0);

    // Output to screen
    fragColor = vec4(0,c,c,1.0);
}

