#define S smoothstep

vec3 hsl2rgb( in vec3 c ){
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0,1.0);
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}
float N11(float n) {
	return fract(sin(n*939.19)*138.131);
}
vec3 pointLight(vec2 uv, float noise,float t, float ratio_) {
    vec2 ratio =  vec2(ratio_,1.);
    float r = .4+mix(-.1,.1,noise);
    float sRange = .3;
    float s = sin(t+noise*983.12)*.5+.5;
    float m = S(r,r-.05,length(uv*ratio)) * s *noise;
    float hue = mix(.5, .6, N11(noise));
    float b = mix(.4, .8, N11(N11(noise)));
    return m*hsl2rgb(vec3(hue,1.,b));;
}
float N21(vec2 n) {
	return N11(N11(n.x)+n.y);
}
float mask(vec2 uv, vec2 id, float zy) {
    float n = N11(id.x);
	return step(floor(mix(-.5,.5,n)*zy)/zy, uv.y);
}
vec3 layer(vec2 uv, float z_, float seed) {
    float ratio = 8.;
    vec2 z = vec2(z_, z_*ratio);
    vec2 uv_ = uv + vec2(seed, N11(seed));
    vec2 gv = fract(uv_*z)-.5;
    vec2 id = floor(uv_*z)-.5;
    return pointLight(gv, N21(id), iTime, ratio)*mask(uv, id, z.y);
}
