#define ITERS 100
#define TOL 1e-3
#define fdist 0.5
#define maxdist 10.
#define LEVELS 9
#define PI 3.1415926

float oscillate(float t_low, float t_high, float t_transition, float t_offset) {
    float t_osc = 0.5*(t_high+t_low)+t_transition;
    float h_l = 0.5*t_low/t_osc;
    float h_h = (0.5*t_low+t_transition)/t_osc;
    return smoothstep(0., 1., (clamp(abs(mod(iTime + t_offset, t_osc*2.)/t_osc-1.), h_l, h_h) - h_l) / (h_h - h_l));
}

vec4 map(in vec3 pos)   
{
    float t = oscillate(2., 2., 7., 0.) * PI * 0.5;
    float t2 = oscillate(7., 7., 2., 5.);
    if (iMouse.z > 1.0)
    {
    	t = iMouse.y/iResolution.y*PI*0.5;
    }
    float c = cos(t);
    float s = sin(t);
    mat3 rotZ = mat3(c, -s, 0, s, c, 0, 0, 0, 1);
    mat3 rotX = mat3(1, 0, 0, 0, c, -s, 0, s, c);
    float sc = 0.5;
    float truncdist = -1e6;
    for (int i=0; i<LEVELS; i++)
    {
        pos = abs(rotX * rotZ * pos) - sc;
        truncdist = max(truncdist, (pos.x+pos.y+pos.z)/sqrt(3.)-(2.-t2)*sc);
     	sc *= 0.5;
    }
    vec3 bx = max(vec3(0.), pos-sc*2.);
    float dist = max(truncdist, length(bx));
    return vec4(dist, step(pos.zxy, pos.xyz) * step(pos.yzx, pos.xyz));
}

vec3 getnormal(vec3 ro) {
    vec2 d = vec2(TOL, 0.0);
    float x1 = map(ro+d.xyy).x;
    float x2 = map(ro-d.xyy).x;
    float y1 = map(ro+d.yxy).x;
    float y2 = map(ro-d.yxy).x;
    float z1 = map(ro+d.yyx).x;
    float z2 = map(ro-d.yyx).x;
    return normalize(vec3(
        x1-x2,
        y1-y2,
        z1-z2));
}

vec4 march(in vec3 pos, in vec3 dir)
{
    float t = 0.;
    vec4 dist;
    int i=0;
    for (; i<ITERS; i++)
    {
        vec3 currpos = t*dir+pos;
    	dist = map(currpos);
        t += dist.x;
        if (abs(dist.x) < TOL)
        {
            return vec4(t, dist.yzw);
        } else if (t > maxdist) {
            break;
        }
    }
    return vec4(t, vec3(-1., t/maxdist*0.8, float(i)/float(ITERS)));
}

float shadowmarch(in vec3 pos, in vec3 dir) {
    float t = 0.;
    for (int i=0; i<50; i++) {
        vec3 currpos = t*dir+pos;
        float dist = map(currpos).x;
        if (dist <= 0.) return 0.;
        t += max(0.01, dist);
        if (t > maxdist) break;
    }
    return 1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ang = iTime * 0.3;
    if (iMouse.z > 1.0) {
        ang = iMouse.x/iResolution.x*PI*2.;
    }
    float c = cos(ang);
    float s = sin(ang);
    mat3 rot = mat3(c, -s, 0, s, c, 0, 0, 0, 1);
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.x;
    vec3 ro = rot * vec3(1.,1.,1.) * (2.+s*0.3);
    vec3 nw = -normalize(ro);
    vec3 up = vec3(0.,0.,1.);
    vec3 nu = cross(nw, up);
    vec3 nv = cross(nu, nw);
   	vec3 rd = normalize(nw*fdist + uv.x*nu + uv.y*nv);
    vec4 d = march(ro, rd);
    vec3 pos = d.x * rd + ro;
    vec3 n = getnormal(pos);
    vec3 col = vec3(0.);
    if (d.y > -0.5) {
        vec3 albedo = d.yzw + 0.75*d.wyz;
        vec3 lightdir = normalize(vec3(1., 0.5, 2.));
        float origfac = dot(n, lightdir);
        float fac = max(0., origfac);
        float shadowfac = shadowmarch(pos+lightdir*TOL, lightdir);

        float ambfac = abs(origfac);
        float occfac = clamp(map(pos + n * 0.25).x*4., 0., 1.);
        col = occfac*(fac * shadowfac + vec3(0.25) * ambfac) * albedo;
    } else {
        col = vec3(d.z, d.w, d.w);
    }
    fragColor = vec4(pow(col, vec3(0.45)),1.0);
}

// Alien engine
// Based upon: https://www.shadertoy.com/view/4ds3zn

#define PI         3.141592654
#define TAU        (2.0*PI) 
#define TOLERANCE  0.0003
#define REPS       11
#define MAX_DIST   20.
#define MAX_ITER   120

const vec3  green  = vec3(1.5, 2.0, 1.0);
const vec3  dark   = vec3(0.2);

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(c*p.x + s*p.y, -s*p.x + c*p.y);
}

void r45(inout vec2 p) {
	p = (p + vec2(p.y, -p.x))*sqrt(0.5);
}

float apollian(vec3 p, float tolerance, out int layer) {
  const float s = 1.9;
  float scale = 1.0;

  float r = 0.2;
  vec3 o = vec3(0.22, 0.0, 0.0);

  float d = 0.0;

  for(int i = 0; i < REPS; ++i) {
    p = (-1.00 + 2.0*fract(0.5*p+0.5));
//    rot(p.xz, -float(i)*PI/4.0);
    r45(p.xz);

    float r2 = dot(p,p) + 0.0;
    float k = s/r2;
    float ss = pow((1.0 + float(i)), -0.15);
    p *= pow(k, ss);
    scale *= pow(k, -ss*ss);
    d = 0.25*abs(p.y)*scale;
    layer = i;
    if(abs(d) < tolerance) break;
  }

  return d;
}

float df(vec3 p, float tolerance, out int layer) { 
  float d = apollian(p, tolerance, layer);
  return d;
} 


float intersect(vec3 ro, vec3 rd, out int iter, out int layer) {
  float res;
  float t = 1.6;
  iter = MAX_ITER;

  for(int i = 0; i < MAX_ITER; ++i) {
    vec3 p = ro + rd * t;
    float tolerance = TOLERANCE * t;
    res = df(p, tolerance, layer);
    if(res < tolerance || res > MAX_DIST) {
      iter = i;
      break;
    }
    t += res;
  }

  if(res > MAX_DIST) t = -1.;

  return t;
}

float ambientOcclusion(vec3 p, vec3 n) {
  float stepSize = 0.012;
  float t = stepSize;

  float oc = 0.0;

  int layer;

  for(int i = 0; i < 12; i++) {
    float tolerance = TOLERANCE * t;
    float d = df(p + n * t, tolerance, layer);
    oc += t - d;
    t += stepSize;
  }

  return clamp(oc, 0.0, 1.0);
}

vec3 normal(in vec3 pos) {
  vec3 eps = vec3(.001,0.0,0.0);
  vec3 nor;
  int layer;
  float tolerance = TOLERANCE * eps.x;
  nor.x = df(pos+eps.xyy, tolerance, layer) - df(pos-eps.xyy, tolerance, layer);
  nor.y = df(pos+eps.yxy, tolerance, layer) - df(pos-eps.yxy, tolerance, layer);
  nor.z = df(pos+eps.yyx, tolerance, layer) - df(pos-eps.yyx, tolerance, layer);
  return normalize(nor);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )  { 
  vec2 q=fragCoord.xy/iResolution.xy; 
  vec2 uv = -1.0 + 2.0*q; 
  uv.x*=iResolution.x/iResolution.y; 

  vec3 la = vec3(0.0,0.5,0.0); 
  vec3 ro = vec3(2.5, 1.5, 0.0);
  rot(ro.xz, iTime/40.0);

  vec3 cf = normalize(la-ro); 
  vec3 cs = normalize(cross(cf,vec3(0.0,1.0,0.0))); 
  vec3 cu = normalize(cross(cs,cf)); 
  vec3 rd = normalize(uv.x*cs + uv.y*cu + 3.0*cf);

  vec3 bg = mix(dark*0.25, dark*0.5, smoothstep(-1.0, 1.0, uv.y));
  vec3 col = bg;

  vec3 p=ro; 

  int iter = 0;
  int layer = 0;

  float t = intersect(ro, rd, iter, layer);

  if(t > -1.0) {
    p = ro + t * rd;
    vec3 n = normal(p);
    float fake = float(iter)/float(MAX_ITER);
    float fakeAmb = exp(-fake*fake*4.0);
    float amb = ambientOcclusion(p, n);


    vec3 dif;

    float ll = length(p);

    if (layer == 0)
    {
      dif = 0.75*green;
    } else {
      dif = green*pow((1.0 + 0.5*cos(-PI*2.0*float(layer)/float(REPS) + iTime*0.25 - 0.5*PI*ll)), 4.0)/pow(float(layer), 1.5);
    }


    const float fogPeriod = TAU*2.0;
    float fogHeight = 0.25 + 0.325*(abs(p.y) + 0.125*(sin(fogPeriod*p.x) * cos(fogPeriod*p.z)));
    float dfog = (fogHeight - ro.y)/rd.y;
    float fogDepth = t > dfog && dfog > 0.0 ? t - dfog : 0.0;
    float fogFactor = exp(-fogDepth*4.0);

    col = dif;
    col *= vec3(mix(1.0, 0.125, pow(amb, 3.0)))*vec3(fakeAmb);
    col = mix(green*0.5, col, fogFactor); 
    col = mix(bg, col, exp(-0.0125*t*t)); 
  } 

  float pp = 1.0 - (1.0 - step(0.5, q.y))*smoothstep(0.85, 1.3, length(2.0*q-1.0));


  col *= pp;


  fragColor=vec4(col.x,col.y,col.z,1.0); 
}

