#define hash1( n ) fract(sin(n)*43758.5453)
#define PI 3.1415926
#define BORDER1 0.025
#define BORDER2 0.005


void mainImage( out vec4 fragColor, in vec2 g)
{
    vec2 r = iResolution.xy
    	,st = (g+g-r)/r.y;
    float a = atan(st.x,st.y)/PI
    	,sm = fwidth(a)*1.5
    	,iT = iTime
    	,c1 = (a*1.5+2.5)
    	,c2 = (a*1.5+2.);
    c2 += (c2 < 1.) ? 3. : 0.;
    float l = dot(abs(st),normalize(vec2(1.,1.73)));
    l = log2(max(l,abs(st.x)))-iT;
    vec2 c_gr = (mod(l,2.)>1.) ? vec2(c1,c2) : vec2(c2,c1) ;
    float sm2 = fwidth(l);

    float i = step(.5,hash1(floor(l)*floor(c_gr.x)));
    float c = i > .5 
        	? 0. 
        	:	smoothstep (0.,sm,abs(.5-fract(c_gr.y))-BORDER2)
    			* smoothstep (sm2, -sm2, abs(.5-fract(l))-.5+BORDER1)
    			* smoothstep(-8.,-1.,floor(l)+iT);

    fragColor = vec4(vec3(c),1.);
}

vec3 HSV_to_RGB(vec3 hsv) {

  float C = hsv.z * hsv.y;
  float X = C * (1. - abs( mod( (hsv.x * 6.),  2.) - 1.) );
  float m = hsv.z - C;

  vec3 rgb;

  switch (int(hsv.x * 6.)) {
      case 0: rgb = vec3(C,X,0); break;
      case 1: rgb = vec3(X,C,0); break;
      case 2: rgb = vec3(0,C,X); break;
      case 3: rgb = vec3(0,X,C); break;
      case 4: rgb = vec3(X,0,C); break;
      case 5: rgb = vec3(C,0,X); break;
  }

  rgb += m;

  return rgb;
}

vec3 plasma(vec2 uv) {
  // plasma
  vec2 uvc = uv - 0.5;
  uvc.x += cos(iTime) / 2.;
  uvc.y += sin(iTime) / 2.;
  vec3 hsv = vec3 (

    (cos(length(uvc*3.) * cos(uv.x*2. + iTime / 1.8) + iTime / 1.7) + 1.) / 2.1,
    1.,
    (sin(length(uvc*2.) * cos(uv.x*5. + iTime / 1.5) + iTime / 1.2) + 1.) / 2.

  );

  return hsv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  // Normalized pixel coordinates (from 0 to 1)
  vec2 uv = fragCoord/iResolution.xy;

  // discretise coordinates to make it all pixelised
  uv.x = floor(uv.x * 320.) / 320.;
  uv.y = floor(uv.y * 200.) / 200.;

  vec3 hsv = plasma(uv);

  // discretise HSV color because who need 16.777.216 colors ?
  hsv.x = floor(hsv.x * 24.) / 24.;
  hsv.y = floor(hsv.y * 16. + 16.) / 32.;
  hsv.z = floor(hsv.z * 24. + 8.) / 32.;

  fragColor = vec4(HSV_to_RGB(hsv), 1.);
}

