#define AA  1   // optional antialiasing

float PrSphDf (vec3 p, float r);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float SmoothBump (float lo, float hi, float w, float x);
float Fbm2 (vec2 p);

vec4 hxHit;
vec3 ltPos;
float tCur, dstFar, aMin, dLoop, aLoop, hPlat, rCyl;
int idObj;
const float nSeg = 5.;
const float pi = 3.14159, sqrt3 = 1.73205;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec3 q, pp;
  vec2 b, c;
  float dMin, d, dn, lb, dr, a, bRad, bHt, rSph;
  dMin = dstFar;
  bRad = 14.;
  bHt = 0.4;
  rSph = 1.1 * rCyl;
  p.y -= rSph + bHt;
  pp = p;
  a = (length (p.xz) > 0.) ? atan (p.z, - p.x) / (2. * pi) : 0.;
  p.xz = Rot2D (p.xz, 2. * pi * (floor (5. * a + 0.5) / 5.));
  p.x += bRad;
  p.xy = Rot2D (p.xy, - aLoop);
  q = p;
  d = PrSphDf (q, rSph);
  DMIN (2);
  for (float k = 0.; k < nSeg; k ++) {
    q = p;
    q.xy = Rot2D (q.xy, 0.5 * pi);
    q.xy = Rot2D (vec2 (q.x, q.y - dLoop), aLoop - 0.5 * pi);
    b = vec2 (length (q.xy) - dLoop, q.z);
    lb = length (b);
    c = atan (vec2 (q.y, b.x), vec2 (- q.x, b.y)) * vec2 (4. * dLoop / pi, 1.) / pi;
    dr = SmoothBump (0.15, 0.85, 0.1, mod (4. * c.x +
       3. * c.y * (mod (float (k), 2.) - 0.5), 1.)) - 0.5;
    dn = max (min (abs (lb - 0.9 * rCyl) + 0.1 * dr * rCyl, lb - 0.6 * rCyl),
       dot (vec2 (q.x, abs (q.y)), sin (aLoop + vec2 (0., 0.5 * pi))));
    if (dn < d) {
      d = dn;
      hxHit = vec4 (c, lb, k);
    }
    p.xy = Rot2D (q.xy, aLoop) + vec2 (dLoop, 0.);
    p.x = - p.x;
  }
  DMIN (1);
  q = p;
  d = PrSphDf (q, rSph);
  DMIN (2);
  q = pp;
  q.y = abs (q.y - 0.5 * hPlat) - (0.5 * hPlat + rSph + bHt);
  d = PrRoundCylDf (q.xzy, bRad + 4., 0.2, bHt);
  DMIN (3);
  return 0.8 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd, float dMax)
{
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = 0; j < 40; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += clamp (2. * h, 0.1, 3.);
    if (sh < 0.05 || d > dMax) break;
  }
  return 0.6 + 0.4 * sh;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float HexEdgeDist (vec2 p)
{
  p = abs (p);
  return (sqrt3/2.) - p.x + 0.5 * min (p.x - sqrt3 * p.y, 0.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 ltDir, vn, col;
  vec2 p, ip;
  float dstObj, dstGrnd, tCyc, sLoop, vDotL, sh, att, c;
  tCyc = 10.;
  aMin = 0.45;
  sLoop = aMin + 20. * pow (1. - SmoothBump (0.25, 0.75, 0.24, mod (tCur / tCyc, 1.)), 4.);
  aLoop = 0.25 * pi / sLoop;
  dLoop = 6. * 0.25 * pi * sLoop;
  hPlat = 2. * nSeg * dLoop * sin (aLoop);
  rCyl = 2.;
  dstObj = ObjRay (ro, rd);
  dstGrnd = (rd.y < 0.) ? - ro.y / rd.y : dstFar;
  if (min (dstObj, dstGrnd) < dstFar) {
    ltDir = normalize (ltPos);
    if (dstObj < dstGrnd) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      vDotL = max (dot (vn, ltDir), 0.);
      if (idObj == 1) {
        if (hxHit.z < 0.75 * rCyl) {
          hxHit.x -= 1.5 * hxHit.y * (mod (hxHit.w, 2.) - 0.5);
          p = hxHit.xy * vec2 (2. * sqrt3, 3.);
          ip = PixToHex (p);
          c = mod (dot (mod (2. * ip + ip.yx, 3.), vec2 (1., 2.)), 3.);
          col4 = (c == 0.) ? vec4 (0.7, 0.6, 0., 0.2) : ((c == 1.) ? vec4 (0.8, 0.8, 0.4, 0.2) :
             vec4 (0.4, 0.2, 0., 0.2));
          col4 *= 0.3 + 0.7 * smoothstep (0.05, 0.07, HexEdgeDist (p - HexToPix (ip)));
        } else col4 = vec4 (0.8, 0.8, 0.9, 0.2);
        vDotL *= vDotL;
      } else if (idObj == 2) {
        col4 = vec4 (0.8, 0.8, 0.9, 0.2);
        vDotL *= vDotL;
      } else if (idObj == 3) {
        col4 = vec4 (0.8, 0.85, 0.8, 0.2);
      }
    } else {
      ro += dstGrnd * rd;
      vn = vec3 (0., 1., 0.);
      col4 = vec4 (0.43, 0.4, 0.4, 0.1) * (0.8 + 0.2 * Fbm2 (0.5 * ro.xz));
      vDotL = max (dot (vn, ltDir), 0.);
    }
    att = smoothstep (0.96, 0.985, dot (normalize (ltPos - ro), ltDir));
    sh = ObjSShadow (ro, ltDir, length (ltPos - ro));
    col = att * (col4.rgb * (0.2 + 0.8 * sh * vDotL) +
       0.2 * step (0.95, sh) * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.));
  } else {
    col = vec3 (0.);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, ltEl, ltAz, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  el = -0.05 * pi;
  az = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += 0.6 * pi * sin (0.01 * 2. * pi * tCur);
  }
  el = clamp (el, -0.2 * pi, 0.02 * pi);
  zmFac = 2.4;
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 20. /*3. * pi * nSeg * aMin * sin (0.25 * pi / aMin)*/, -70.);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  dstFar = 300.;
  ltEl = -0.3 * pi * (1. + 0.15 * sin (0.1 * 2. * pi * tCur));
  ltAz = pi + 0.2 * pi * cos (0.125 * 2. * pi * tCur);
  ltPos = vec3 (0., 0., 200.);
  ltPos.yz = Rot2D (ltPos.yz, ltEl);
  ltPos.xz = Rot2D (ltPos.xz, ltAz);
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  float dxy, dz;
  dxy = length (p.xy) - r;
  dz = abs (p.z) - h;
  return min (min (max (dxy + rt, dz), max (dxy, dz + rt)), length (vec2 (dxy, dz) + rt) - rt);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

