float hash31(vec3 p)
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

float noise3d( in vec3 x )
{
    vec3 i = floor(x);
    vec3 f = fract(x);
	f = f*f*f*(6.0*f*f - 15.0*f + 10.0);
    return mix(mix(mix( hash31(i+vec3(0,0,0)), 
                        hash31(i+vec3(1,0,0)),f.x),
                   mix( hash31(i+vec3(0,1,0)), 
                        hash31(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash31(i+vec3(0,0,1)), 
                        hash31(i+vec3(1,0,1)),f.x),
                   mix( hash31(i+vec3(0,1,1)), 
                        hash31(i+vec3(1,1,1)),f.x),f.y),f.z);
}

float marble(vec3 p){
    float m = 0.0;
    float f = 1.0;
    for(float i = 0.0; i < 3.0; i += 1.0){
        m += noise3d(p*f)/f;
        f*=2.17;
    }
    return m;
}

//æ ¹æ®må¨colors[1]å°colors[n-1]ä¹é´è¿è¡æ ·æ¡çº¿æå¼
vec3 marbleColor(float m){
    m = clamp(0.0,1.0,m);
    float nspan = 10.0;
    vec3 colors[13];
    colors[0]=PALE_BLUE;
    colors[1]=PALE_BLUE;
    colors[2]=MEDIUM_BLUE;
    colors[3]=MEDIUM_BLUE;
    colors[4]=MEDIUM_BLUE;
    colors[5]=PALE_BLUE;
    colors[6]=PALE_BLUE;
    colors[7]=DARK_BLUE;
    colors[8]=DARK_BLUE;
    colors[9]=DARKER_BLUE;
    colors[10]=DARKER_BLUE;
    colors[11]=PALE_BLUE;
    colors[12]=DARKER_BLUE;
    colors[12]=DARKER_BLUE;

    m = clamp(0.0,1.0,m)*nspan;
    float span = ceil(m);
    if(span>=nspan){
        span = nspan - 1.0;
    }
    m -= span;
    int p0 = int(span);
    int p1 = int(span+1.0);
    int p2 = int(span+2.0);
    int p3 = int(span+3.0);
    vec3 c3 = CR00*colors[p0] + CR01*colors[p1] + CR02*colors[p2] + CR03*colors[p3];
    vec3 c2 = CR10*colors[p0] + CR11*colors[p1] + CR12*colors[p2] + CR13*colors[p3];
    vec3 c1 = CR20*colors[p0] + CR21*colors[p1] + CR22*colors[p2] + CR23*colors[p3];
    vec3 c0 = CR30*colors[p0] + CR31*colors[p1] + CR32*colors[p2] + CR33*colors[p3];
    return ((c3*m+c2)*m+c1)*m+c0;
}


