struct Data{
    vec3 normal;
    float dist;
};


vec3 getPostion(Ray ray, float dist){

    return ray.origin + ray.direction * dist;

}

Data distPlane(Ray ray, Plane plane)
{
    Data d;
    float denom = dot(plane.normal, ray.direction);
    if (denom < 0.)
    {
        vec3 originPosition = plane.position - ray.origin;

        d.normal = plane.normal;
        d.dist = dot(originPosition, plane.normal) / denom;

        return d;
    }
    d.dist = INFINITY;
    return d;
}

float checkPosition(Plane plane, Ray ray, Box box){
    vec3 vecMin = vec3(-.5, -.5, -.5);
    vec3 vecMax = vec3(.5, .5, .5);


    float dist = distPlane(ray, plane).dist;
    vec3 intersectionPoint = (getPostion(ray, dist));
    if(dist > 0. && 
       intersectionPoint.x > vecMin.x-0.001 && intersectionPoint.x < vecMax.x+0.001 && 
       intersectionPoint.y > vecMin.y-0.001 && intersectionPoint.y < vecMax.y+0.001 && 
       intersectionPoint.z > vecMin.z-0.001 && intersectionPoint.z < vecMax.z+0.001)
        return dist;
    return INFINITY;
}


Data distBox(Ray ray, Box box)
{
    Data d;
    d.dist = INFINITY;
    ray.origin = (vec4((ray.origin.xyz), 1.)*inverse(box.transform)).xyz;
    ray.direction = (vec4((ray.direction.xyz), 0.)*inverse(box.transform)).xyz;

    vec3 Fractal = 1./ray.direction;

    float Vmin = -1.;
    float Vmax =  1.;

    float t1 = (Vmin - ray.origin.x) * Fractal.x;
    float t2 = (Vmax - ray.origin.x) * Fractal.x;
    float t3 = (Vmin - ray.origin.y) * Fractal.y;
    float t4 = (Vmax - ray.origin.y) * Fractal.y;
    float t5 = (Vmin - ray.origin.z) * Fractal.z;
    float t6 = (Vmax - ray.origin.z) * Fractal.z;

    float tmin = max(max(min(t1, t2), min(t3, t4)), min(t5, t6));
    float tmax = min(min(max(t1, t2), max(t3, t4)), max(t5, t6));

    if (!(tmax < 0. || tmin > tmax))
    {
        d.dist = tmin;
        vec3 centerVec = d.dist*ray.direction+ray.origin;
        if(centerVec.x > 0.9999) d.normal = vec3(1., 0., 0.);
        if(centerVec.x < -0.9999) d.normal = vec3(-1., 0., 0.);
        if(centerVec.y > 0.9999) d.normal = vec3(0., 1., 0.);
        if(centerVec.y < -0.9999) d.normal = vec3(0., -1., 0.);
        if(centerVec.z > 0.9999) d.normal = vec3(0., 0., 1.);
        if(centerVec.z < -0.9999) d.normal = vec3(0., 0., -1.);
    }

    return d;
}

BoundingBox calculateBoundingBox(Box box){
    BoundingBox b;

    mat4 inv = box.transform;

    float xPos = inv[0][3];
    float yPos = inv[1][3];
    float zPos = inv[2][3];

    float x = (abs(inv[0][0])+abs(inv[0][1])+abs(inv[0][2]));
    float y = (abs(inv[1][0])+abs(inv[1][1])+abs(inv[1][2]));
    float z = (abs(inv[2][0])+abs(inv[2][1])+abs(inv[2][2]));

    float minX = xPos-x;
    float minY = yPos-y;
    float minZ = zPos-z;

    float maxX = xPos+x;
    float maxY = yPos+y;
    float maxZ = zPos+z;

    b.Min = vec3(minX, minY, minZ);
    b.Max = vec3(maxX, maxY, maxZ);

    return b;
}

bool RenderBoundingBox(Ray ray, BoundingBox box)
{

    vec3 Fractal = 1./ray.direction;

    vec3 Vmin = box.Min;
    vec3 Vmax = box.Max;

    float t1 = (Vmin.x - ray.origin.x) * Fractal.x;
    float t2 = (Vmax.x - ray.origin.x) * Fractal.x;
    float t3 = (Vmin.y - ray.origin.y) * Fractal.y;
    float t4 = (Vmax.y - ray.origin.y) * Fractal.y;
    float t5 = (Vmin.z - ray.origin.z) * Fractal.z;
    float t6 = (Vmax.z - ray.origin.z) * Fractal.z;

    float tmin = max(max(min(t1, t2), min(t3, t4)), min(t5, t6));
    float tmax = min(min(max(t1, t2), max(t3, t4)), max(t5, t6));

    if (!(tmax < 0. || tmin > tmax))
    {
        return true;
    }

    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec3 camPosition = vec3(0., 0., -4.);
    mat4 cam = Camera(camPosition, vec3(0., 0., 1.), vec3(0., 1., 0.));

    Ray ray;
    ray.origin = camPosition;
    ray.direction = normalize(cam * vec4(uv.xy, 1.0, 0.)).xyz;

    Plane plane;
    plane.position = vec3(0., -3., 0.);
    plane.normal = vec3(0., 1., 0.);

    vec3 pos = vec3(0.);

    vec2 mouseUv = (iMouse.xy-0.5*iResolution.xy)/iResolution.y;
    Ray positionRay;
    positionRay.origin = camPosition;
    positionRay.direction = normalize(cam * vec4(mouseUv, 1.0, 0.)).xyz;

    Data p = distPlane(positionRay, plane);
    pos = p.dist * positionRay.direction + positionRay.origin;

    Box boxes[3];
    boxes[0].transform = GetBoxTransform(pos, vec3(0.5, 0.5, 0.5), vec3(iTime, iTime, 0.));
    //boxes[0].transform = rotateX(iTime*2.)*(rotateZ(iTime)*boxes[0].transform);
    boxes[1].transform = GetBoxTransform(vec3(-3., 0., 6.), vec3(2., 2., 1.), vec3(2.*iTime, 0., 2.*iTime));
    boxes[2].transform = GetBoxTransform(vec3( 3., 0., 6.), vec3(2., 2., 1.), vec3(iTime, 0., 2.*iTime));

    BoundingBox boundingBoxes[3];
    boundingBoxes[0] = calculateBoundingBox(boxes[0]);
    boundingBoxes[1] = calculateBoundingBox(boxes[1]);
    boundingBoxes[2] = calculateBoundingBox(boxes[2]);

    vec3 col;
    Data d = distPlane(ray, plane);
    //d.dist = INFINITY;
    vec3 intersectPoint = ray.direction*d.dist+ray.origin;
    vec3 localNormal = vec3(0., 1., 0.);
    for(int i = 0; i < 3; i++){
        Data d2 = distBox(ray, boxes[i]);
        if(d2.dist < d.dist) {
            d = d2;
            localNormal = d.normal;
            d.normal = (vec4(d.normal, 0.)*boxes[i].transform).xyz;
            intersectPoint = (boxes[i].transform* vec4((inverse(boxes[i].transform)*vec4(ray.direction, 0.)).xyz*d.dist, 0.)).xyz+
                              ray.origin;

        }
    }

    vec3 lights[4];
    lights[0] = vec3(0., 0., 0.);
    lights[1] = vec3(-6., 4., -5.73);
    lights[2] = vec3(2., -1., -2.423);
    lights[3] = vec3(0., -3., -4.5);

    for(int i = 0; i < 4; i++){
        if(d.dist > 0. && d.dist < INFINITY) col += (localNormal*0.5+0.5) * dot(d.normal, 
                                        normalize(lights[i]-intersectPoint));
    }

    fragColor = vec4(col,1.0);

    for(int i = 0; i < 3; i++){
        if(RenderBoundingBox(ray, boundingBoxes[i])){
            fragColor += vec4(1., 0., 0., .5);
        }
    }

}

