const float maxDist = 200.;
const float planeDist = 2.;

vec3 cameraPos()
{
    float t = time*0.2;
    float r = 7.;

    vec3 res = vec3(sin(t),1.3,-cos(t));
    res *= r;

    return res;
}

vec3 getRay(vec2 uv)
{
    vec3 cam = cameraPos();
    vec3 origin = vec3(0, 0, 0);
    vec3 look = normalize(origin - cam);
    vec3 upGlob = vec3(0,1,0);
    vec3 right = normalize(cross(upGlob, look));
    vec3 camUp = normalize(-cross(right, look));

    vec3 p = cam + camUp * uv.y + right * uv.x;
    p += look * planeDist;

    vec3 res = p - cam;
    return normalize(res);
}

float getSphere(vec3 p, float size, vec3 origin)
{
    return length(p - origin) - size;
}

float getSphere(vec3 p, float size)
{
    return getSphere(p, size, vec3(0,0,0));
}

vec2 rot(vec2 p, float a)
{
    float ca = cos(a), sa = sin(a);
    mat2 m = mat2(ca, sa, -sa, ca);
    return p * m;
}

vec3 rot(vec3 p, vec3 u, float a)
{
    float omc = 1. - cos(a);
    float sa = sin(a);
    float ca = cos(a);

    mat3 m = mat3(
        ca + u.x*u.x*omc,
        u.x*u.y*omc + u.z*sa,
        u.x*u.z*omc - u.y*sa,

        u.x*u.y*omc - u.z*sa,
        ca + u.y*u.y*omc,
        u.y*u.z*omc + u.x*sa,

        u.x*u.z*omc + u.y*sa,
        u.y*u.z*omc - u.x*sa,
        ca + u.z*u.z*omc
    );

    return m * p;
}

float getSd(vec3 uv)
{
    const int steps = 7;
    float t = time * 0.5;
    float s = 1.;

    vec3 u = normalize(vec3(1));
    vec3 ouv = uv;

    for (int i = 0; i < steps; i++)
    {
        uv = abs(uv);
        uv -= .5;
        uv = rot(uv, u, .3*cos(ouv.y*2. + t*5.));

        uv *= 2.;
        s *= 2.;
    }

    float res = getSphere(uv, 0.5)/s;

    return res;
}

vec3 calcNormal(vec3 p )
{
    const float h = 0.001; // or some other value const
    vec2 k = vec2(1,-1);

    return
        normalize(
            k.xyy*getSd( p + k.xyy*h ) +
            k.yyx*getSd( p + k.yyx*h ) +
            k.yxy*getSd( p + k.yxy*h ) +
            k.xxx*getSd( p + k.xxx*h ) );
}

float raymarch(vec3 sp, vec3 ray)
{
    float depth = 0.;

    for(int i = 0; i < 150; i++)
    {
        vec3 p = sp + ray * depth;
        float dist = getSd(p);

        if (dist <= 0.001)
            return depth - dist;

        depth += dist;

        if (depth >= maxDist)
            return depth;
    }

    return depth;
}

