    vec3 lightDir = normalize(vec3(-1,1,1));
    float dd = dot(n, lightDir);

    dd = dd *.1 + .9;

    vec3 res = col * intP * dd;

    res = pow(abs(res), vec3(1.2));
    fragColor = vec4(res, 0);
}

