float map(vec3 point) {
    vec3 q = fract(point) * 2.0 - 1.0;

    return length(q) - 0.3;
}

float trace(vec3 origin, vec3 direction) {
    float trace = 0.0;

    for(int i = 0; i < 40; i++) {
        vec3 intersectPoint = origin + direction * trace;

        float dist = map(intersectPoint);
        trace += dist * 0.5;
    }

    return trace;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 direction = normalize(vec3(uv, 1.0));
    float timeMod = iTime * 0.25;
    direction.xz *= mat2(cos(timeMod), -sin(timeMod), sin(timeMod), cos(timeMod));

    vec3 origin = vec3(0.0, timeMod * 2.0, timeMod);

    float trace = trace(origin, direction);

    float fog = 1.0 / (1.0 + trace * trace * 0.01);
    vec3 color = vec3((0.5 * cos(timeMod) + 0.5) * fog, (0.5 * cos(2.0 + timeMod) + 0.5) * fog, (0.5 * cos(4.0 + timeMod) + 0.5) * fog);

    fragColor = vec4(color, 1.0);
}

