    vec4 col2 = vec4(sin(beta),
                    -sin(alpha)*cos(beta),
                     cos(alpha)*cos(beta),
                     0);

    vec4 col3 = vec4(x, y, z, 1);

    return mat4(col,
                col1,
                col2,
                col3);

}

