    if(dGr < obj.dist) obj.color = vec3(1);
    obj.dist = min(dGr, obj.dist);

    float lightX = pos.x;
    float lightZ = pos.z;
    if(lightX < 0.95 && lightX > -0.5)
    {
        lightZ = abs(pos.z);
        lightX = fmod(pos.x, 0.5);   
    }

    vec3 lightPos = vec3(lightX, pos.y, lightZ);
    float dL = sdSphere(lightPos , vec3(0., -1.97, 1.5), 0.05); //light
    if(dL < obj.dist) obj.color = vec3(2,0,0);
    //col = getMapColor(d, dL, col, vec3(2,0,0));
    obj.dist = min(obj.dist, dL);

    float dl1 = sdSphere(lightPos, vec3(0.3, -1.97, 1.3), 0.05);//light
    if(dl1 < obj.dist) obj.color = vec3(0,1,0);
    //col = getMapColor(d, dl1, col, vec3(0,1,0));
    obj.dist = min(dl1, obj.dist);

    float dRop = sdCapsule(lightPos, vec3(0, -1.97, 1.5), vec3(0.3, -1.97, 1.3), 0.03); //rope
    float dRop1 = sdCapsule(lightPos, vec3(0.3, -1.97, 1.3), vec3(0.6, -1.97, 1.6), 0.03); //rope 1
    dRop1 += 1000.0*step(0.6, pos.x); //clip last rope off

    dRop = min(dRop, dRop1);
    if(dRop < obj.dist) obj.color = vec3(0);
    //col = getMapColor(d, dRop, col, vec3(0));  
    obj.dist = min(dRop, obj.dist);

    float boundingCap = sdSphere(pos, vec3(0, -1, 0), 1.3);
	//obj.dist = boundingCap;

    if(boundingCap < obj.dist)
    {
        float top = sdSphere(pos, vec3(0,-1.5,0), 1.0);
        float mid = sdSphere(pos, vec3(0,-.3,0), 0.8);
        float bottom = sdSphere(pos, vec3(0,0.7,0), 0.6);
        float bodyDist = min(mid, bottom);
        bodyDist = min(top, bodyDist);
        obj.dist = min(bodyDist, obj.dist);
        obj.color = vec3(1);

        //btns
        float btnY = pos.y;
    	float btnX = pos.x;

    	btnX += step(0.0, btnY)*0.07;
    	if(btnY < 0.4 && btnY > -0.6)
    	{
        	btnY = fmod(pos.y, -0.4);
    	}

        float dBtn = sdSphere(vec3(btnX, btnY, pos.z), vec3(0.8,-0.3,0.0), .07); //buttons
        if(dBtn < obj.dist) obj.color = vec3(0);
        //col = getMapColor(d, dBtn, col, vec3(0));
        obj.dist = min(dBtn, obj.dist);

        float eyeZ = abs(pos.z);
        float dEye = sdSphere(vec3(pos.xy, eyeZ), vec3(0.5,0.9,0.2), .1); //eyes
        if(dEye < obj.dist) obj.color = vec3(0);
        //col = getMapColor(d, dEye, col, vec3(0));
        obj.dist = min(dEye, obj.dist);

        float dNose = sdCone(pos - vec3(1.0, 0.8, 0), 0.05); //nose
        if(dNose < obj.dist) obj.color = vec3(235./255., 137./255., 33./255.);
        //col = getMapColor(d, dNose, col, vec3(235./255., 137./255., 33./255.));
        obj.dist = min(dNose, obj.dist);

        vec3 pipePos1 = vec3(getTransXYZ(-PI/4.0, .0, .0,.0, .0, .0) * vec4(pos,1)); //pipe
        vec3 pipePos = pipePos1 - vec3(.92, 0.8, -0.3);
        pipePos = vec3(getTransXYZ(.0, -PI/4.0, .0, .0, .0, .0) * vec4(pipePos, 1.0));

        float dPipe = sdCappedCylinder(pipePos, vec3(.0, -0.1, .0), vec3(.0, .1, .0), 0.05);
        float dPipe1 = sdCapsule(pipePos1, vec3(.92, 0.8, -0.3), 0.5*normalize(vec3(-1,-0.1,-0.4)), 0.03);    
        dPipe = min(dPipe, dPipe1);
        if(dPipe < obj.dist) obj.color = vec3(0.35, 0.1, 0.1);
        //col = getMapColor(d, dPipe, col, vec3(0.35, 0.1, 0.1));
        obj.dist = min(dPipe, obj.dist);

        vec3 rotPos = vec3(getTransXYZ(.0, .0, -PI/10.0, .0, .0, .0) * vec4(pos,1)); //hat

        float dHat = sdRoundedCylinder(rotPos - vec3(0.2, 1.4, .0), 0.2, 0.1, 0.25); 
        float dHatBtm = sdRoundedCylinder(rotPos - vec3(0.2, 1.15, .0), 0.3, 0.05, 0.015);
        dHat = min(dHat, dHatBtm);
        if(dHat < obj.dist) obj.color = vec3(0);
        obj.dist = min(dHat, obj.dist);

        float dHatRib = sdCappedCylinder(rotPos - vec3(0.2, 1.2, .0), vec3(0), vec3(.0, 0.13, .0), 0.43);
        if(dHatRib < obj.dist) obj.color = vec3(0.7,0,0);
        obj.dist = min(dHatRib, obj.dist);


    }

    obj.isSnow = false;
 	return obj;   
}

vec3 calcNorm(vec3 pos)
{
    float ep = 0.001;
 	float x = map(pos + vec3(ep, 0, 0)).dist - map(pos - vec3(ep, 0, 0)).dist;  
    float y = map(pos + vec3(0, ep, 0)).dist - map(pos - vec3(0, ep, 0)).dist;  
    float z = map(pos + vec3(0, 0, ep)).dist - map(pos - vec3(0, 0, ep)).dist;  

    return normalize(vec3(x, y, z));
}

ray marchRay(vec3 pos, vec3 dir)
{
    vec3 curRay;
    float dist = 0.001;
    object obj;
    ray r;

    for(int x=0; x<100; x++)
    {
        curRay = pos + dist*dir;

        obj = map(curRay);
        float d = obj.dist;
        dist += d;

        if(d < 0.001)
            break;
        if(dist > 25.0)
            break;


    }

    r.curRay = curRay;
    r.obj = obj;
    r.totalDist = dist;

    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0 * fragCoord - iResolution.x)/iResolution.y;
	vec3 camPos = vec3(0, 0, 0);
	vec3 dir = normalize(vec3(uv, -1));

    float rotMod = PI/2.0 + sin(iTime/4.0) * PI/3.0;

    //(2.2+rotMod*iTime/4.0)
    mat4 camTransform = getTransXYZ(0.0, rotMod, 0., 
                                    4.*sin(rotMod), 2., 4. * cos(rotMod));

    dir = normalize(vec3(camTransform * vec4(dir,0))); //transform direction
    camPos = vec3(camTransform * vec4(camPos,1)); //rotate camera around sphere

    //lights
    vec3 sunLightPos = vec3(10, 10, 1);
    vec3 skyLightPos = vec3(0, 12, 0);
    vec3 bouLightPos = vec3(0, -1.9, -1);

    vec3 xmasLightPos = vec3(0, -1.8, 1.5);
    vec3 xmasLightPos1 = vec3(0.3, -1.8, 1.3);
    vec3 xmasLightPos2 = vec3(0.6, -1.8, 1.5);
    vec3 xmasLightPos3 = vec3(0.9, -1.8, 1.3);
    vec3 xmasLightPos4 = vec3(-0.5, -1.9, 1.5);
    vec3 xmasLightPos5 = vec3(-0.2, -1.9, 1.3);

    float sunLightInt = 5.0;
    float skyLightInt = 1.0;
    float bouLightInt = 1.0;
    float xmasLightInt = 0.3;

    ray ret = marchRay(camPos, dir);
    vec3 curRay = ret.curRay;
    float dist = ret.totalDist;

    vec3 rayNorm = calcNorm(curRay);

    ray shaRet = marchRay(curRay + rayNorm*0.01, normalize(sunLightPos - curRay));
    float shaDist = shaRet.totalDist;

    //ray shaRetBou = marchRay(curRay + rayNorm*0.01, normalize(bouLightPos - curRay));
    //float shaDistBou = shaRetBou.totalDist;

    bool objIsSnow = shaRet.obj.isSnow;
    if(objIsSnow)
        shaDist = 26.0;

    vec3 col;

    vec3 xmasRay = curRay;
    vec3 xmasNorm = rayNorm;
    xmasRay.z = abs(xmasRay.z);
    xmasNorm.z = abs(xmasNorm.z);

    float slIntensity = sunLightInt * clamp(dot(rayNorm, normalize(sunLightPos - curRay)), 0.0, 1.0);
    float skLIntensity = skyLightInt * clamp(dot(rayNorm, normalize(skyLightPos - curRay)), 0.0, 1.0);
    float bouIntensity = bouLightInt * clamp(dot(rayNorm, normalize(bouLightPos - curRay)), 0.0, 1.0);

    float xmInt = xmasLightInt * clamp(dot(xmasNorm, normalize(xmasLightPos - xmasRay)), 0.0, 1.0);
    float xmInt1 = xmasLightInt * clamp(dot(xmasNorm, normalize(xmasLightPos1 - xmasRay)), 0.0, 1.0);
    float xmInt2 = xmasLightInt * clamp(dot(xmasNorm, normalize(xmasLightPos2 - xmasRay)), 0.0, 1.0);
    float xmInt3 = xmasLightInt * clamp(dot(xmasNorm, normalize(xmasLightPos3 - xmasRay)), 0.0, 1.0);
    float xmInt4 = xmasLightInt * clamp(dot(xmasNorm, normalize(xmasLightPos4 - xmasRay)), 0.0, 1.0);
    float xmInt5 = xmasLightInt * clamp(dot(xmasNorm, normalize(xmasLightPos5 - xmasRay)), 0.0, 1.0);

    vec3 slCol = vec3(0.2, 0.2, 0.2) * slIntensity;
    vec3 skCol = vec3(0.0, 0.0, 0.2) * skLIntensity;
    vec3 bouCol = vec3(0.2, 0.2, 0.2) * bouIntensity;
    vec3 xmCol = vec3(1, 0, 0) * xmInt;
    vec3 xmCol1 = vec3(0, 1, 0) * xmInt1;
    vec3 xmCol2 = vec3(1, 0, 0) * xmInt2;
    vec3 xmCol3 = vec3(0, 1, 0) * xmInt3;
    vec3 xmCol4 = vec3(1, 0, 0) * xmInt4;
    vec3 xmCol5 = vec3(0, 1, 0) * xmInt5;

    vec3 allLightMod = slCol*step(25., shaDist) + skCol + bouCol + xmCol + xmCol1 + xmCol2 + xmCol3 + xmCol4 + xmCol5;

    if(dist > 25.0)
    {
        col = (0.6+uv.y)*vec3(0.0, 0.2, 0.4) + (1.0-uv.y)*vec3(0.1);
        col = applyFog(col, dist, curRay, sunLightPos);
    }
    else
    {        
        col = ret.obj.color * (allLightMod);
        col = applyFog(col, dist, curRay, sunLightPos);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

float noise1(int x, int y)
{
    int n;
    n = x+y*57;
    x = (n<<13) ^ n;
    return ( 1.0 - float( (n * (n * n * 15731 + 789221) + 1376312589) & 0x7fffffff) / 1073741824.0);

}

float smoothedNoise2D(int x, int y)
{
    int multiplier = 1;
    float corners, sides, center;
    corners = ( noise1(int(x-multiplier), int(y-multiplier))+noise1(int(x+multiplier), int(y-multiplier))+noise1(int(x-multiplier), int(y+multiplier))+noise1(int(x+multiplier), int(y+multiplier)) ) / 16.0;
    sides   = ( noise1(int(x-multiplier), int(y))+noise1(int(x+multiplier), int(y))+noise1(int(x), int(y-multiplier))+noise1(int(x), int(y+multiplier)) ) /  8.0;
    center  =  noise1(int(x), int(y)) / 4.0;
    return corners + sides + center;
}


float interpol(float a, float b, float x)
{
    float ft, f;
    ft = x * 3.1415927;
    f = (1.0 - cos(ft)) * 0.5;
    return  a*(1.0-f) + b*f;
}

float interpolatedNoise(float x,float y)
{
      float fractional_X,fractional_Y,v1,v2,v3,v4,i1,i2;
      int integer_X,integer_Y;

      integer_X    = int(x);
      fractional_X = x - float(integer_X);

      integer_Y    = int(y);
      fractional_Y = y - float(integer_Y);

      v1 = smoothedNoise2D(integer_X,     integer_Y);
      v2 = smoothedNoise2D(integer_X + 1, integer_Y);
      v3 = smoothedNoise2D(integer_X,     integer_Y + 1);
      v4 = smoothedNoise2D(integer_X + 1, integer_Y + 1);

      i1 = interpol(v1 , v2 , fractional_X);
      i2 = interpol(v3 , v4 , fractional_X);

      return interpol(i1 , i2 , fractional_Y);

}

float fbmNoise(float x,float y)
{
    x = x*1000.0;
    y = y*1000.0;
    float total,p, frequency,amplitude;
    int i,n;
    total = 0.0;
    p = 2.0;
    n = 8;


    for(i=0; i<=n;i++)
    {
        frequency = pow(2.0,float(i));    
        amplitude = sin(iTime)+p/(pow(2.0,float(i)));
        total = total+interpolatedNoise(x*frequency/20.0,y*frequency/20.0)*amplitude;
    }
    return total;
}

