#define BRICKWIDTH 0.25
#define BRICKHEIGHT 0.08
#define MORTARTHICKNESS 0.01

#define BMWIDTH (BRICKWIDTH+MORTARTHICKNESS)
#define BMHEIGHT (BRICKHEIGHT+MORTARTHICKNESS)

#define MWF (MORTARTHICKNESS*0.5/BMWIDTH)
#define MHF (MORTARTHICKNESS*0.5/BMHEIGHT)

#define CBrick vec3(0.5,0.15,0.14)
#define CMortar vec3(0.5,0.5,0.5)


#define frac(x) mod((x),1.0)
#define sIntegral(s) (floor(s)*(1.0-2.0*MWF)+min(1.0-2.0*MWF,max(0.0,frac(s)-MWF)))
#define tIntegral(t) (floor(t)*(1.0-2.0*MHF)+min(1.0-2.0*MHF,max(0.0,frac(t)-MHF)))


float xPixelWidth;
float yPixelWidth;

vec3 brick(vec2 p)
{
    float s = p.x/BMWIDTH;
    float t = p.y/BMHEIGHT;

    if(mod(floor(t),2.0)==1.0)//å¥æ°è¡æ°´å¹³åç§»åä¸ªç å
        s += 0.5;
    //åç´ ç¹æå¨ç åç¼å·
    float sBrick = floor(s);
    float tBrick = floor(t);
    ///åç´ ç¹å¨ç åååæ 
    s -= sBrick;
    t -= tBrick;
    float w = step(MWF,s) - step(1.0-MWF,t);
    float h = step(MHF,s) - step(1.0-MHF,t);
    vec3 col = mix(CMortar, CBrick, w*h);
    return col;
}


vec3 brickAntialiase(vec2 p){
    float s = p.x/BMWIDTH;
    float t = p.y/BMHEIGHT;
    float ds = xPixelWidth/BMWIDTH;
    float dt = yPixelWidth/BMHEIGHT;
    if(mod(floor(t),2.0)==1.0)//å¥æ°è¡æ°´å¹³åç§»åä¸ªç å
        s += 0.5;
    ///åç´ ç¹å¨ç åååæ 
    float w = (sIntegral(s+ds)-sIntegral(s))/ds;
    float h = (tIntegral(t+dt)-tIntegral(t))/dt;
    vec3 col = mix(CMortar, CBrick, w*h);
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy;
    p = p*2.0-1.0;
    p.x *= iResolution.x/iResolution.y;

    float anim = sin(fract(iTime/4.0)*3.1415926);
    float scale = anim*2.0+0.1;

    xPixelWidth = yPixelWidth = 2.0/iResolution.y*scale;

    vec3 col;
    if(p.x<0.0)
    {
        col = brick(p*scale);
    }else if(p.x>0.0)
    {
        col = brickAntialiase(p*scale);
    }

    float f = smoothstep(0.0,0.01,abs(p.x));
    col*=f;
    fragColor = vec4(col,1.0);
}

