float hash(float i) { return rand(vec2(i, i*123.4+245.6)); }


float noise(float x) {
    float i = floor(x), f = fract(x);
    float u = f * f * (3.0 - 2.0 * f);
    return 2.0 * mix(hash(i), hash(i + 1.0), u) - 1.0;
}


float terrain(float x) {
    float y = 0.0;
    for (int octave = 0; octave < 10; ++octave) {
        float k = pow(2.0, float(octave));
        y += noise(x * k) / k;
    }

    return y * 0.4 + 0.66;
}


