float n(vec3 uv)
{
    vec3 ijk = floor(uv);
    vec3 luv = fract(uv);

    float d = 10000.;
    vec3 di = ijk;

    for(int i = -1; i <= 1; i++)
    for(int j = -1; j <= 1; j++)
    for(int k = -1; k <= 1; k++)
    {
    	vec3 p = getPoint(ijk + vec3(i,j,k));
        p += vec3(i,j,k);
        float dist = distance(p, luv);

        if (dist < d)
        {
            d = dist;
            di = ijk + vec3(i,j,k);
        }
    }

    return rand31(di);
}

