mat3 cspace(vec3 fo)
{
  mat3 res;
  res[2] = fo;
  res[0] = cross(fo, vec3(0,0,1));
  res[1] = cross(res[0],fo);
  return res;
}

mat3 inter(vec3 cpos, vec3 ray)
{
  mat3 res = mat3(0);
  int hit = 0;
  float h; float t = 0.;
  for (int i = 0; i<250; i++)
  {
    h = de(cpos+t*ray);
    if (h<.0005)
    {
      hit = 1;
      break;
    }
    t+=h;
  }
  res[0] = cpos+t*ray;
  res[1] = grade(cpos+t*ray);
  if (hit==0) res[2] = vec3(-1);
  return res;
}

float de(vec3 p)
{
  /*
  p = abs(p);
  vec3 q = vec3(min(p.x,1.), min(p.y,1.), min(p.z,1.));
  return distance(p,q)-1.;
  */

  float r=sin(iTime*PI/2.)*4.;
  float t = 0.;
  for (int i = 0; i<3; i++)
  {
    //t+=1./distance(p,r*vec3(cos(PI*2.*i/6.), sin(PI*2.*i/6.),0));
    t+=exp(-distance(p,r*vec3(cos(PI*2.*float(i)/3.), sin(PI*2.*float(i)/3.),0)));
  }

  return -log(exp(-max(-log(t)-1.,abs(p.z)-.1))+exp(.5-cir(p,4.)));

}

vec3 grade(vec3 p)
{
  vec3 e = vec3(.001,0,0);
  return vec3(
    de(p+e.xyz)-de(p-e.xyz),
    de(p+e.zxy)-de(p-e.zxy),
    de(p+e.yzx)-de(p-e.yzx)
  )/(e.x*2.);
}

float cir(vec3 p, float r)
{
  vec3 q = vec3(p.xy/length(p.xy),0);
  return distance(p, r*q);
}

