//https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdSphere(vec3 pos, float rad)
{
 	return length(pos) - rad;   
}


//https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
    vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
    return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

//https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCappedCylinder( vec3 p, float h, float r, float rd)
{
    vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rd;
}

//https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdTorus( vec3 p, vec2 t )
{
    vec2 q = vec2(length(p.xz)-t.x,p.y);
    return length(q)-t.y;
}

mat4 getTransXYZ(float alpha, float beta, float gamma, float x, float y, float z)
{
 	vec4 col =  vec4(cos(beta)*cos(gamma),
                     cos(alpha)*sin(gamma) + sin(alpha)*sin(beta)*cos(gamma),
                     sin(alpha)*sin(gamma) - cos(alpha)*sin(beta)*cos(gamma),
                     0);

    vec4 col1 = vec4(-cos(beta)*sin(gamma),
                      cos(alpha)*cos(gamma) - sin(alpha)*sin(beta)*sin(gamma),
                      sin(alpha)*cos(gamma) + cos(alpha)*sin(beta)*sin(gamma),
                      0);

    vec4 col2 = vec4(sin(beta),
                    -sin(alpha)*cos(beta),
                     cos(alpha)*cos(beta),
                     0);

    vec4 col3 = vec4(x, y, z, 1);

    return mat4(col,
                col1,
                col2,
                col3);

}

float fmod(float x, float y)
{
 	return x - y * floor(x/y); 
}

//https://www.iquilezles.org/www/articles/smin/smin.htm
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

Object map(vec3 pos)
{
    Object o;
    o.dist = 1000.0;
    o.normEps = 0.0001;

    vec3 ballPos = pos;
    ballPos.z = abs(ballPos.z);

    float dBaseBall = sdSphere(ballPos - vec3(0, -2.1, 0.85), 0.16);    
    if(dBaseBall < o.dist)
    {
        o.dist = smin(o.dist, dBaseBall, 0.1);
        o.color = vec3(60.0 / 255.0, 23.0/255.0, 30.0/255.0);
        o.specVal = 55.0;
        o.specKs = 0.5;
        o.normEps = 0.000001;
    }

    vec3 ball1Pos = pos;
    ball1Pos.x = abs(ball1Pos.x);
    float dBaseBall1 = sdSphere(ball1Pos - vec3(0.8, -2.1, 0.2), 0.16); 
    if(dBaseBall1 < o.dist)
    {
        o.dist = smin(o.dist, dBaseBall1, 0.1);
        o.color = vec3(60.0 / 255.0, 23.0/255.0, 30.0/255.0);
        o.specVal = 55.0;
        o.specKs = 0.5;
        o.normEps = 0.000001;
    }

    vec3 ball2Pos = pos;
    ball2Pos.x = abs(ball2Pos.x);
    ball2Pos.z = abs(ball2Pos.z);
    float dBaseBall2 = sdSphere(ball2Pos - vec3(0.5, -2.1, 0.66), 0.16);

    if(dBaseBall2 < o.dist)
    {
        o.dist = smin(o.dist, dBaseBall2, 0.1);
        o.color = vec3(60.0 / 255.0, 23.0/255.0, 30.0/255.0);
        o.specVal = 55.0;
        o.specKs = 0.5;
        o.normEps = 0.000001;
    }

    /*float dBaseBallBig = sdSphere(pos - vec3(0, -2.25, 0.0), 0.52);
    o.dist = smin(o.dist, dBaseBallBig, 0.1);
    if(dBaseBallBig < o.dist)
    {
        o.color = vec3(23.0 / 255.0, 23.0/255.0, 30.0/255.0);
        o.specVal = 55.0;
        o.normEps = 0.000001;
    }*/


    float dTubeBase = sdCappedCylinder(pos - vec3(0, -2.26, 0), 1.1, 0.15, 0.02);
    float dTubeBaseBtm = sdCappedCylinder(pos - vec3(0, -2.55, 0), 2.0, 0.15, 0.03);
    float dTubeBaseRing = sdTorus(pos - vec3(0, -2.445, 0), vec2(1.65, 0.1));
    dTubeBase = smin(dTubeBaseBtm, dTubeBase, 0.02);
    dTubeBase = smin(dTubeBase, dTubeBaseRing, 0.01);
    if(dTubeBase < o.dist)
    {
        o.dist = dTubeBase;
        o.color = vec3(23.0 / 255.0, 23.0/255.0, 30.0/255.0);
        o.specVal = 10.0;
        o.specVal = 2.5;
        o.specKs = 0.5;
        o.normEps = 0.000001;
    }

    vec3 pipePos = pos;
    pipePos.x = abs(pipePos.x);
    float dPipeBound = sdSphere(pipePos - vec3(1.8, -3., 1.8), 1.0);    
    if(dPipeBound < o.dist)
    {

        float ang = PI/2.0;
        vec2 col1 = vec2(cos(ang), sin(ang));
        vec2 col2 = vec2(-sin(ang), cos(ang));
        mat2 rotMat = mat2(col1, col2);

        vec2 col3 = vec2(cos(-ang/2.0), sin(-ang/2.0));
        vec2 col4 = vec2(-sin(-ang/2.0), cos(-ang/2.0));
        mat2 rotMat1 = mat2(col3, col4);

        pipePos = vec3(pipePos.x, rotMat * pipePos.yz);
        pipePos = vec3(rotMat1 * pipePos.xy, pipePos.z);


        float dPipe = sdCappedCylinder(pipePos - vec3(-0.2,-2.9,-2.8), 0.3, 0.91, 0.02);
        if(dPipe < o.dist)
        {
            o.dist = smin(o.dist , dPipe, 0.02);
            o.color = vec3(23.0 / 255.0, 23.0/255.0, 30.0/255.0);
            o.specVal = 10.0;
            o.specVal = 5.5;
            o.specKs = 0.5;
            o.normEps = 0.0001;
        }
    }


    float dSphereBound = sdSphere(pos-vec3(0,-1.,0), 1.3); //blob's bounding sphere
    if(dSphereBound < o.dist)
    {
        //o.dist = sphereBound; //uncomment to show bounding sphere...
        vec3 spherePos = vec3(getTransXYZ(0., iTime/2.0, .0, .0, .0, .0) * vec4(pos, 1));

        //modified version of deformation pattern at 
        //https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
        float xMod = 0.12*cos(iTime/2.0)*sin(12.0*spherePos.y)*sin(12.0*spherePos.x)*sin(12.0*spherePos.z);
        float yMod = 0.12*cos(iTime/2.0)*cos(spherePos.y)*sin(10.0*spherePos.x)*sin(10.0*spherePos.z);
        float zMod = 0.12*(cos(iTime/2.0))*sin(10.0*spherePos.y)*sin(10.0*spherePos.x)*sin(9.0*spherePos.z);

        spherePos = vec3(getTransXYZ(0., 1.5*iTime, .0, .0, .0, .0) * vec4(spherePos, 1));
        spherePos = (spherePos - vec3(.0, -1.1, .0) - vec3(xMod, yMod, zMod));

        float dSphere = sdSphere(spherePos, 1.0);

        if(dSphere < o.dist)
        {
            o.dist = dSphere;
            o.color = vec3(.1, 0.5, .1);
            o.specVal = 50.0;
            o.specKs = 1.0;
            o.normEps = 0.0001;
        }      
    }



    return o;
}

vec3 calcNorm(vec3 pos, float eps)
{
    float ep = min(0.0001, eps);
    //float ep = 0.0001;
 	float x = map(pos + vec3(ep, .0, .0)).dist - map(pos - vec3(ep, .0, .0)).dist;
    float y = map(pos + vec3(.0, ep, .0)).dist - map(pos - vec3(.0, ep, .0)).dist;
   	float z = map(pos + vec3(.0, .0, ep)).dist - map(pos - vec3(.0, .0, ep)).dist;
    return normalize(vec3(x,y,z));
}

float calcLight(vec3 pos, vec3 lightDir)
{
    return dot(pos, lightDir);
}

MarchRes marchRays(vec3 pos, vec3 dir)
{
    MarchRes res;
    Object o;
    float totalDist = 0.001;
    vec3 curRay = pos;
    for(int x=0; x<100; x++)
    {
        curRay = pos + (dir*totalDist);
     	o = map(curRay);

        if(abs(o.dist) < 0.0002)
            break;
        if(totalDist > 20.0)
            break;

        totalDist += o.dist*0.85; //slow down to handle insane morphing of sphere
    }

    res.totalDist = totalDist;
    res.curRay = curRay;
    res.obj = o;

    if(totalDist <= 20.0)
        res.obj.normal = calcNorm(curRay, res.obj.normEps);

    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord - iResolution.x)/iResolution.y;
	vec3 camEye = vec3(0, 0.0, 3.2 + -0.5*sin(iTime/3.0));
    vec3 dir = vec3(uv, -1);
    dir = normalize(dir);

    vec3 light = vec3(0, 10, 10);
    vec3 lightBlob = vec3(1., -0.5, 0.);
    float lightTime = fmod(iTime, 7.0);
    float lightInt = 2.7 + fract(lightTime*lightTime / 3.0);
    float lightBlobInt = 2.0;
    vec3 lightCol = vec3(0.4,0.55,0.4);;
    vec3 lightBlobCol = vec3(0., 0.2, 0);

    MarchRes res = marchRays(camEye, dir);
    vec3 col = vec3(0);
    if(res.totalDist < 20.0)
    {
        vec3 pos = res.curRay;

        float lightShadow = step(20.0, marchRays(pos, normalize(light)).totalDist);
        vec3 lightDif = lightCol * clamp(calcLight(res.obj.normal, normalize(light - pos)), 0.0, 1.0) * lightInt;
        vec3 lightSpecR = 2.0 * 
            clamp(dot(res.obj.normal, normalize(light-pos)), 0.0, 1.0) 
            * res.obj.normal - normalize(light-pos);

        float lightSpec = clamp(dot(normalize(camEye-pos), lightSpecR), 0.0, 1.0);

        vec3 lightBlobFinal = lightBlobCol * clamp(calcLight(res.obj.normal, normalize(lightBlob - pos)), .0, 1.) * lightBlobInt;
        col = res.obj.color * (lightCol*lightDif) * lightShadow;
        col += lightInt*(lightCol*pow(lightSpec, res.obj.specVal))*res.obj.specKs * lightShadow;

    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

/**
 Just messing around with different types of glitching effects.
*/

// try commenting/uncommenting these to isolate/combine different glitch effects.
#define ANALOG
#define DIGITAL
#define CRT

// amount of seconds for which the glitch loop occurs
#define DURATION 5.
// percentage of the duration for which the glitch is triggered
#define AMT .5 

#define SS(a, b, x) (smoothstep(a, b, x) * smoothstep(b, a, x))

#define UI0 1597334673U
#define UI1 3812015801U
#define UI2 uvec2(UI0, UI1)
#define UI3 uvec3(UI0, UI1, 2798796415U)
#define UIF (1. / float(0xffffffffU))

// Hash by David_Hoskins
vec3 hash33(vec3 p)
{
	uvec3 q = uvec3(ivec3(p)) * UI3;
	q = (q.x ^ q.y ^ q.z)*UI3;
	return -1. + 2. * vec3(q) * UIF;
}

// Gradient noise by iq
float gnoise(vec3 x)
{
    // grid
    vec3 p = floor(x);
    vec3 w = fract(x);

    // quintic interpolant
    vec3 u = w * w * w * (w * (w * 6. - 15.) + 10.);

    // gradients
    vec3 ga = hash33(p + vec3(0., 0., 0.));
    vec3 gb = hash33(p + vec3(1., 0., 0.));
    vec3 gc = hash33(p + vec3(0., 1., 0.));
    vec3 gd = hash33(p + vec3(1., 1., 0.));
    vec3 ge = hash33(p + vec3(0., 0., 1.));
    vec3 gf = hash33(p + vec3(1., 0., 1.));
    vec3 gg = hash33(p + vec3(0., 1., 1.));
    vec3 gh = hash33(p + vec3(1., 1., 1.));

    // projections
    float va = dot(ga, w - vec3(0., 0., 0.));
    float vb = dot(gb, w - vec3(1., 0., 0.));
    float vc = dot(gc, w - vec3(0., 1., 0.));
    float vd = dot(gd, w - vec3(1., 1., 0.));
    float ve = dot(ge, w - vec3(0., 0., 1.));
    float vf = dot(gf, w - vec3(1., 0., 1.));
    float vg = dot(gg, w - vec3(0., 1., 1.));
    float vh = dot(gh, w - vec3(1., 1., 1.));

    // interpolation
    float gNoise = va + u.x * (vb - va) + 
           		u.y * (vc - va) + 
           		u.z * (ve - va) + 
           		u.x * u.y * (va - vb - vc + vd) + 
           		u.y * u.z * (va - vc - ve + vg) + 
           		u.z * u.x * (va - vb - ve + vf) + 
           		u.x * u.y * u.z * (-va + vb + vc - vd + ve - vf - vg + vh);

    return 2. * gNoise;
}

// gradient noise in range [0, 1]
float gnoise01(vec3 x)
{
	return .5 + .5 * gnoise(x);   
}

// warp uvs for the crt effect
vec2 crt(vec2 uv)
{
    float tht  = atan(uv.y, uv.x);
    float r = length(uv);
    // curve without distorting the center
    r /= (1. - .1 * r * r);
    uv.x = r * cos(tht);
    uv.y = r * sin(tht);
    return .5 * (uv + 1.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float t = iTime;

    // smoothed interval for which the glitch gets triggered
    float glitchAmount = SS(DURATION * .001, DURATION * AMT, mod(t, DURATION));  
	float displayNoise = 0.;
    vec3 col = vec3(0.);
    vec2 eps = vec2(5. / iResolution.x, 0.);
    vec2 st = vec2(0.);
#ifdef CRT
	uv = crt(uv * 2. - 1.); // warped uvs
    ++displayNoise;
#endif
    // analog distortion
    float y = uv.y * iResolution.y;
    float distortion = gnoise(vec3(0., y * .01, t * 500.)) * (glitchAmount * 4. + .1);
    distortion *= gnoise(vec3(0., y * .02, t * 250.)) * (glitchAmount * 2. + .025);
#ifdef ANALOG
    ++displayNoise;
    distortion += smoothstep(.999, 1., sin((uv.y + t * 1.6) * 2.)) * .02;
    distortion -= smoothstep(.999, 1., sin((uv.y + t) * 2.)) * .02;
    st = uv + vec2(distortion, 0.);
    // chromatic aberration
    col.r += textureLod(iChannel0, st + eps + distortion, 0.).r;
    col.g += textureLod(iChannel0, st, 0.).g;
    col.b += textureLod(iChannel0, st - eps - distortion, 0.).b;
#else
    col += texture(iChannel0, uv, 0.).xyz;
#endif

#ifdef DIGITAL
    // blocky distortion
    float bt = floor(t * 30.) * 300.;
    float blockGlitch = .2 + .9 * glitchAmount;
    float blockNoiseX = step(gnoise01(vec3(0., uv.x * 3., bt)), blockGlitch);
    float blockNoiseX2 = step(gnoise01(vec3(0., uv.x * 1.5, bt * 1.2)), blockGlitch);
    float blockNoiseY = step(gnoise01(vec3(0., uv.y * 4., bt)), blockGlitch);
    float blockNoiseY2 = step(gnoise01(vec3(0., uv.y * 6., bt * 1.2)), blockGlitch);
    float block = blockNoiseX2 * blockNoiseY2 + blockNoiseX * blockNoiseY;
    st = vec2(uv.x + sin(bt) * hash33(vec3(uv, .5)).x, uv.y);
    col *= 1. - block;
    block *= 1.15;
    col.r += textureLod(iChannel0, st + eps, 0.).r * block;
    col.g += textureLod(iChannel0, st, 0.).g * block;
    col.b += textureLod(iChannel0, st - eps, 0.).b * block;
#endif
    // white noise + scanlines
    displayNoise = clamp(displayNoise, 0., 1.);
    col += (.15 + .65 * glitchAmount) * (hash33(vec3(fragCoord, mod(float(iFrame),
					1000.))).r) * displayNoise;
    col -= (.25 + .75 * glitchAmount) * (sin(4. * t + uv.y * iResolution.y * 1.75))
					* displayNoise;
#ifdef CRT
    //crt vignette (from https://www.shadertoy.com/view/Ms23DR)
    float vig = 8.0 * uv.x * uv.y * (1.-uv.x) * (1.-uv.y);
	col *= vec3(pow(vig, .25)) * 1.5;
    if(uv.x < 0. || uv.x > 1.) col *= 0.;
#endif
    fragColor = vec4(col, 1.0);
}

const vec3 RING_COL = vec3(.6,.2,.1);
const float SCALE = 2.5;

float sdCircle(vec2 p, float r) {
    return length(p) - r;
}

float sdSquare(vec2 p, vec2 s) {
    vec2 b = abs(p) - s;
	return max(b.x,b.y);    
}

//Thanks iq
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }
//Thanks again iq
float sdRoundedX( in vec2 p, in float w, in float r )
{
    p = abs(p);
    return length(p-clamp(p.x+p.y,0.0,w)*0.5) - r;
}

//Rotate a point about the origin
vec2 rotate(vec2 p, float a) {
    float s = sin(a);
  	float c = cos(a);

    float xnew = p.x * c - p.y * s;
  	float ynew = p.x * s + p.y * c;

    return vec2(xnew,ynew);
}

//Given a point, find the closest distance
float scene(vec2 p) {

    float st = sin(iTime);

    //p = rotate(p,length(p)*st);

    float repeat = .75;
    vec2 pr = mod(p,repeat)-repeat/2.;

    float square = abs(sdSquare(rotate(pr,iTime),vec2(.2)))-.025;
    float x = sdRoundedX(rotate(pr,0.7853982),.75,.1);

  	//float circle = sdCircle(p-vec2(sin(iTime),cos(iTime))*.5,.2);

    return max(square,-x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = (fragCoord/iResolution.xy - .5) * SCALE;

    //Multiply by aspect
    uv.x *= iResolution.x / iResolution.y;

    //Get distance from point to nearest surface
    float dist = scene(uv);
    vec3 col;
    if (dist <= 0.) {
        col = vec3(1.);
    }else{
        //Get ring
        float rings = step(mod(dist*100.0-iTime*5.0,7.5),.5);

        col = mix(RING_COL,RING_COL*.75,rings)*(3.-dist);
        col = col * .5;

        //Get mouse position
        vec2 mp = (iMouse.xy / iResolution.xy - .5) * SCALE;
        mp.x *= iResolution.x / iResolution.y;

        float mDist = distance(uv,mp);
        vec2 mDir = normalize(mp-uv);

        //Gradient for light, thanks cleiprelli
        col /= 1. + mDist*mDist;

        float t = 0.;
        float k;
        float minK = 1000.;
        while (t < mDist) {
            k = scene(uv+mDir*t);
            minK = min(k,minK);
            if (k < .0001) {
                col *= .5;
                break;
            }
            t += k;
        }
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

