vec3 sunDir, qHit;
vec2 arcLimV[2];
float tCur, tCyc, dstFar, rBase, rIn, sLoopV[2], aLoopV[2], rLoop, armP, kMin, rBilGrnd, szVeg;
int idObj;
const int idArm = 1, idArmE = 2, idCylL = 3, idCylU = 4, idVeg = 5, idGrnd = 6;
const float nArm = 6.;
const float pi = 3.14159, sqrt3 = 1.7320508;

#define VAR_ZERO min (iFrame, 0)

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 sv;
  float dMin, d, s, sLoop, aLoop, t, r, h, hVeg;
  dMin = dstFar;
  q = p;
  r = length (q.xz);
  h = 0.;
  if (r < rBilGrnd) {
    sv = HexVor (0.5 * q.xz + 0.05 * Noisev2v2 (4. * q.xz));
    h += 0.2 * (smoothstep (0.01 + 0.01 * sv.y, 0.1 + 0.03 * sv.y, sv.x) - 1.) *
       (1. - smoothstep (0.8, 1., r / rBilGrnd));
  }
  q = p;
  d = 0.5 * (q.y - h - 1.5e-3 * r * r);
  DMIN (idGrnd);
  p.y -= rIn + 0.15;
  for (float k = 0.; k < nArm / 2.; k ++) {
    q = p;
    q.xz = Rot2D (q.xz, (2. * pi / nArm) * k);
    if (k == armP) {
      sLoop = sLoopV[0];
      aLoop = aLoopV[0];
    } else {
      sLoop = sLoopV[1];
      aLoop = aLoopV[1];
    }
    q.xy = Rot2D (vec2 (rBase - abs (q.x), q.y - rLoop * sLoop), aLoop - 0.5 * pi);
    s = mod ((64. / aLoop) * atan (q.y, - q.x) / pi + 0.5, 1.);
    r = length (vec2 (length (q.xy) - rLoop * sLoop, q.z)) - rIn;
    d = max (r - 0.02 * SmoothBump (0.25, 0.75, 0.1, s), - r - 0.15);
    d = 0.8 * max (d, dot (vec2 (q.x, abs (q.y)), ((k == armP) ? arcLimV[0] : arcLimV[1])));
    if (d < dMin) {
      dMin = d;
      idObj = idArm;
      qHit = q;
      kMin = k;
    }
    q.xy = Rot2D (q.xy, aLoop) - vec2 (- rLoop * sLoop, 0.27);
    d = max (PrRoundCylDf (q.xzy, rIn + 0.03, 0.1, 0.3), rIn - 0.15 - length (q.xz));
    if (d < dMin) kMin = k;
    DMIN (idArmE);
  }
  q = p;
  d = PrRoundCylDf (q.xzy, rBase + 0.05, 0.15, rIn + 0.1);
  DMIN (idCylL);
  q = p;
  q.y -= 3. * rIn;
  d = PrRoundCylDf (q.xzy, 0.5 * rBase, 0.1, 3. * rIn);
  DMIN (idCylU);
  hVeg = 0.03 + 0.22 * szVeg;
  q = p - vec3 (10.9, -0.3 + hVeg, 9.9);
  d = PrCylDf (q.xzy, 0.03, hVeg);
  q.y -= hVeg;
  q.xz = abs (q.xz) - max (0.08 * (1. + szVeg) - 0.1, 0.02);
  d = min (d, PrSphDf (q, 0.05 * (1. + szVeg)));
  DMIN (idVeg);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.03;
  for (int j = VAR_ZERO; j < 20; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += clamp (3. * h, 0.05, 0.3);
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol, sunCol;
  vec2 q;
  float f, fd, ff, sd;
  q = 0.01 * (ro.xz + 16. * tCur + ((128. - ro.y) / rd.y) * rd.xz);
  ff = Fbm2 (q);
  f = smoothstep (0.2, 0.8, ff);
  fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
  clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
     smoothstep (0., 0.05, abs (fd)));
  col = mix (vec3 (0.4, 0.5, 0.8), clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  sd = clamp (dot (sunDir, rd), 0., 1.);
  sunCol = 2. * vec3 (1., 0.8, 0.7) * pow (sd, 1024.) + vec3 (1., 0.4, 0.2) * pow (sd, 256.);
  return 0.5 * col + 0.5 * Maxv3 (col) * vec3 (1., 0., 0.) + sunCol;
}

vec4 ObjCol (vec3 ro, vec3 rd, float dstObj)
{
  vec4 col4;
  vec3 vn;
  vec2 sv;
  float nDotL, sh, s, a;
  vn = ObjNf (ro);
  nDotL = max (dot (vn, sunDir), 0.);
  if (idObj == idArm) {
    col4 = vec4 (0.9, 0.9, 1., 0.2);
    if (length (vec2 (length (qHit.xy) - rLoop *
       ((kMin == armP) ? sLoopV[0] : sLoopV[1]), qHit.z)) < rIn - 0.01)
       col4 = vec4 (0.6 + 0.4 * sin (512. * atan (qHit.y, - qHit.x) + 5. * tCur), 0., 0., -1.);
    nDotL *= nDotL;
  } else if (idObj == idArmE) {
    col4 = vec4 (0.8, 0.7, 0.8, 0.1);
    nDotL *= nDotL;
  } else if (idObj == idCylL) {
    col4 = vec4 (0.8, 0.8, 0.7, 0.1);
  } else if (idObj == idCylU) {
    col4 = vec4 (0.8, 0.8, 0.7, 0.1);
    a = atan (ro.z, - ro.x) / (2. * pi);
    if (abs (vn.y) < 0.01) col4.rgb *= 0.9 + 0.1 * sin (60. * pi * a);
    s = length (Rot2D (ro.xz, 2. * pi * (floor (6. * a + 0.5) / 6.)) + vec2 (0.7, 0.)) - rIn;
    if (s < 0.) {
      if (s < -0.1) col4 = vec4 (0.6 + 0.4 * sin (5. * tCur), 0., 0., -1.);
      else col4.rgb *= 0.7;
    }
  } else if (idObj == idVeg) {
    col4 = vec4 (0., 0.6, 0., 0.1);
  } else if (idObj == idGrnd) {
    s = Fbm2 (16. * ro.xz);
    col4 = mix (vec4 (0.4, 0.4, 0.45, 0.05), vec4 (0.5, 0.3, 0.1, 0.) *
       (0.9 + 0.2 * smoothstep (0.5, 0.6, s)), smoothstep (0.35, 0.4, s));
    vn = VaryNf (8. * ro, vn, 2. - 1.5 * smoothstep (0.2, 0.5, dstObj / dstFar));
    nDotL = max (dot (vn, sunDir), 0.);
    s = length (ro.xz);
    if (s > 0.8 * rBilGrnd) {
      sv = HexVor (0.5 * ro.xz + 0.05 * Noisev2v2 (4. * ro.xz));
      col4 *= mix (0.6 + 0.4 * smoothstep (0.01 + 0.01 * sv.y, 0.1 + 0.03 * sv.y, sv.x), 1.,
         smoothstep (0.3, 0.8, s / dstFar));
    } else col4 *= 0.5 + 0.5 * smoothstep (-0.2, -0.02, ro.y - 1.5e-3 * s * s);
  }
  if (col4.a >= 0.) {
    sh = min (ObjSShadow (ro, sunDir), 1. - 0.3 * smoothstep (0.4, 0.6,
       Fbm2 (0.1 * ro.xz + 0.2 * tCur)));
    col4.rgb = col4.rgb * (0.2 + 0.8 * sh * nDotL) +
       col4.a * step (0.95, sh) * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
  }
  return col4;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, colR;
  float dstObj;
  int idObjP;
  bool doRefl;
  rIn = 0.3;
  rBase = 2.8;
  rLoop = 6.;
  tCyc = 8.;
  armP = floor (mod (tCur / tCyc, nArm / 2.));
  sLoopV[0] = 1./3. + 100. * pow (1. - SmoothBump (0.25, 0.75, 0.22, mod (tCur / tCyc, 1.)), 2.);
  sLoopV[1] = 1./3. + 100.;
  for (int k = 0; k < 2; k ++) {
    aLoopV[k] = 0.25 * pi / sLoopV[k];
    arcLimV[k] = sin (aLoopV[k] + vec2 (0., 0.5 * pi));
  }
  szVeg = SmoothBump (0.25, 0.75, 0.2, mod (tCur / 10., 1.));
  HexVorInit ();
  doRefl = false;
  rBilGrnd = 25.;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    idObjP = idObj;
    ro += dstObj * rd;
    col4 = ObjCol (ro, rd, dstObj);
    if (col4.a >= 0. && idObjP != idVeg && idObjP != idGrnd) doRefl = true;
    col = col4.rgb;
  } else {
    col = SkyCol (ro, rd);
  }
  if (doRefl) {
    rd = reflect (rd, ObjNf (ro));
    ro += 0.01 * rd;
    rBilGrnd = 5.;
    dstObj = ObjRay (ro, rd);
    if (dstObj < dstFar) {
      ro += dstObj * rd;
      colR = ObjCol (ro, rd, dstObj).rgb;
    } else {
      colR = SkyCol (ro, rd);
    }
    col = mix (col, 0.9 * colR, 0.6);
  }
  return pow (clamp (col, 0., 1.), vec3 (0.8));
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv;
  float el, az, zmFac, t, tSmooth;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  if (mPtr.z > 0.) {
    az = 2. * pi * mPtr.x;
    el = -0.05 * pi + pi * mPtr.y;
  } else {
    t = mod (0.01 * tCur, 2.);
    tSmooth = (floor (32. * t) + smoothstep (0.8, 1., mod (32. * t, 1.))) / 32.;
    az = 2.5 * pi * (0.5 - abs (tSmooth - 1.));
    el = -0.05 * pi - 0.14 * pi * (0.5 + 0.5 * cos (2. * az));
  }
  el = clamp (el, -0.4 * pi, -0.02 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -25.);
  zmFac = 3. + 0.6 * el;
  rd = vuMat * normalize (vec3 (uv, zmFac));
  dstFar = 100.;
  sunDir = normalize (vec3 (1., 1., -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  float dxy, dz;
  dxy = length (p.xy) - r;
  dz = abs (p.z) - h;
  return min (min (max (dxy + rt, dz), max (dxy, dz + rt)), length (vec2 (dxy, dz) + rt) - rt);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec2 gVec[7], hVec[7];

void HexVorInit ()
{
  vec3 e = vec3 (1., 0., -1.);
  gVec[0] = e.yy;
  gVec[1] = e.xy;
  gVec[2] = e.yx;
  gVec[3] = e.xz;
  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

vec2 HexVor (vec2 p)
{
  vec4 sd, udm;
  vec2 ip, fp, d, u;
  float amp, a;
  amp = 0.5;
  ip = PixToHex (p);
  fp = p - HexToPix (ip);
  sd = vec4 (4.);
  udm = vec4 (4.);
  for (int k = 0; k < 7; k ++) {
    u = Hashv2v2 (ip + gVec[k]);
    a = 2. * pi * (u.y - 0.5);
    d = hVec[k] + amp * (0.4 + 0.6 * u.x) * sin (a + vec2 (0.5 * pi, 0.)) - fp;
    sd.w = dot (d, d);
    if (sd.w < sd.x) {
      sd = sd.wxyw;
      udm = vec4 (d, u);
    } else sd = (sd.w < sd.y) ? sd.xwyw : ((sd.w < sd.z) ? sd.xyww : sd);
  }
  sd.xyz = sqrt (sd.xyz);
  return vec2 (SmoothMin (sd.y, sd.z, 0.1) - sd.x, Hashfv2 (udm.zw));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

vec2 Noisev2v2 (vec2 p)
{
  return vec2 (Noisefv2 (p), Noisefv2 (p + vec2 (17., 23.)));
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

