float signed_distance_box_repeating(vec3 p, vec3 box_position, vec3 box_dimensions, vec3 spacing)
{
    // Translation:
    p -= box_position;

    // Repetition:
    //const vec3 c = vec3(0.0, 0.0, 2.5);
    //p = mod(p, c) - 0.5 * c;
    p = mod(p, spacing) - 0.5 * spacing;


