float signed_distance_box(vec3 p, vec3 box_position, vec3 box_dimensions)
{
    // Translation:
    p -= box_position;

	vec3 d = abs(p) - box_dimensions;
	return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

