void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    // Normalized pixel coordinates (from 0 to 1 on largest axis)
    float maxRes = max(iResolution.x, iResolution.y);
    vec2 uv = fragCoord / maxRes;
    vec2 center = iResolution.xy / maxRes / 2.0;
    float uvFade = dot(uv - center, uv - center) * 16.0;
    uv = (uv - center) * 12.0;
    float dist = length(uv);

    vec3 X = vec3(uv, mod(iTime, 578.0) * 0.8660254037844386 - dist * 1.2);

    vec4 noiseResult = bccNoiseDerivatives_PlaneFirst(X);

    float value = dot(normalize(uv), vec2(noiseResult.y, -noiseResult.x)) / 8.0 + 1.0 - uvFade;

    // Time varying pixel color
    vec3 col = vec3(1.0, 1.0, 1.0) * value;

    // Output to screen
    fragColor = vec4(col, 1.0);
}

//derived from Dancy Tree Doodle 3D by wyatt https://www.shadertoy.com/view/4lVyzh
#define pi 3.1415926
vec3 light;

//perlin noise from https://www.shadertoy.com/view/4lB3zz
const int firstOctave = 3;
const int octaves = 3;
const float persistence = 0.6;

//Not able to use bit operator like <<, so use alternative noise function from YoYo
//
//https://www.shadertoy.com/view/Mls3RS
//
//And it is a better realization I think
float noise(int x,int y)
{   
    float fx = float(x);
    float fy = float(y);

    return 2.0 * fract(sin(dot(vec2(fx, fy) ,vec2(12.9898,78.233))) * 43758.5453) - 1.0;
}

float smoothNoise(int x,int y)
{
    return noise(x,y)/4.0+(noise(x+1,y)+noise(x-1,y)+noise(x,y+1)+noise(x,y-1))/8.0+(noise(x+1,y+1)+noise(x+1,y-1)+noise(x-1,y+1)+noise(x-1,y-1))/16.0;
}

float COSInterpolation(float x,float y,float n)
{
    float r = n*3.1415926;
    float f = (1.0-cos(r))*0.5;
    return x*(1.0-f)+y*f;

}

float InterpolationNoise(float x, float y)
{
    int ix = int(x);
    int iy = int(y);
    float fracx = x-float(int(x));
    float fracy = y-float(int(y));

    float v1 = smoothNoise(ix,iy);
    float v2 = smoothNoise(ix+1,iy);
    float v3 = smoothNoise(ix,iy+1);
    float v4 = smoothNoise(ix+1,iy+1);

   	float i1 = COSInterpolation(v1,v2,fracx);
    float i2 = COSInterpolation(v3,v4,fracx);

    return COSInterpolation(i1,i2,fracy);

}

float PerlinNoise2D(float x,float y)
{
    float sum = 0.0;
    float frequency =0.0;
    float amplitude = 0.0;
    for(int i=firstOctave;i<octaves + firstOctave;i++)
    {
        frequency = pow(2.0,float(i));
        amplitude = pow(persistence,float(i));
        sum = sum + InterpolationNoise(x*frequency,y*frequency)*amplitude;
    }

    return sum;
}



float ln (vec3 p, vec3 a, vec3 b, float R, float accum) { 
    float r = dot(p-a,b-a)/dot(b-a,b-a);
    r = clamp(r,0.,1.);
	p.x+=((clamp(1.0-2.0*abs(0.5-r),0.,1.))*PerlinNoise2D(accum+p.y,iTime/10.0)) / (accum+5.);
   //p.x+= 0.2*sqrt(R)*smoothstep(1.,0.,abs(r*2.-1.))*cos(pi*(2.*iTime));
    return length(p-a-(b-a)*r)-R*(1.5-0.4*r);
}
mat2 ro (float a) {
	float s = sin(a), c = cos(a);
    return mat2(c,-s,s,c);
}


float hash(int branchid){
    int treeNumber = int(iTime/3.0);

    int factor = treeNumber%15+1;
	return 1.0-float(branchid%factor)/float(factor)/2.0;
}

float hash2(int branchid){
    int treeNumber = int(iTime/3.0);
    int factor = treeNumber%5+1;
	return 1.0-float(branchid%factor)/float(factor);
}

float hash3(int branchid){
    int treeNumber = int(iTime/3.0);
    int factor = treeNumber%13+1;
	return 1.0-float(branchid%factor)/float(factor);
}

float map (vec3 p) {
    float l = length(p-light)-1e-2;
    l = min(l,abs(p.y+0.4)-1e-2);
    l = min(l,abs(p.z-0.4)-1e-2);
    l = min(l,abs(p.x-0.7)-1e-2);
    p.y += 0.4;
    p.z += 0.1;
    float accum = 0.0;
    int branchid = 1;
    //p.zx *= ro(.5*iTime);
    vec2 rl = vec2(0.02,.3);
    for (int i = 1; i <14; i++) {

        l = min(l,ln(p,vec3(0),vec3(0,rl.y,0),rl.x, accum));
    	p.y -= rl.y*0.9;
        //p.xy *= ro(iTime*1.);
        if(p.x>0.0){
        	branchid*=2;
            p.y += rl.y*hash2(branchid); //shift branch position based on id
        }else{
            branchid*=2;
            branchid++;
        }

        accum+=p.y;

        p.x = abs(p.x);

        float per = PerlinNoise2D(p.x,p.y);

        p.xy+=sign(p.xy)*per*length(p.xy);

        p.xy *= ro(0.6+0.4*sin(iTime)*sin(0.871*iTime/4.0)+0.05*float(i)*sin(2.*iTime/3.0));
        p.zx *= ro(0.5*pi+0.2*sin(0.5278*iTime/5.0)+0.8*float(i)*(sin(0.1*iTime/3.0)*(sin(0.1*pi*iTime/4.5)+sin(0.333*iTime/6.0)+0.2*sin(1.292*iTime/4.0))));


        p.xz *= ro(p.y*hash3(branchid)); //twirl

        rl *=0.8;

        rl.y*=hash(branchid); //scale branch down based on id

        l=min(l,length(p)-0.04*sqrt(rl.y));
    }
	return l;
}

vec3 march (vec3 p, vec3 d) {
    float o = 1e3;
    for (int i = 0; i < 24; i++) {
        float l = map(p);
    	p += l*d;
        if (l < 1e-3)break;
    }
    return p;
}
vec3 norm (vec3 p) { // iq
		vec2 e = vec2 (.001,0.);
		return normalize(vec3(
				map(p+e.xyy) - map(p-e.xyy),
				map(p+e.yxy) - map(p-e.yxy),
				map(p+e.yyx) - map(p-e.yyx)
			));
	}
void mainImage( out vec4 C, in vec2 U )
{   vec2 R = iResolution.xy;
    light = vec3(0.2*sin(iTime),0.5,-.5);
    if (iMouse.z > 0.) light = vec3(vec2(-0.5,0.5)*0.+0.7*(iMouse.xy-0.5*R)/R.y,-.3);

    U = (U-0.5*R)/R.y;
    vec3 p = vec3(0,0,-1);
    vec3 d = normalize(vec3(U,1));
    p =  march(p,d);
    vec3 n = norm(p);
	C = 0.6+0.4*sin(1.1*vec4(1,2,3,4)*dot(d,n));
    vec3 D = light-p;
    d = normalize(D);
    vec3 lp = march(p+d*1e-2,d);
    C *= 2.5*(dot(d,n))*(.3+0.7*length(lp-p)/length(light-p));
    C = atan(C)/pi*2.;
}

// Noise from https://www.shadertoy.com/view/4sdGD8
lowp vec3 permute(in lowp vec3 x) { return mod( x*x*34.+x, 289.); }
lowp float snoise(in lowp vec2 v) {
  lowp vec2 i = floor((v.x+v.y)*.36602540378443 + v),
      x0 = (i.x+i.y)*.211324865405187 + v - i;
  lowp float s = step(x0.x,x0.y);
  lowp vec2 j = vec2(1.0-s,s),
      x1 = x0 - j + .211324865405187, 
      x3 = x0 - .577350269189626; 
  i = mod(i,289.);
  lowp vec3 p = permute( permute( i.y + vec3(0, j.y, 1 ))+ i.x + vec3(0, j.x, 1 )   ),
       m = max( .5 - vec3(dot(x0,x0), dot(x1,x1), dot(x3,x3)), 0.),
       x = fract(p * .024390243902439) * 2. - 1.,
       h = abs(x) - .5,
      a0 = x - floor(x + .5);
  return .5 + 65. * dot( pow(m,vec3(4.))*(- 0.85373472095314*( a0*a0 + h*h )+1.79284291400159 ), a0 * vec3(x0.x,x1.x,x3.x) + h * vec3(x0.y,x1.y,x3.y));
}

