const float log_step = N/M;

vec2 uv(vec2 p) {
    vec2 f = vec2(p) + 20.0*vec2(snoise(p.xy*0.015 + 0.04*iTime),snoise(p.yx*0.015 + 0.04*iTime));
    return f.xy;
}

float d(vec2 p) {

    //return log_step; // uncomment to reveal distortion

    float delta = 1.0;
    float dudx = ( uv(p - vec2(delta,0.0)).x - uv(p + vec2(delta,0.0)).x ) * 0.5 / delta;
    float dudy = ( uv(p - vec2(0.0,delta)).x - uv(p + vec2(0.0,delta)).x ) * 0.5 / delta;
    float d = log_step / (0.000001 + length(vec2(dudx,dudy)));
    return max(log_step,d);
}

