float Granite( vec3 pos )
{
    const int n = 4;
    float o = 0.;
    for ( int i=0; i < n; i++ )
    {
    	o = o*2. + abs(Noise(pos)*25.-1.);
        pos *= 2.;
    }
    return o / float((1<<(n))-1);
