//https://www.shadertoy.com/view/4dS3Wd
float hash(float p) { p = fract(p * 0.011); p *= p + 7.5; p *= p + p; return fract(p); }

//https://www.shadertoy.com/view/4dS3Wd
float noise(vec3 x) {
    const vec3 step = vec3(110, 241, 171);

    vec3 i = floor(x);
    vec3 f = fract(x);

    // For performance, compute the base input to a 1D hash from the integer part of the argument and the 
    // incremental change to the 1D based on the 3D -> 1D wrapping
    float n = dot(i, step);

    vec3 u = f * f * (3.0 - 2.0 * f);
    return mix(mix(mix( hash(n + dot(step, vec3(0, 0, 0))), hash(n + dot(step, vec3(1, 0, 0))), u.x),
                   mix( hash(n + dot(step, vec3(0, 1, 0))), hash(n + dot(step, vec3(1, 1, 0))), u.x), u.y),
               mix(mix( hash(n + dot(step, vec3(0, 0, 1))), hash(n + dot(step, vec3(1, 0, 1))), u.x),
                   mix( hash(n + dot(step, vec3(0, 1, 1))), hash(n + dot(step, vec3(1, 1, 1))), u.x), u.y), u.z);
}

//https://www.shadertoy.com/view/4dS3Wd
//slightly modified for octave spcification
float fbm5(vec3 x) {
	float v = 0.0;
	float a = 0.5;
	vec3 shift = vec3(100);
	for (int i = 0; i < 5; ++i) {
		v += a * noise(x);
		x = x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}

float fbm3(vec3 x) {
	float v = 0.0;
	float a = 0.5;
	vec3 shift = vec3(100);
	for (int i = 0; i < 3; ++i) {
		v += a * noise(x);
		x = x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}

//iq
float sdPlaneInf(vec3 pos, float y)
{
 	return pos.y - y;   
}

//iq, modified color and made fog amount grow exponentially, shrink as y gets bigger
vec3 applyFog( in vec3  rgb,      // original color of the pixel
               in float distance, // camera to point distance
               in vec3  rayDir,   // camera to point vector
               in vec3  sunDir )  // sun light direction
{
    float fogAmount = 1.0 - exp( -distance*(distance*(0.001 / pow(clamp(VIEW_DIST / 70.0, 0.0, 1.0), 2.0))) );
    fogAmount -= rayDir.y;
    float sunAmount = max( dot( rayDir, sunDir ), 0.0 );
    vec3  fogColor  = mix( vec3(0.5,0.6,0.7), // bluish
                           vec3(0.5,0.6,0.7), 
                           pow(sunAmount,8.0) );
    return mix( rgb, fogColor, fogAmount );
}

//https://www.iquilezles.org/www/articles/smin/smin.htm
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

Object map(vec3 pos)
{
    Object o;
    o.difVal = 1.0;
    o.specVal = 20.0;
    o.specKs = 0.0;
    o.dist = 1000.0;
    o.normEps = 0.001;
    o.color = vec3(0);

    vec3 btmPos = pos;
    btmPos.x += iTime*0.75;
    btmPos.y -= 0.03*sin(btmPos.x*2.0);
    float dPlaneBtm = sdPlaneInf(btmPos, 0.3);
    if(dPlaneBtm < o.dist)
    {
        o.dist = dPlaneBtm;
        //o.color = vec3(1.0, 1.0, 0.1);
        o.color = mix(vec3(1.0, 1.0, 0.1)*(1.3+abs(0.5*sin(iTime))), vec3(1.0, 0.1, 0.), fbm5(btmPos*3.0));
    }


    vec3 planePos = pos;
    planePos.x += iTime;
    float yVal = fbm3(planePos/2.5);
    yVal -= 0.2*fbm5(planePos*1.5);
    planePos.y -= yVal;
    float dPlane = sdPlaneInf(planePos, 0.0);
    if(dPlane < o.dist)
    {
        o.dist = dPlane;
		o.specKs = 0.15;
        o.color = mix(vec3(1.0, 0.7, 0.1)*2.0, vec3(0.8, 0.1, 0.), smoothstep(0.0, 0.5, yVal));
        o.color = mix(o.color, vec3(0.5, 0.1, 0.), smoothstep(0.4, 0.6, yVal));
        //o.color += mix(o.color, vec3(0), smoothstep(0.0, 1.0, yVal*fbm5(planePos*10.0)));
    }

    vec3 bbPos = pos;
    bbPos.x = mod(bbPos.x, -5.0);
    bbPos.z = abs(bbPos.z);

    return o;
}

vec3 calcNormal(vec3 pos, float ep)
{
    return normalize(vec3(map(pos + vec3(ep, 0, 0)).dist - map(pos - vec3(ep, 0, 0)).dist,
                		map(pos + vec3(0, ep, 0)).dist - map(pos - vec3(0, ep, 0)).dist,
                		map(pos + vec3(0, 0, ep)).dist - map(pos - vec3(0, 0, ep)).dist));                                
}

MarchRes marchRay(vec3 pos, vec3 dir, float speed)
{
 	MarchRes res;
    Object o;

    res.totalDist = 0.001;

    for(int x=0; x<100; x++)
    {
 		res.curRay = pos + (dir*res.totalDist);

        o = map(res.curRay);
        if(abs(o.dist) < 0.0001)
        {
            res.obj = o;
            break;
        }
        else if(res.totalDist >= VIEW_DIST) break;

        res.totalDist += o.dist*speed; // repalce 0.8 w/ this for trippy mode ;p => (0.3+0.2*(sin(iTime))); //couldn't handle the hair :' (
    }

    if(res.totalDist < VIEW_DIST)
    {
        o.normal = calcNormal(res.curRay, o.normEps);
        res.obj = o;
    }


    return res;
}

vec3 calcDiffuseLight(Object o, Light l, vec3 pos)
{
    vec3 dir = normalize(l.pos - pos);
    return (o.color) * l.intensity * l.color * clamp(dot(o.normal, dir), 0.0, 1.0) * o.difVal;   
}

vec3 calcSpecLight(Object o, Light l, vec3 pos, vec3 camPos)
{
 	vec3 dir = normalize(l.pos - pos);  
    vec3 viewDir = normalize(camPos - pos);
    vec3 specR = 2.0*clamp( dot(o.normal, dir), 0.0, 1.0) * o.normal - dir;
    float spec = clamp( dot(viewDir, specR), 0.0, 1.0);
    //lightInt*(lightCol*pow(lightSpec, res.obj.specVal))*res.obj.specKs * lightShadow;

    return o.specKs*l.intensity*(l.color*pow(spec, o.specVal));
}

vec3 drawVolcano(vec3 origCol, vec3 col, vec2 uv, vec2 yBound)
{
    uv.x = abs(uv.x);
    return mix(origCol, col, smoothstep(uv.y, uv.y+0.01, yBound.x)
               *smoothstep(uv.y-0.01, uv.y+0.01, pow(-uv.x*2.0 + 1.0,3.0) - 0.025)*step(yBound.y, uv.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord - iResolution.xy)/iResolution.y;
	vec3 camEye = vec3(0.0,3.0,0.0);
    vec3 dir = normalize(vec3(uv, -1));


    float camAng = 0.0;//-iMouse.x/20.0;//
    float camAngX = iMouse.y/20.0;

    mat2 rotCam = mat2( vec2(cos(camAng), -sin(camAng)), vec2(sin(camAng), cos(camAng)) );
    mat2 rotCamX = mat2( vec2(cos(camAng), sin(camAng)), vec2(-sin(camAng), cos(camAng)) );

    //vec2 camXZ = rotCam * camEye.xz;
    //camEye = vec3(camXZ.x, camEye.y, camXZ.y);
    vec2 dirXZ = rotCam * dir.xz;
    dir = vec3(dirXZ.x, dir.y, dirXZ.y);

    Light light;
    light.intensity = 1.0;
    light.pos = vec3(10, 5, -10.0);
    light.color = vec3(1.0);

    Light lightSky;
    lightSky.intensity = 0.3;
    lightSky.pos = vec3(0, 3, 0);
    lightSky.color = vec3(1);

	MarchRes res = marchRay(camEye, dir, 1.0);
    vec3 pos = res.curRay;  

    float y = uv.y + 1.0;
    y = y / 1.62;
    y = clamp(y, 0.7, 1.0);

    y = smoothstep(1.1, 0.5, y);
    vec3 col = (vec3(1)*(y) + vec3(0.3, 0.3, 0.4)*(1.0-y));
    //col = vec3(0);
    vec3 volCol = mix(vec3(0.3, 0.3, 0.4), vec3(0.83, 0.35, 0.35)*abs(1.0 + 0.3*sin(iTime)), smoothstep(0.37, 0.4, uv.y));
    col = drawVolcano(col, volCol, uv, vec2(0.4, -0.3));

    if(res.totalDist < VIEW_DIST)
    {
        col = calcDiffuseLight(res.obj, light, vec3(0)) + calcDiffuseLight(res.obj, lightSky, vec3(0,0,0));
        col += calcSpecLight(res.obj, light, pos, camEye);// * calcShadowSoft(pos, light);
    }

    col = applyFog(col, length(pos), normalize(pos), normalize(light.pos - pos));

    fragColor = vec4(col,1.0);
}

